INSERT INTO `news` (`id`, `title`, `image`, `shortdescription`, `content`, `categoryid`, `createddate`, `modifieddate`, `authorid`) VALUES
	(1, 'Thủ tướng yêu cầu hoàn thiện chính sách đãi ngộ giáo viên', 'news_1_tsu.jpg', 'Thủ tướng Phạm Minh Chính yêu cầu cơ quan chức năng nhanh chóng hoàn thiện chính sách tuyển dụng, đãi ngộ để giáo viên được thụ hưởng tương xứng công sức.', 'Thủ tướng Phạm Minh Chính yêu cầu cơ quan chức năng nhanh chóng hoàn thiện chính sách tuyển dụng, đãi ngộ để giáo viên được thụ hưởng tương xứng công sức. <br>\r\n\r\nChiều 19/11, Thủ tướng Phạm Minh Chính gặp mặt đại diện các nhà giáo tiêu biểu nhân dịp kỷ niệm 40 năm ngày Nhà giáo Việt Nam 20/11.<br>\r\n\r\nThủ tướng cho rằng, kỷ nguyên toàn cầu hóa và hội nhập quốc tế hiện nay mang lại cơ hội và không ít thách thức mới, nhất là với đội ngũ nhà giáo, hạt nhân trong sự nghiệp trồng người. Giáo dục và đào tạo cần được coi là sự nghiệp chung của toàn xã hội. Để học sinh phát triển toàn diện thì gia đình, nhà trường, xã hội phải là ba trụ cột chính để phát triển nhân cách đạo đức, tính sáng tạo, giúp học sinh hình thành tình yêu thương, trung thực, sống có lý tưởng, học cách vươn lên từ khó khăn...<br>\r\n\r\n"Giáo dục cần bám sát nguyên tắc chấp nhận khác biệt, khuyến khích tư duy phản biện để phát huy cao nhất trí tuệ, phẩm chất học sinh", Thủ tướng nói.<br>\r\n\r\nLãnh đạo Chính phủ nhấn mạnh, phát triển giáo dục và đào tạo cần tìm câu trả lời cho các câu hỏi, như phải làm gì để đổi mới giáo dục thực chất, cải thiện nguồn nhân lực, nâng cao năng suất lao động và sức cạnh tranh? Cần làm gì để giáo dục đào tạo gắn kết với nhu cầu, lợi ích, đời sống? Người học nắm được gì, phát triển nhận thức như thế nào, ứng dụng và thực hành trong công việc và cuộc sống ra sao? Giải pháp nào để cải thiện cơ sở vật chất cho việc dạy và học? Cần cơ chế, chính sách gì để nâng cao đời sống, để giáo viên yên tâm cống hiến?\r\nÔng Chính nhấn mạnh, muốn xây dựng đất nước, trước hết phải phát triển giáo dục, muốn trị nước, phải trọng dụng người tài. Quốc gia, dân tộc muốn phát triển phải dựa vào con người, lấy con người làm trung tâm, là mục tiêu và động lực. Và muốn phát triển con người thì phải dựa vào giáo dục và đào tạo. Hệ thống giáo dục và đào tạo muốn được vận hành tốt, có hiệu quả cao thì thầy cô giáo đóng vai trò quyết định.<br>\r\n\r\nNăm 1945, hơn 95% dân số không biết chữ thì nay Ngân hàng thế giới đánh giá Việt Nam là một trong hai nước phát triển ấn tượng và tiên phong đổi mới giáo dục. Trong hai năm Covid-19 ảnh hưởng nặng nề, các thầy cô giáo đã cố gắng để việc dạy và học không bị ngắt quãng, "dừng đến trường, không dừng việc học".<br>\r\n\r\nThủ tướng xúc động khi nhiều thầy cô leo ghềnh, vượt thác, gác lại niềm riêng, chấp nhận ở những nơi khó khăn, heo hút với cơ sở vật chất thiếu thốn để "gùi con chữ" lên vùng cao, mang kiến thức đến với đồng bào. Vì vậy, ông yêu cầu các Bộ, ngành nhanh chóng hoàn thiện chính sách tuyển dụng, đãi ngộ để giáo viên được thụ hưởng tương xứng công sức. Giáo viên vùng sâu, vùng xa, giáo viên mầm non hoặc giảng dạy ngành nghề nặng nhọc cần phải được quan tâm hơn.<br>\r\nTại cuộc gặp, giáo viên mầm non Phạm Thị Tâm (Đồng Xuân, Phú Yên), chia sẻ cô đến giảng dạy ở vùng đồng bào dân tộc khó khăn nhất của tỉnh vì nghĩ "ai cũng chọn việc nhẹ nhàng, gian khổ sẽ dành phần ai". Dù một tuần, nửa tháng mới về nhà, đường đi khó khăn, cuộc sống thiếu thốn nhưng cô luôn cố gắng hoàn thành nhiệm vụ.<br>\r\n\r\nCô Tâm cho biết "chứng kiến nhiều gia đình đồng bào dân tộc Ba Na còn thiếu thốn, thương họ vô cùng, và nhớ đến hình ảnh cơ cực của bản thân, gia đình trước đây". Hình ảnh những ngôi nhà xập xệ trước gió mưa, cụ già ăn cơm với muối, những đứa trẻ đầu trần chân đất khiến cô luôn trăn trở "mình là giáo viên, phải làm cách nào giúp học sinh vượt qua nghịch cảnh để vươn lên".<br>\r\n\r\nKhông chỉ dạy học, cô Tâm giúp người dân làm ăn và chi tiêu hợp lý. 5 năm qua, cô gom được hơn 300 bao đồ chở đến cho người dân nơi công tác. Cô hướng dẫn phụ huynh nấu ăn, nấu cháo dinh dưỡng, làm sữa chua, bánh. Cô dạy người dân cách chữa bệnh bằng thuốc thay vì cúng bái. Cô mở hai thư viện với hơn 1.000 đầu sách; xin tài trợ 50 xe đạp cho học sinh nghèo.<br>\r\nCô Tâm nói, chỉ nghĩ đơn giản là giúp được người dân đến đâu thì giúp, "vì thêm chiếc áo là thêm em bé được ấm áp, thêm tấm chăn là thêm giấc ngủ trọn vẹn, thêm ký gạo là thêm một ngày no. Nhiều giọt nước sẽ tạo thành biển lớn, nhiều sự chia sẻ sẽ chiến thắng khổ đau, nhiều điều nhỏ bé sẽ lan tỏa để thắp ước mơ, mở ra tương lai tốt đẹp cho các em và bản làng vào ngày mai tươi sáng".<br>\r\n\r\nThầy Võ Văn Sen, Đại học Khoa học xã hội và Nhân văn, Đại học quốc gia TP HCM có hơn 40 năm giảng dạy, cho rằng đã đến lúc Việt Nam có biện pháp đặc biệt, mang tính nhảy vọt để ưu tiên phát triển giáo dục.<br>\r\n\r\nThầy Nguyễn Vũ Quốc Huy, Đại học Y dược, Đại học Huế, thì đánh giá tự chủ đại học dù đạt nhiều thành quả nhưng có những điểm chưa thống nhất, hài hòa trong văn bản pháp quy. Ông mong các cơ quan tháo gỡ vướng mắc này để tạo điều kiện tối đa cho giáo dục đại học phát triển mạnh.', 11, '2022-11-19 15:39:07', '2022-11-22 15:47:16', 1),
	(2, 'Messi: \'Argentina chịu cú sốc lớn\'', 'news_1_tt.png', 'Thủ quân Lionel Messi hứa với người hâm mộ rằng Argentina sẽ vượt qua vòng bảng World Cup 2022, sau thất bại sốc trước Saudi Arabia.', '"Trận thua này là cú sốc lớn với chúng tôi", TNT Sports dẫn lời Messi sau trận hôm 22/11. "Mọi người và chúng tôi đều không nghĩ sẽ thất bại như vậy. Nhưng hãy tin rằng Argentina sẽ không làm mọi người thất vọng. Đội bóng này đã thể hiện được sự đoàn kết, và lúc này Argentina cần đoàn kết hơn bao giờ hết". <br>\r\nSiêu sao 35 tuổi còn cho rằng Argentina chỉ cần thắng Mexico ở lượt trận tiếp theo, tình thế sẽ thay đổi. "Đã lâu Argentina không rơi vào tình cảnh tương tự, khi chịu thất bại và áp lực", anh nói. "Nhưng nếu thắng Mexico, chúng tôi lại có thể thoải mái. Dù sao Argentina cũng phải thắng các trận còn lại".\r\n\r\nMessi mở tỷ số cho Argentina ở phút 10 từ chấm phạt đền, nhưng đội bóng của anh thua ngược 1-2. Họ đứt chuỗi bất bại 36 trận, từ tháng 7/2019. Nghiêm trọng hơn khi Messi và đồng đội đứng trước nguy cơ bị loại, khi Mexico và Ba Lan được cho là khó chịu hơn Saudi Arabia.\r\n\r\nCũng như lời của HLV Lionel Scaloni, Messi đã lường trước cách chơi của Saudi Arabia. "Họ có nhiều cầu thủ giỏi và phòng ngự theo cách đặc biệt", tiền đạo đang chơi cho PSG nói thêm. "Đáng lẽ chúng tôi phải đá nhanh hơn nữa, khi Saudi Arabia phòng ngự tốt. Rồi chúng tôi mắc sai lầm ở đầu hiệp hai dẫn tới bàn thua, kể từ đó mọi chuyện vuột khỏi tầm kiểm soát".\r\n\r\nSaudi Arabia chỉ sút ba lần trong trận nhưng ghi hai bàn, còn Argentina dứt điểm nhiều hơn gấp năm lần mà chỉ ghi một bàn. Đại diện châu Á chơi với hàng thủ dâng cao, cự ly giữa các tuyến gần nhau. Argentina ba lần đưa bóng vào lưới đối thủ nhưng bị từ chối vì việt vị.\r\nMessi nhận điểm cao thứ hai trận này, chỉ sau tiền đạo Al Shehri. Thủ quân Argentina bốn lần dứt điểm và tạo ra ba cơ hội cho đồng đội. Nhưng có tình huống anh chọc khe hơi chậm khiến Lautaro Martinez thoát xuống ghi bàn nhưng không được công nhận vì việt vị. Sau trận, Martinez cho biết anh "đau đớn" với thất bại này, nhưng sẽ cùng đồng đội tìm cách sửa sai ở những trận còn lại tại World Cup 2022.', 14, '2022-11-22 17:16:16', '2022-11-22 17:16:36', 1),
	(3, 'Xả súng tại siêu thị Mỹ, nhiều người chết', 'news_1_tg.jpg', 'Nghi phạm xả súng khiến nhiều người thiệt mạng tại siêu thị Walmart ở bang Virginia của Mỹ, cảnh sát nói rằng tay súng đã thiệt mạng.', 'Nghi phạm xả súng khiến nhiều người thiệt mạng tại siêu thị Walmart ở bang Virginia của Mỹ, cảnh sát nói rằng tay súng đã thiệt mạng.\r\n\r\nVụ xả súng xảy ra tại siêu thị Walmart ở thành phố Chesapeake, bang Virginia của Mỹ, tối 22/11 (sáng 23/11 giờ Hà Nội).\r\n\r\n"Sở cảnh sát Chesapeake xác nhận xảy ra xả súng ở siêu thị Walmart. Nghi phạm đã thiệt mạng. Lực lượng phản ứng của chúng tôi được huấn luyện kỹ càng và sẵn sàng hành động, hãy để họ có không gian làm việc", tài khoản Twitter của chính quyền thành phố đăng thông báo cho hay.\r\nCác sĩ quan đã phát hiện nhiều người thiệt mạng và bị thương, giới chức chưa công bố con số cụ thể nhưng nói rằng số người chết "ít hơn 10". Leo Kosinkski, phát ngôn viên Sở cảnh sát Chesapeake, nói rằng không có sĩ quan nào nổ súng trong quá trình tiến vào hiện trường.\r\n\r\nLực lượng an ninh đang tìm kiếm thêm nạn nhân trong siêu thị và đề nghị người dân tránh xa khu vực này. Danh tính nghi phạm và động cơ dẫn tới vụ xả súng chưa được công bố.\r\n\r\nMột nhân viên Walmart nói rằng nghi phạm là quản lý làm việc tại siêu thị này và đã tự sát sau khi xả súng.', 13, '2022-11-23 06:14:09', '2022-11-23 06:46:34', 1),
	(5, 'Làn sóng cắt giảm lao động', 'news_2_tsu.jpg', 'Hàng chục nghìn lao động phía Nam mất việc do nhà máy thiếu đơn hàng, con số này có thể tăng khi các thị trường xuất khẩu chủ lực của Việt Nam chưa thể hồi phục', 'Đầu tháng 9, nhà máy S.K Vina (TP HCM), trong ngành may mặc, với gần 900 công nhân, nhận yêu cầu dừng toàn bộ hoạt động từ công ty mẹ bên Hàn Quốc. Lý do đưa ra là một hãng thời trang bất ngờ hủy toàn bộ đơn hàng, tập đoàn không tìm được đối tác khác bù vào. S.K Vina cần hoàn tất phương án giải thể trong tháng 11. Tiền lương, các khoản bồi thường, hỗ trợ cho công nhân gần 30 tỷ đồng sẽ được tập đoàn "rót" từ Hàn Quốc sang. Những đơn hàng dang dở được chuyển đến nhà máy ở Bình Dương để hoàn thiện.\r\nCông ty S.K Vina hoạt động tại TP HCM gần 15 năm. Từ giữa năm nay, trong khi hầu hết doanh nghiệp trong ngành bị ảnh hưởng bởi sức mua của thị trường châu Âu, Mỹ giảm, S.K Vina vẫn có hàng để sản xuất, công nhân chỉ phải giảm giờ làm thêm. "Không ai trong chúng tôi chuẩn bị cho tình huống phải giải thể", đại diện công ty nói. Nhà máy muốn giữ lại công nhân nhưng trong bối cảnh đơn hàng trong ngành giảm đồng loạt lãnh đạo khó xoay ra việc để làm.\r\n\r\nCách nhà máy S.K Vina hơn 200 km, ở miền Tây, từ tháng 10 đến cuối tháng 12 lần lượt hơn 5.300 lao động (chiếm 53% nhân sự) của Công ty TNHH An Giang Samho, sản xuất da giày, lâm cảnh thất nghiệp. Trong văn bản gửi cơ quan chức năng, công ty cho hay một đối tác lớn của doanh nghiệp, chiếm 40% quy mô sản xuất, đã dừng đặt hàng. Nhiều đơn hàng khác cũng sụt giảm vì lạm phát, thị trường xuất khẩu thu hẹp. Công ty cố gắng tìm kiếm nguồn hàng khác để bù đắp nhưng không thể xoay chuyển, phải thu hẹp sản xuất, sắp xếp lao động.\r\n\r\nSau nhiều cuộc làm việc với ngành chức năng địa phương, công ty quyết định không cắt giảm những lao động đang trong thời kỳ thai sản, nuôi con dưới 12 tháng, một mình nuôi con, người có sổ hộ nghèo. Nếu gia đình có nhiều người làm ở An Giang Samho, nhà máy sẽ giữ lại một người... Phó giám đốc Sở Lao động, Thương binh và Xã hội An Giang Phạm Sơn nói nhiều năm làm việc ông chưa từng gặp trường hợp nhà máy cho nghỉ cùng lúc hơn 5.000 công nhân như An Giang Samho. Số lượng lao động bị ảnh hưởng quá lớn nên ban đầu khâu giải quyết của địa phương khá lúng túng.\r\n\r\nS.K Vina, An Giang Samho là hai trong số các nhà máy ở phía Nam phải cắt giảm lao động do thiếu, mất đơn hàng. Số liệu của Tổng liên đoàn lao động Việt Nam, từ tháng 9-11, hơn 631.300 lao động ở 28 tỉnh, thành phố, chủ yếu ở phía Nam bị ảnh hưởng. Trong đó, gần 570.000 người bị giảm giờ làm, hơn 34.500 bị cắt giảm và trên 31.000 trường hợp nghỉ không lương hoặc bị tạm hoãn hợp đồng. Ngành nghề bị ảnh hưởng nhiều nhất là chế biến gỗ, dệt may, da giày, điện tử, thực phẩm, dịch vụ, du lịch... Việc cắt giảm này đi ngược xu thế mọi năm khi cùng kỳ các nhà máy thường gia tăng tuyển dụng để đáp ứng đơn hàng.\r\nỞ một số địa phương khác, theo thống kê của cơ quan Bảo hiểm xã hội Bình Dương, đến cuối tháng 10, khoảng 28.000 người bị ngưng việc. Khảo sát của công đoàn tỉnh này cho thấy, khoảng 240.000 lao động bị ảnh hưởng. Tại Đồng Nai, 5 tháng qua có khoảng 30.000 lao động bị chấm dứt hợp đồng. Con số này ở Long An thời gian qua hơn 1.000 người, có nhà máy giảm 700 nhân sự...\r\n\r\nTại TP HCM, hồi giữa tháng 10, Phó giám đốc Sở Lao động, Thương binh và Xã hội thành phố Nguyễn Văn Lâm cho biết năm 2022 hơn 2.800 lao động bị mất việc. Tuy nhiên, số liệu từ các địa phương cho thấy chỉ với ba nhà máy ở quận 12, Bình Tân, huyện Củ Chi, lao động bị cắt giảm thời gian qua đã hơn 3.400 người. Một nhà máy giày ở Khu chế xuất Linh Trung II (TP Thủ Đức) với hơn 6.000 công nhân đang tính giảm nhiều nhân sự vì thiếu đơn hàng.\r\nHơn 30 năm làm trong ngành may mặc, ông Phạm Xuân Hồng, Chủ tịch Hội Dệt may thêu đan TP HCM kiêm Chủ tịch HĐQT Công ty cổ phần May Sài Gòn 3, nói rằng đây là lần đầu gặp cảnh công nhân thiếu việc, nhà máy thiếu đơn hàng diện rộng. Các nhà máy bị giảm 30-50% đơn hàng nên buộc phải ngừng giờ làm, cho công nhân nghỉ luân phiên. Tình hình lúc này còn khó hơn thời điểm Covid-19 bùng phát.\r\n\r\n"Lúc Covid-19 lan rộng, mình có niềm tin dịch sẽ được khống chế và doanh nghiệp có cơ sở đề xuất chính phủ hỗ trợ cho công nhân. Giờ đây, chúng tôi cực kỳ lúng túng", ông Hồng nói và ví von hai tháng cuối năm lẫn 6 tháng đầu năm 2023 "như đi vào đường hầm, chưa thấy ánh sáng". Tình hình sẽ khó khăn hơn bởi nhiều nhà máy có rất ít đơn hàng cho năm mới, trong khi việc xoay xở tìm thêm thị trường không phải dễ. Nhiều doanh nghiệp sẵn sàng nhận các đơn hàng giá thấp, thậm chí lỗ để duy trì việc cho công nhân nhưng cũng khó.\r\n\r\nThông tin từ Hiệp hội doanh nghiệp TP HCM, đơn hàng tại các thị trường xuất khẩu chính của ngành dệt may như Mỹ, châu Âu giảm rõ rệt. Cụ thể, châu Âu giảm 60%, Mỹ giảm 30-40%. Lượng hàng tồn kho tăng, chiếm tới 20-25%. Từ quý 4/2022 và dự báo quý 1/2023, doanh nghiệp không có khách hàng mới. Các công ty cạnh tranh đơn hàng khá gay gắt. Nhiều khách hàng đưa ra mức giá chỉ bằng 40-50%. Không ít doanh nghiệp phải cắt giảm lao động và quy mô sản xuất.\r\n\r\nBà Bùi Thị Ninh, Trưởng văn phòng Giới sử dụng lao động (Liên đoàn Thương mại và Công nghiệp Việt Nam VCCI - chi nhánh TP HCM), nói nhu cầu tiêu thụ các mặt hàng giày dép, áo quần, nội thất của dân châu Âu, Mỹ vốn là thị trường xuất khẩu chủ lực của Việt Nam giảm, nên các nhà máy gia công cho các nhãn hàng bị ảnh hưởng nặng nề. Ngoài ra, nhiều công ty hoạt động trong các ngành phụ trợ cho thị trường Nhật, doanh thu cũng sụt giảm do đồng yen mất giá.\r\nPGS.TS Nguyễn Đức Lộc, Viện trưởng Nghiên cứu đời sống xã hội (Social Life), nêu một khảo sát của viện cho thấy 60% lao động vào các nhà máy sản xuất làm việc mà không cần bất cứ điều kiện gì, tức rất dễ tuyển và cũng dễ bị thay thế khi nhà máy gặp khó khăn về tài chính. Khảo sát cho thấy hơn 10% lao động lớn tuổi ở miền Tây về từ các tỉnh thành công nghiệp, chủ yếu hết tuổi nghề nhưng chưa đến tuổi hưu. Đây là hệ quả của nền kinh tế thâm dụng lao động và công nhân dễ bị đào thải khi xảy ra dịch bệnh, kinh tế suy thoái.\r\n\r\nTheo ông Lộc, khi thị trường bên ngoài chững lại, các nhà máy xuất khẩu không giải quyết được nhiều việc làm cho người lao động, nhà nước cần xem đây là dịp để cân đối lại tỷ trọng của các ngành, điều phối lao động giữa các ngành sản xuất, dịch vụ, xây dựng... Trước mắt, một phương án có thể tạo ra việc làm mới cho người lao động là thúc đẩy nhanh các dự án đầu tư công ở các ngành, địa phương. Hiện hàng trăm nghìn tỷ đồng không giải ngân được đồng nghĩa hàng triệu chỗ làm mới đã không được tạo ra.\r\n\r\nPhó chủ tịch Tổng liên đoàn lao động Việt Nam Phan Văn Anh cho hay cơ quan này đang tổng hợp báo cáo, đề xuất Thủ tướng có sự hỗ trợ cho các doanh nghiệp bị giảm sâu đơn hàng. Phía công đoàn sẽ hỗ trợ những trường hợp khó khăn, động viên các chủ doanh nghiệp cố gắng giữ người lao động để chờ phục hồi. "Nhìn vào bài học sau dịch sẽ thấy, những doanh nghiệp vội vàng sa thải lao động sẽ gặp rất nhiều khó khăn khi thị trường phục hồi", ông Phan Anh nói.', 11, '2022-11-23 06:17:15', '2022-11-23 06:46:37', 3),
	(6, 'Cố Thủ tướng Võ Văn Kiệt qua ký ức văn nghệ sĩ', 'news_1_gtr.jpg', 'Sinh thời, ông Võ Văn Kiệt từng giúp nhiều văn nghệ sĩ nổi tiếng được "cởi trói" nhờ tầm nhìn, tư tưởng đổi mới của ông.', 'Sinh thời, ông Võ Văn Kiệt từng giúp nhiều văn nghệ sĩ nổi tiếng được "cởi trói" nhờ tầm nhìn, tư tưởng đổi mới của ông.\r\n\r\nNhiều hoạt động kỷ niệm 100 năm ngày sinh Thủ tướng Võ Văn Kiệt (23/11/1922 - 23/11/2022) diễn ra trong tháng 11. Với đông đảo văn nghệ sĩ, trí thức một thời, ông Sáu Dân (tên gọi thân mật của cố Thủ tướng) là nhà lãnh đạo nổi tiếng có tầm nhìn, tư duy cởi mở, góp công giúp bộ mặt văn hóa đất nước thay đổi.\r\nNhững năm đầu thập niên 1980, nhà thơ Nguyễn Duy viết bài Đánh thức tiềm lực - một trong những tác phẩm gây sóng gió trong sự nghiệp của ông. Khi ấy, Nguyễn Duy từ chiến trường trở về, chứng kiến nền kinh tế quốc gia "tụt dốc theo chiều rơi thẳng đứng" (chữ của nhà thơ Tố Hữu). Mỗi ngày, ông ám ảnh với cảnh "đất nước của ba miền cày ruộng, chưa đủ no cho đều khắp ba miền".<br>\r\nHoàn thành tác phẩm, ông không dám công bố bởi nghe tin một số bạn cùng thời bị bắt vì "thơ có vấn đề". Lần đầu ông đọc tác phẩm hoàn chỉnh là trong cuộc họp mặt thân tình với ông Võ Văn Kiệt - khi đó đang làm Ủy viên Bộ Chính trị, Bí thư Thành ủy TP HCM.\r\n\r\nNguyễn Duy nhớ lại khi ông dứt câu thơ cuối, khán phòng im lặng. Một hồi sau, Thủ tướng chậm rãi nói: "Nặng lắm. Nhưng chịu được". Nhà thơ thở phào. Sau lần đó, ông mạnh dạn phổ biến bài thơ ở nhiều nơi. Đến năm 1986, nhờ nghị quyết Đổi Mới của Đại hội Đại biểu Toàn quốc lần VI của Đảng, tác phẩm lần đầu được in nguyên văn trên báo Tuổi trẻ. Một năm sau, Đánh thức tiềm lực xuất hiện trong tập Mẹ và em của Nguyễn Duy.\r\n\r\n"Chính nhờ những người dám \'xé rào cơ chế\', chịu nghe những gì khác đi, thậm chí là ngược lại công thức phát ngôn chính thống, dám hành động vừa quyết đoán vừa nhân tình như ông Sáu Dân, chúng ta mới có được một con đường đổi mới với những ngày dễ thở hơn như hôm nay", nhà thơ Nguyễn Duy nói.\r\n\r\nMột giai đoạn, Thủ tướng Võ Văn Kiệt là người đỡ đầu cho ngành xuất bản TP HCM. Năm 1975, TP HCM chỉ có hai đơn vị là Tổng hợp và Văn nghệ, không có cơ sở xuất bản sách thiếu nhi. Ông Cửu Thọ (sau đó là giám đốc đầu tiên của Nhà xuất bản Trẻ) cùng các cộng sự làm đơn xin thành lập, gửi ra Trung ương hai năm nhưng không thấy phản hồi. Ông Võ Văn Kiệt (lúc đó là Bí thư Thành ủy TP HCM) đã đích thân ra Trung ương can thiệp. Năm 1981, Nhà xuất bản Măng Non (tên do ông Sáu Dân đặt) ra đời.<br>\r\n\r\n<h5>* Bà Quách Thu Nguyệt nói về công xây dựng NXB Trẻ của Thủ tướng Võ Văn Kiệt</h5><br>\r\n\r\nNhững ngày đầu, trụ sở của Nhà xuất bản được đặt ở số 53 Nguyễn Đình Chiểu (quận 3). Khi ấy, Thủ tướng trao tặng chiếc xe Renault ông đang dùng để góp vào tài sản còn khiêm tốn của đơn vị. 5 năm sau, Nhà xuất bản Măng non đổi tên thành Trẻ, đến nay trở thành một trong những thương hiệu xuất bản lớn nhất cả nước. Một lần trò chuyện cùng Thủ tướng, bà Quách Thu Nguyệt - nguyên giám đốc Nhà xuất bản Trẻ - nói các thế hệ lãnh đạo của Nhà xuất bản luôn xem ông như ân nhân. Nghe vậy, ông khoát tay, lắc đầu cười đôn hậu.<br>\r\n\r\nTừng được gặp, làm việc trực tiếp với Thủ tướng, ông Nguyễn Văn Huy - nguyên Giám đốc Bảo tàng Dân tộc học Việt Nam - nói Thủ tướng nhiều lần giúp giải quyết khó khăn để xây dựng Bảo tàng.\r\n\r\nNăm 1994, khi nghe báo cáo về việc bảo tàng ít hiện vật do thiếu kinh phí nghiên cứu, sưu tầm, Thủ tướng lập tức giao cho Ủy ban Kế hoạch Nhà nước (nay là Bộ Kế hoạch và Đầu tư) xử lý. Nhờ vậy, các cán bộ được đi thực tế từ Bắc vào Nam, sưu tầm được hơn 20.000 hiện vật, hình ảnh của 54 dân tộc. "Việc Thủ tướng sát sao vào việc xây dựng bảo tàng, cho thấy được tầm nhìn, tâm huyết của ông với việc bảo tồn, phát triển di sản, văn hóa - quan trọng không kém kinh tế hay giáo dục", ông Huy nói.<br>\r\n\r\nÔng Võ Văn Kiệt còn quan tâm đến những tài năng nghệ thuật trưởng thành trong chế độ cũ, mà Trịnh Công Sơn là đại diện tiêu biểu. Sau năm 1975, một giai đoạn, nhạc sĩ về quê nhà tại Thừa Thiên - Huế tạm lánh vì bị một số người bài xích. Khoảng năm 1978, ông trở lại TP HCM, được Nguyễn Quang Sáng kết nối với ông Sáu Dân. Có mối giao cảm đặc biệt với nhạc Trịnh, ông Võ Văn Kiệt giới thiệu nhạc sĩ với Hội liên hiệp văn học nghệ thuật TP HCM, nhận ông vào biên chế chính thức. Từ đó, Trịnh Công Sơn được tạo điều kiện hòa nhập với đời sống nghệ sĩ, lấy lại niềm cảm hứng sáng tác.<br>\r\n\r\n"Dù là lãnh đạo, ông rất cởi mở với chúng tôi. Ngày ấy, vô tình mà ba chúng tôi - Nguyễn Duy (Bắc), Trịnh Công Sơn (Trung), Nguyễn Quang Sáng (Nam) - là ba nghệ sĩ thân thiết nhất với ông Sáu Dân. Có thể mỗi người có cảm nhận riêng về những đóng góp của ông, còn với tôi, ông là một người bạn lớn", Nguyễn Duy nói.\r\n\r\nNhiều ca khúc nhạc Trịnh nổi tiếng sau này được gợi cảm hứng nhờ Thủ tướng. Nhớ mùa thu Hà Nội là một sáng tác như thế, theo ông Nguyễn Địch - nguyên Chủ nhiệm Văn phòng của ông Võ Văn Kiệt thời kỳ 1982-1985. Tháng 8/1985, nhạc sĩ lần đầu ra Hà Nội trên chuyên cơ của ông Sáu Dân. Chuyến bay hôm đó, ông gợi ý nhạc sĩ sáng tác một ca khúc về thủ đô vì "Hà Nội bây giờ là của cả nước, cũng là của Sơn". Sau khi lang thang khắp phố phường, Trịnh Công Sơn phác thảo những nốt nhạc đầu tiên: "Hà Nội mùa thu/ Cây cơm nguội vàng/ Cây bàng lá đỏ...". Khi Trịnh Công Sơn vừa ngưng đàn guitar, ông Võ Văn Kiệt ôm chầm, vỗ vai nhạc sĩ và khen ngợi: "Thật tuyệt vời".\r\nNhững năm sau 1975, nhiều tuồng tích cải lương bị kiểm soát nội dung gắt gao, một số vở kinh điển bị cấm trình diễn. Theo nghệ sĩ Lê Thiện - nguyên Phó giám đốc Nhà hát cải lương Trần Hữu Trang, một lần, bà dựng vở Tuyệt tình ca (còn có tên Ông cò quận 9), có nghệ sĩ Hồng Nga tham gia. Vở vướng kiểm duyệt vì một tình tiết liên quan đến chế độ cũ. Bà đang bế tắc, không biết phải làm sao thì hay tin ông Võ Văn Kiệt từ Hà Nội vào TP HCM. Ông đề nghị được xem vở đó và nhiều lần mỉm cười hài lòng khi các nghệ sĩ biểu diễn. "Nhờ đó, chúng tôi dễ thở hơn mỗi lần công diễn vở này. Ông chính là ân nhân của đông đảo nghệ sĩ thời đó", bà nói.<br>\r\n\r\n<h6>Cố Thủ tướng quan tâm sâu sát nơi ăn, chốn ở của văn nghệ sĩ </h6>. Từng nhiều dịp tiếp xúc ông, bà Lê Thiện cho biết mỗi lần gặp nghệ sĩ, ông đối đãi họ bằng cả tấm lòng. Trong các chuyến công tác, ông thường hỏi thăm bà và các diễn viên có được ăn uống đầy đủ, dặn cấp dưới lo cho đời sống nghệ sĩ trong đoàn, giúp họ giữ sức khỏe để biểu diễn phục vụ quần chúng. Có lần, Nhà hát Trần Hữu Trang mới thành lập, ông cùng đoàn thanh niên xung kích - trong đó có bà - đi xuồng về những nơi hẻo lánh ở miền Tây để biểu diễn văn nghệ, xem đó là cách đền ơn đáp nghĩa người dân.\r\n\r\nKhoảng năm 1996, khi đến thăm nghệ sĩ Phùng Há - đại diện Ban ái hữu nghệ sĩ, ông hỏi thăm bà cần gì và được bà nêu nguyện vọng muốn xây một khu nhà ổn định cho các diễn viên già yếu, không nơi nương tựa. Nhờ sự chỉ đạo của ông, bà được tạo điều kiện để cùng các nhà hảo tâm gây quỹ, góp tiền xây viện dưỡng lão trên một khu đất bỏ hoang.\r\n\r\nNăm 1998, Viện dưỡng lão nghệ sĩ (đường Âu Dương Lân, quận 8) ra đời trong sự hân hoan của đông đảo giới sân khấu Sài Gòn, trở thành nơi cưu mang hơn 20 tên tuổi gạo cội một thời như Ngọc Đáng, Thiên Kim, Diệu Hiền...\r\n\r\nThủ tướng Võ Văn Kiệt tên khai sinh là Phan Văn Hòa (bí danh Sáu Dân, Chín Hòa), sinh ngày 23/11/1922 trong gia đình nông dân, ở làng Trung Lương, tổng Bình Trung, huyện Vĩnh Trị, nay là xã Trung Hiệp, huyện Vũng Liêm, tỉnh Vĩnh Long.\r\n\r\nÔng trở thành đảng viên Đảng Cộng sản Đông Dương năm 17 tuổi và hoạt động cách mạng đến ngày thống nhất đất nước. Năm 1976, ông làm Chủ tịch UBND TP HCM, sau đó giữ chức Bí thư Thành ủy TP HCM. Tháng 2/1987, ông làm Phó chủ tịch Thường trực, sau đó là Phó chủ tịch thứ nhất Hội đồng Bộ trưởng. 5 năm sau, ông làm Chủ tịch Hội đồng Bộ trưởng.\r\n\r\nNăm 1992, ông được Quốc hội bầu làm Thủ tướng nhiệm kỳ 1992-1997. Từ 1997 đến 2001, ông làm cố vấn Ban chấp hành Trung ương Đảng Cộng sản Việt Nam. Ông là đại biểu Quốc hội các khóa VI, VIII, IX. Ông qua đời ngày 11/6/2008, thọ 86 tuổi.', 12, '2022-11-23 06:23:35', '2022-11-23 06:53:07', 4),
	(7, 'Chàng thủ khoa Cảnh sát được thăng hàm sớm hai năm', 'news_1_gd.jpg', 'Nguyễn Đức Thịnh, 22 tuổi, quê Bắc Giang là thủ khoa đầu ra Học viện Cảnh sát nhân dân năm 2022 và được phong hàm Trung úy trước niên hạn.', 'Nguyễn Đức Thịnh, 22 tuổi, quê Bắc Giang là thủ khoa đầu ra Học viện Cảnh sát nhân dân năm 2022 và được phong hàm Trung úy trước niên hạn.\r\n\r\nNguyễn Đức Thịnh được xướng tên trong Lễ tuyên dương thủ khoa xuất sắc tại Văn Miếu Quốc Tử Giám (Hà Nội) tối 18/11. Học chuyên ngành Điều tra tội phạm trật tự xã hội chất lượng cao, Thịnh là thủ khoa đầu ra của Học viện Cảnh sát nhân dân với điểm trung bình học tập 3,66/4, cũng là học viên duy nhất của khóa tốt nghiệp loại xuất sắc. Với thành tích này, Thịnh được phong hàm Trung úy sớm hai năm.\r\nLà con út trong gia đình hai anh em, có bố là bộ đội còn mẹ là giáo viên, Thịnh bắt đầu tìm hiểu về lực lượng vũ trang qua những lần nói chuyện về nghề nghiệp của bố. Từ nhỏ, Thịnh cũng say mê series phim truyền hình Cảnh sát hình sự, nên khi vào cấp ba, nam sinh trường THPT chuyên Bắc Giang đặt mục tiêu thi đỗ Học viện Cảnh sát nhân dân, mơ một ngày được trực tiếp phá án. Năm 2018, Thịnh đạt 27,3 điểm thi tốt nghiệp THPT, là thủ khoa tổ hợp D01 (Toán, Văn, tiếng Anh) của tỉnh và trúng tuyển nguyện vọng như mong muốn.\r\n\r\nVào nhập học, Thịnh ngay lập tức bị ngợp với cường độ của các hoạt động rèn thể lực. Nam sinh đánh giá đây không phải thế mạnh của mình và nhiều bạn khi thời phổ thông tập trung cho việc học, ít hoạt động thể thao.\r\n\r\nCuối học kỳ hai năm thứ nhất, nam sinh tham gia Giải thể thao sinh viên Việt Nam (VUG) cùng lúc với giai đoạn tổng kết đợt huấn luyện đầu khóa. Mỗi ngày, Thịnh dậy từ 5h theo lịch chung của trường, sau 17h tiếp tục tập luyện thêm 4-5 tiếng. Có hôm về ký túc xá lúc gần 0h, Thịnh ngất ngay khi đến cửa phòng. "Sau khi được bạn bè giúp đỡ, ngủ nghỉ, hôm sau mình vẫn dậy lúc 4h để kịp cùng cả đoàn đi thi đấu. Trường mình đấu hai trận vòng bảng và thắng cả hai nên đây là kỷ niệm đáng nhớ với mình", Thịnh kể.\r\nThời điểm chật vật với các hoạt động thể lực, Thịnh "chỉ mong ra trường đúng hạn". Dù vậy, khi đã quen với cường độ sinh hoạt và tự đánh giá được sức học của bản thân, nam sinh đặt mục tiêu tốt nghiệp xuất sắc.\r\n\r\nHai năm đại dịch Covid-19 cũng là thời gian Thịnh học các môn chuyên ngành. Do đặc thù của khối lực lượng vũ trang, không phải môn nào Thịnh cũng học trực tuyến, nhằm đảm bảo không lộ, lọt nội dung các bài giảng, tài liệu có tính bảo mật cao. Môn học khó, lại không được lên lớp thường xuyên, Thịnh phải tìm cách học khác để đạt hiệu quả.\r\n\r\nTrước mỗi học kỳ, nam sinh lên mạng nội bộ, thư viện của trường để tìm giáo trình, tài liệu, chuẩn bị kiến thức nền cho môn học. Gần đến ngày thi, Thịnh gặp giáo viên để hỏi các vấn đề chưa nắm rõ hay học nhóm và hỏi thêm bạn bè, anh chị khóa trên. Riêng những môn học về tội phạm kinh tế, Thịnh đánh giá là thử thách lớn. Theo nam sinh, hành vi của tội phạm kinh tế trừu tượng, khó nhận biết hơn tội phạm hình sự, ma túy. Do đó, ngoài tự đọc và chuẩn bị kiến thức nền, Thịnh còn thường xuyên theo dõi các vụ án được dư luận quan tâm để cập nhật thông tin.\r\n\r\nVới cách học này, nam sinh nói có thể hiểu rõ về môn học và các vấn đề liên quan, duy trì thành tích học trong top đầu lớp.\r\nNgoài học tập, Thịnh được bạn bè đánh giá đa tài khi là chủ nhiệm Câu lạc bộ Dân vũ, tham gia Câu lạc bộ tiếng Anh, Nội san - Truyền thanh của Học viện Cảnh sát nhân dân. Khi trường hoặc khối lực lượng vũ trang có các hoạt động sinh viên, Thịnh thường góp mặt.\r\n\r\nThiếu tá Đặng Đình Cường, giáo viên chủ nhiệm của Thịnh, nhận xét học trò có năng khiếu nghệ thuật, hăng hái tham gia phong trào của trường và ngành. Nam sinh còn đóng vai trò kết nối, động viên các thành viên lớp trong hoạt động này. "Thịnh cởi mở, sôi nổi và hoà đồng, hay giúp đỡ mọi người", thầy Cường nói.\r\n\r\nTự đánh giá, Thịnh nói thế mạnh của mình là chủ động, biết cách lên kế hoạch và phân bổ thời gian, nên có thể cân bằng giữa học và chơi. Khi giữ vị trí chủ nhiệm Câu lạc bộ Dân vũ, Thịnh thường linh động về giờ tập của các thành viên để phù hợp với lịch của trường, chịu khó nghe các ý kiến đóng góp về tổ chức, chuyên môn của bạn bè.\r\n\r\nTuy nhiên, ngoài một số tình huống có thể linh hoạt, Thịnh nhấn mạnh việc sống và làm việc có kỷ luật, tuân thủ kế hoạch đã đặt ra. Nam sinh cho rằng đây phẩm chất, tôn chỉ đầu tiên của người làm công tác pháp luật. "Mỗi vụ án luôn có quy trình thụ lý và giải quyết rõ ràng, cụ thể. Một cán bộ nếu làm việc tùy hứng có thể gây ảnh hưởng tới tiến độ điều tra, thậm chí dẫn đến vi phạm nghiêm trọng về tố tụng", Thịnh nói.\r\n\r\nThiếu tá Cường cũng đánh giá Thịnh có tính kỷ luật, chủ động cao. "Thịnh không đặt mục tiêu rồi để đó. Em đặt mục tiêu và đạt được, đó là cái đáng quý, thể hiện sự kỷ luật, khoa học trong học tập và làm việc của em", thầy Cường nói.\r\n\r\nSau bốn năm học, thầy chủ nhiệm thấy học trò có sự cải thiện rõ rệt về mặt thể lực, trưởng thành trong nhận thức và khả năng ngoại ngữ. Năm ngoái, Thịnh giành giải nhì và giải thuyết trình hay nhất tại Cuộc thi hùng biện tiếng Anh PPA’s Voice Battle cấp học viện, giải ba vòng Lan tỏa (Inspire Essay Round), cuộc thi Olympic tiếng Anh toàn quốc do trường Đại học Ngoại ngữ (Đại học Quốc gia Hà Nội) tổ chức.\r\n\r\nTừ giữa tháng 10, Thịnh nhận công tác tại Công an thành phố Bắc Giang, là cán bộ của Đội Cảnh sát điều tra tội phạm về trật tự xã hội. "Từ một cậu bé yêu thích và hâm mộ phim Cảnh sát hình sự, mình đã thực hiện được ước mơ. Mình hy vọng có thể áp dụng và phát huy tối đa những gì được học, hoàn thành mọi nhiệm vụ được giao", Thịnh nói.', 16, '2022-11-23 06:27:28', '2022-11-23 06:46:48', 1),
	(8, 'Cánh gà chiên trong suất ăn trường Ischool nhiễm khuẩn Salmonella từ đâu?', 'news_1_sk.jpg', 'Các bác sĩ cho rằng vi khuẩn bị tiêu diệt ở hơn 100 độ C, nên món cánh gà chiên chỉ có thể nhiễm khuẩn từ môi trường nhà bếp, hoặc quá trình bảo quản sau chế biến.', 'Các bác sĩ cho rằng vi khuẩn bị tiêu diệt ở hơn 100 độ C, nên món cánh gà chiên chỉ có thể nhiễm khuẩn từ môi trường nhà bếp, hoặc quá trình bảo quản sau chế biến.\r\n\r\nKết quả kiểm nghiệm của Viện Pasteur Nha Trang trên 8 mẫu thức ăn tại trường Ischool Nha Trang, nơi xảy ra vụ ngộ độc khiến hơn 600 học sinh nhập viện, cho thấy vi khuẩn Salmonella, Bacillus cereus và E. Coli có trong mẫu cánh gà chiên. Ngoài ra, vi khuẩn Bacillus cereus còn có trong mẫu nước mắm. Trước đó, kết quả cấy phân của bệnh nhân cho thấy vi khuẩn Salmonella.\r\n\r\nTheo bác sĩ Doãn Uyên Vy (chuyên gia chống độc, phụ trách Phòng khám Chống độc Bệnh viện Chợ Rẫy), thực phẩm lúc chiên ở hơn 100 độ C thì vi khuẩn khó sống sót (Salmonella bị tiêu hủy ở nhiệt độ 80 độ C) nên khả năng nhiễm khuẩn có thể xảy ra từ môi trường nhà bếp - nơi để cánh gà sau khi chiên xong. Bởi vi khuẩn ngoài tồn tại trong thực phẩm tươi sống còn lây lan qua các dụng cụ như dao, mâm đựng gà, bề mặt làm bếp, bàn tay người chế biến... "Cánh gà chiên sau khi để nguội mà vẫn cấy ra vi khuẩn, chứng tỏ bị nhiễm sau khi chiên rán", bác sĩ Vy nói, thêm rằng đây cũng có thể là nguyên nhân nước mắm bị nhiễm khuẩn Bacillus.\r\n\r\nCùng quan điểm, bác sĩ Nguyễn Trung Cấp, Phó Giám đốc Bệnh viện Bệnh nhiệt đới Trung ương, cho biết khuẩn E.Coli, Salmonella trong mẫu cánh gà chiên có thể do đã nhiễm trong quá trình đóng gói, bảo quản, trình bày thức ăn sau khi chế biến, do ô nhiễm nguồn nước hoặc bàn tay người thao tác bị nhiễm khuẩn chạm vào món ăn. "Nếu nhiễm từ khâu nguyên liệu cánh gà chưa chế biến, vi khuẩn sẽ bị tiêu diệt trong quá trình chiên rán nhiệt độ cao và sẽ không tìm thấy trong thành phẩm", bác sĩ Cấp nói.\r\nCơ quan chức năng tỉnh Khánh Hòa đang tiếp tục truy tìm nguồn lây nhiễm vi khuẩn trên cho các món ăn tại trường Ischool, chưa có kết luận.\r\n\r\nỞ góc độ chuyên gia, bác sĩ Vy đưa ra giả thuyết vi khuẩn Salmonella có thể lây lan qua cánh gà chiên từ món sốt trứng, bởi món này là lòng đỏ trứng còn sống, không được nấu chín ở nhiệt độ cao. Vỏ trứng gà thường dễ dính Salmonella từ phân gà, dễ lây lan qua bàn tay người nấu bếp cầm trứng đập vỏ. "Một vài mẫu xét nghiệm sốt trứng âm tính Salmonella không có nghĩa là hơn 800 phần ăn đều âm tính, không có khuẩn này", bà nói, cho biết thêm khuẩn Salmonella dễ sinh sôi nảy nở ở môi trường nhiệt độ từ 5 đến 60 độ C.\r\n\r\nBà đề nghị thực hiện ITAS - kỹ thuật khám sàng lọc riêng của bệnh nhân nhiễm độc để truy tìm độc chất và nguồn độc, từ đó giúp cắt bỏ nguồn độc cho bệnh nhân. Các chuyên gia phải phân tích kết quả xét nghiệm, xem xét toàn bộ tình huống, bối cảnh xảy ra, triệu chứng của các bệnh nhân... để tìm ra chính xác nguồn độc tại sao xảy ra, từ đó mới giúp phòng tránh được ngộ độc lần kế tiếp.\r\n\r\n"Sau khi xác định thủ phạm thì việc tìm ra nguồn độc từ đâu rất quan trọng, chẳng hạn môi trường nhà bếp dơ và tồn tại ổ vi khuẩn, cần phải xử lý để tránh nhiễm khuẩn sau này", bác sĩ Vy phân tích.\r\nBữa cơm trưa 17/11 tại trường Ischool Nha Trang gồm các món: cơm gà, sốt trứng; gỏi gà (gà xé, cà rốt, bắp sú, rau răm), cánh gà chiên, canh (xương, cà rốt, cải thảo), dưa leo. Bữa xế lúc 13h30 là bánh ngọt Paparoti, uống nước tại hệ thống lọc nước của trường. Sau khi ăn 6-9 tiếng đồng hồ, hơn 600 học sinh phải nhập viện điều trị do ngộ độc thực phẩm, trong đó một bé tử vong. TS. Cao Văn Trung, Cục An toàn thực phẩm (Bộ Y tế) đánh giá đây là vụ ngộ độc thực phẩm lớn nhất trong khối học đường.\r\n\r\nTrước sự việc đặc biệt nghiêm trọng này, Công an tỉnh Khánh Hòa đã đề nghị Sở Y tế chỉ đạo các bệnh viện và cơ sở y tế tổng hợp số liệu, cung cấp thông tin về vụ ngộ độc; bệnh nhân điều trị; để phục vụ công tác điều tra. Nhà chức trách cũng đề nghị các cơ sở y tế bảo quản tất cả mẫu máu của bệnh nhân, cung cấp hồ sơ bệnh án khi có yêu cầu.', 17, '2022-11-23 07:03:40', '2022-11-23 07:03:48', 2),
	(11, 'Ronaldo gây sốt với pha bật nhảy hơn 2 m', 'news_2_tt.jpg', 'Tiền đạo Cristiano Ronaldo lại khiến người hâm mộ trầm trồ với khả năng không chiến siêu phàm ở trận thắng Ghana 3-2 tại World Cup 2022 hôm 24/11.', 'Phút 16, từ quả phạt góc bên cánh trái, tiền vệ Bruno Fernandes chuyền sệt cho Raphael Guerreiro. Hậu vệ trái của Bồ Đào Nha xử lý hai nhịp rồi tạt về cột xa. Khi bóng rời chân Guerreiro, Ronaldo băng vào, bật nhảy cao hơn 2 m đánh đầu chệch cột.\r\n\r\nChưa có số liệu về độ cao Ronaldo đạt đến trong pha bật nhảy này. Nhưng Mohammed Salisu - người cao 1,91 m - cũng bật nhảy theo kèm, và đầu của trung vệ Ghana chỉ cao hơn phần hông của Ronaldo một chút.\r\n"Thật vô lý khi Ronaldo có thể bật cao đến vậy", tài khoản @DoentesPFutebol đăng ảnh Ronaldo đánh đầu và viết. "Tuổi tác chỉ là những con số. Ronaldo vẫn có thể bật cao như vậy ở tuổi 37", @renatoojunio chia sẻ. "Tôi muốn sự nghiệp của mình có một bước nhảy vọt như cách Ronaldo bật nhảy đánh đầu", @zuleidemota bình luận dí dỏm.\r\n\r\nTrong khi đó, @milheiraoligia chỉ ra thống kê đã được kiểm chứng rằng Ronaldo - cao 1,87 m - đã bật nhảy cao hơn so với tầm nhảy trung bình khi úp rổ của một cầu thủ bóng rổ ở giải nhà nghề Mỹ NBA. Năm 2011, Ronaldo từng tham gia một thí nghiệm đo chỉ số cơ thể của kênh Fox Soccer. Tiến sĩ Neil Smith - chuyên gia tại Đại học Chichester - kết luận rằng Ronaldo có thể nhảy cách mặt đất 0,44 m khi đứng yên và 0,78 m khi chạy đà.\r\n\r\nNăm 2019, khi còn khoác áo Juventus, Ronaldo từng ghi bàn nhờ khả năng vượt trội này. Sau quả tạt của Alex Sandro, tiền đạo Bồ Đào Nha bật cao 2,56 m với đầu gối gần như ngang đầu đối phương, rồi đánh đầu tung lưới Sampdoria. Cũng năm đó, Ronaldo bật cao 2,47 m để đón quả tạt của Emre Can và đánh đầu xé lưới Torino.\r\n\r\nTại Champions League mùa giải 2012-2013, Ronaldo - khi đó trong màu áo Real - thậm chí bật cao đến 2,93 m với thời gian trên không là 0,73 giây, để đón quả tạt của Angel Di Maria rồi đánh đầu tung lưới David De Gea trong sự bất lực của người theo kèm Patrice Evra.\r\n\r\nỞ đội tuyển Bồ Đào Nha, pha bật cao nhất đánh đầu thành bàn cao của Ronaldo đến ở trận bán kết Euro 2016 gặp Xứ Wales. Từ quả phạt góc bên cánh trái, Ronaldo bật cao hơn 2,4 m, ở trên không tới 0,7 giây rồi lái quả bóng đi với vận tốc 71,3 km/h, mở tỷ số ở phút 50.\r\n\r\nTrong sự nghiệp, Ronaldo ghi tổng cộng 140 bàn bằng đầu, gồm 112 bàn qua 104 trận ở cấp CLB và 28 bàn qua 194 trong màu áo đội tuyển Bồ Đào Nha. Đó là con số thống kê vượt xa cả những chuyên gia đánh đầu như Oliver Bierhoff, Miroslav Klose hay Peter Crouch.\r\n\r\nHôm qua, Ronaldo góp một bàn từ chấm 11m, mở ra chiến thắng 3-2 của Bồ Đào Nha trước Ghana. Nhờ đó, tiền đạo 37 tuổi thành cầu thủ đầu tiên ghi bàn ở năm kỳ World Cup và được bầu là cầu thủ hay nhất trận.', 14, '2022-11-25 14:42:37', '2022-11-25 14:42:37', 4),
	(12, 'Thuỳ Dương vô địch cờ vua thanh niên châu Á', 'thuyduongcovua.jpg', 'Kỳ thủ 19 tuổi Bạch Ngọc Thuỳ Dương giành chuẩn Đại kiện tướng nữ (WGM) đầu tiên, khi giành HC vàng cờ vua U20 châu Á sáng 25/11.', 'Sau chín ván cờ tiêu chuẩn từ 19/11 đến 25/11, Kiện tướng quốc tế nữ (WIM) Thuỳ Dương giành 7,5 điểm để độc chiếm vị trí đầu bảng nữ. Cô thắng bảy, hoà một và chỉ thua một ván tại giải, đạt hiệu suất thi đấu 2.307, và kiếm thêm 37 Elo để vượt mốc Elo 2.200.\r\nChức vô địch giúp Thuỳ Dương giành chuẩn WGM đầu tiên. Để trở thành WGM, cô cần giành thêm hai chuẩn nữa, đồng thời nâng Elo lên mốc 2.300.\r\n\r\nThuỳ Dương có Elo trước giải là 2.164, là hạt giống số năm. Hạt giống số một là WIM Assel Serikbay người Kazakhstan, với Elo 2.250. Bảng U20 nữ có 20 kỳ thủ tham dự, không có ai là WGM.\r\n\r\nViệt Nam cử hai đại diện dự giải cờ vua thanh niên châu Á ở thành phố Tagaytay, tỉnh Cavite, Philippines. Ở bảng U20 nam, kỳ thủ 11 tuổi Đầu Khương Duy góp mặt, khi chỉ là hạt giống số 17 trên 32 kỳ thủ. Khương Duy giành năm điểm qua chín ván, đứng thứ 11, kiếm thêm 92,8 Elo. Dù không thể kiếm thêm chuẩn IM, cậu vẫn để lại dấu ấn với chiến thắng trước hạt giống số ba Daniel Quizon (Elo 2.420). Chức vô địch bảng nam năm nay thuộc về kỳ thủ Ấn Độ Harshavardhan (2.426)\r\n\r\nGiải cờ vua thanh niên châu Á dành cho lứa U20, được tổ chức từ năm 1977 tại Philippines. Việt Nam từng có bốn kỳ thủ giành HC vàng bảng nam, và ba nhà vô địch bảng nữ. Trong đó, Võ Thị Kim Phụng từng hai lần đăng quang, năm 2000 tại Chennai, Ấn Độ và 2013 tại Sharjah, UAE.', 14, '2022-11-25 14:46:22', '2022-11-25 14:46:22', 1),
	(13, 'Phá đường dây đưa người từ Trung Quốc nhập cảnh trái phép', 'nhapcanh.jpg', 'Bộ đội Biên phòng vừa phá đường dây đưa người từ Trung Quốc nhập cảnh trái phép vào Việt Nam, rồi tiếp tục đi các nước Lào, Campuchia, Thái Lan...', 'Ngày 25/11, Đồn biên phòng Cửa khẩu quốc tế Lao Bảo khởi tố vụ án hình sự Tổ chức cho người khác xuất cảnh trái phép, quy định tại Điều 384 Bộ luật hình sự và đề nghị Viện kiểm sát nhân dân huyện Hướng Hóa ra quyết định chuyển vụ án hình sự đến công an tỉnh Quảng Trị tiếp tục điều tra.\r\n\r\nBiên phòng Quảng Trị cũng bàn giao 5 người Việt Nam cho Phòng An ninh điều tra, 11 người Trung Quốc cho Phòng quản lí xuất nhập cảnh, công an tỉnh Quảng Trị tiếp tục xử lý.\r\n\r\nĐại tá Lê Văn Phương, Chỉ huy trưởng Biên phòng Quảng Trị cho biết, đường dây tội phạm trên được tổ chức chặt chẽ, thủ đoạn tinh vi, liên quan đến nhiều người ở các tỉnh, thành. "Đường dây này đưa người từ Trung Quốc nhập cảnh trái phép sang Việt Nam, rồi tiếp tục đi các nước Lào, Campuchia, Thái Lan, Myanma", đại tá Phương nói.\r\nTrước đó, Biên phòng Quảng Trị phối hợp với Cục Trinh sát (Bộ Tư lệnh Biên phòng) xác lập chuyên án 822X nhằm triệt xóa đường dây tổ chức đưa, đón người nước ngoài xuất, nhập cảnh trái phép qua biên giới tỉnh Quảng Trị.\r\n\r\n23h ngày 19/11, tại bến đò khóm Tân Kim, thị trấn Lao Bảo, huyện Hướng Hóa, nhà chức trách bắt quả tang Lê Ngọc Quý (27 tuổi, trú khóm Duy Tân, thị trấn Lao Bảo) đang đưa 4 người Trung Quốc, từ 20 đến 28 tuổi, vượt biên trái phép sang Lào. Nhà chức trách sau đó bắt giữ khẩn cấp Lê Anh, 50 tuổi, trú xã Tân Thành, huyện Hướng Hóa - người cùng Lê Ngọc Quý đưa người vượt biên.\r\n\r\nMở rộng vụ án, Ban chuyên án bắt giữ thêm Trần Văn Bắc, 39 tuổi, trú huyện Hải Hậu, tỉnh Nam Định khi anh này đang lái xe chở 7 công dân Trung Quốc để xuất cảnh trái phép sang Lào.\r\n\r\nĐến sáng 20/11, lực lượng chức năng bắt giữ khẩn cấp Hồ Thị Vân, 48 tuổi, trú xã Tân Thành, huyện Hướng Hóa, là chủ mưu, trực tiếp nhận, điều hành và tổ chức cho toàn bộ số người Trung Quốc bị bắt nói trên xuất cảnh sang Lào.\r\n\r\nKết thúc chuyên án, ngoài 16 người bị bắt giữ, nhà chức trách thu giữ tang vật gồm hai xe ôtô, hai xe máy, 19 điện thoại di động...\r\n', 15, '2022-11-25 14:48:08', '2022-11-25 14:48:08', 2),
	(14, 'Nguyễn Võ Quỳnh Trang lĩnh án tử hình', 'thuytrang.jpg', 'HĐXX xác định hành vi của Nguyễn Võ Quỳnh Trang là tàn nhẫn, gây hậu quả đặc biệt nghiêm trọng... nên áp dụng mức án cao nhất; Nguyễn Kim Trung Thái lĩnh 8 năm tù.', 'Chiều 25/11, phiên xử Nguyễn Võ Quỳnh Trang, 26 tuổi, và Nguyễn Kim Trung Thái, 36 tuổi, bước vào phần tranh luận. Nêu quan điểm về vụ án, đại diện VKS cho rằng hành vi phạm tội của các bị cáo gây bức xúc trong dư luận. Trang vì "động cơ mục đích đê hèn" là không được gia đình Thái đồng ý cho kết hôn, không được người tình cho sinh con, nên trút sự ghen tức lên đầu bé An, đánh cháu bất kể lúc nào.\r\n\r\nĐỉnh điểm là ngày 22/12/2021, trong lúc Thái đi làm, Trang đã liên tục đánh cháu bé trong 4 giờ liên tiếp. Thái xem camera thấy Trang đánh con, gọi điện về bảo đừng đánh nữa nhưng Trang vẫn tiếp tục. Việc này thể hiện sự nhẫn tâm, vô cảm, lạnh lùng của bị cáo.\r\n\r\nTrang còn thể hiện sự tức giận, ghen tuông khi tìm đến nhà mẹ bé Vân An yêu cầu không được gặp con, xóa hết hình ảnh về chị này trên Facebook của Thái. Bị cáo lợi dụng việc sống chung với Thái để hành hạ bé An về cả thể chất và tinh thần.\r\n\r\n"Mặc dù trong quá trình điều tra bị cáo thành khẩn, nhưng hành vi của bị cáo là đặc biệt nguy hiểm cho xã hội; phạm tội với trẻ em; vì động cơ đê hèn... Xét thấy bị cáo không có khả năng cải tạo, cần phải loại bỏ ra ngoài xã hội", đại diện VKS nhận định, đồng thời đề nghị HĐXX tuyên phạt Trang mức án tử hình về tội Giết người; 2-3 năm tù về tội Hành hạ người khác; tổng hợp hình phạt là tử hình.\r\n\r\nĐối với Thái, lẽ ra bị cáo phải chăm sóc và bảo vệ con khi bé An không được sống với mẹ, đằng này bị cáo biết Trang nhiều lần đánh con còn hùa theo. Lúc biết hành vi phạm tội của Trang, Thái đã xóa tất cả dữ liệu camera của căn hộ nhằm che giấu cho người tình, gây cản trở quá trình điều tra. "Hành vi của bị cáo gây nhiều bức xúc trong dư luận", đại diện VKS đánh giá, đề nghị tòa tuyên phạt Thái 2-3 năm tù về tội Hành hạ người khác, 4-5 năm tù về tội Che giấu tội phạm.\r\nTự bào chữa, Nguyễn Võ Quỳnh Trang cho rằng hành vi của mình "đáng phải tử hình" và chấp nhận mức án này. Tuy nhiên, bị cáo cho rằng mình không ghen tuông với mẹ bé An để ngăn chị này gặp con, bởi quan hệ tình cảm với Thái đã hơn một năm chứ không phải mới. "Ngoài những hành vi sai trái với bé Vân An, bị cáo và gia đình (cha con Thái) cũng có những khoảnh khắc yêu thương, hạnh phúc. Trong những ngày bị tạm giam, bị cáo rất sám hối. Xin tòa xem xét cho bị cáo con đường quay đầu, để có cơ hội về chăm sóc mẹ và em nhỏ", Trang nói.\r\n\r\nBào chữa cho Trang, luật sư nói đã nhận thấy rõ sự ăn năn của bị cáo. Suốt thời gian bị tạm giam, Trang ăn chay sám hối về việc làm của mình. Bị cáo không biện minh, không lý giải được nguyên nhân hành hạ cháu Vân An, mà chỉ vì suy nghĩ không thấu đáo, không lường trước hậu quả xảy ra. Vì vậy, luật sư cho rằng mức án VKS đề nghị đối với Trang là quá nghiêm khắc.\r\n\r\nVề phần Nguyễn Kim Trung Thái, bị cáo nói sự việc xảy ra là mất mát, bài học rất lớn cho bản thân và gia đình, đề nghị HĐXX xem xét cho mức án nhẹ để sớm về chăm sóc người con còn lại.\r\n\r\nTrong khi đó, các luật sư của gia đình nạn nhân đề nghị tòa xử lý nghiêm Thái, bởi bị cáo đã thừa nhận nhiều lần chứng kiến cảnh Trang cởi trần bé Vân An ra đánh; túm tóc, bẻ răng con... Hay nhiều lần khác Thái chỉ ngồi cách con (đang bị Trang hành hạ) chưa đầy một mét nhưng cũng không có động thái can ngăn, thậm chí còn tham gia đánh bé. Căn cứ các tình tiết trên và hậu quả của vụ án, luật sư đề nghị HĐXX trả hồ sơ điều tra bổ sung, xử lý Thái về tội Cố ý gây thương tích hoặc đồng phạm thúc đẩy hành vi Giết người của Trang.\r\nĐối đáp lại quan điểm của luật sư xin giảm nhẹ cho Trang, VKS cho rằng, đây là vụ án vô cùng đau lòng và thương tâm, không gì có thể bù đắp cho bị hại. "Hành vi của bị cáo là quá tàn ác. Tại toà, bị cáo không ăn năn hối cải", đại diện VKS nêu quan điểm giữ nguyên đề nghị mức án tử hình.\r\n\r\nVề việc bị cáo xin giảm nhẹ hình phạt để khắc phục hậu quả và bù đắp tình cảm cho người con còn lại, VKS cho rằng, hậu quả xảy ra khiến bé An chết thì "không còn gì để khắc phục".\r\n\r\nVới quan điểm của các luật sư bảo vệ gia đình bị hại, đề nghị truy tố Thái về tội Cố ý gây thương tích, VKS cho là không có căn cứ bởi kết luận giám định thể hiện các vết thương cũ của cháu An "không phải nguyên nhân dẫn đến cái chết của nạn nhân", không giám định được tỷ lệ % thương tích...\r\n\r\nĐối với việc luật sư cho rằng Thái đồng phạm tội Giết người, VKS phân tích, vào ngày Trang đánh bé An bất tỉnh (sau đó tử vong) Thái không có nhà, không mong muốn hậu quả dẫn đến cái chết của con. Khi biết cháu bé bị Trang đánh bất tỉnh, Thái đã đưa con đi bệnh viện cấp cứu.\r\nNói lời sau cùng trước khi tòa nghị án, Nguyễn Kim Trung Thái cho rằng đã hiểu được sai lầm của bản thân, vì nhu nhược nên đã không can ngăn Trang đánh bé Vân An, gây ra vụ việc. "Bị cáo mong HĐXX xem xét thương tình để bị cáo sớm trở về lo cho con nhỏ, cha mẹ. Bị cáo vô cùng hối lỗi", Thái nói.\r\n\r\nCòn Trang cho biết: "Trong 11 tháng bị tạm giam bị cáo đã ăn năn hối cải, luôn day dứt về hành vi tàn nhẫn của mình. Xin HĐXX cho bị cáo cơ hội sống để ăn năn sám hối".', 15, '2022-11-25 14:50:25', '2022-11-25 14:50:25', 3),
	(15, 'Giấu ma túy trong bỉm trẻ em', 'giaumatuy.jpg', 'Phạm Thị Thùy Trang chia nhỏ 8 kg ma túy rồi giấu trong bỉm trẻ em để đưa đi tiêu thụ.', 'Khoảng 13h ngày 23/11, tại ngôi nhà ở đường Trần Hưng Đạo (TP Đồng Hới), Phạm Thị Quỳnh Trang, 24 tuổi, đang chia nhỏ ketamine vào các túi nylon thì bị cảnh sát phòng chống ma túy Quảng Bình bắt quả tang.\r\nNhà chức trách thu 8 kg tang vật được Trang giấu trong những chiếc bỉm quần của trẻ em để chuẩn bị vận chuyển tiêu thụ.\r\n\r\nKhám căn phòng, nhà chức trách thu thêm 3.111 viên nén (khoảng 1,5 kg), theo khai nhận của Trang là ma túy tổng hợp loại thuốc lắc.\r\nMở rộng điều tra, nhà chức trách bắt ông Phạm Văn Duẩn (60 tuổi, trú phường Đức Ninh Đông, TP Đồng Hới) là bố ruột của Trang.\r\n\r\nÔng Duẩn thừa nhận đã cùng cất giấu ma túy với con gái.\r\nViệc bắt cha con ông Duẩn nằm trong giai đoạn hai của chuyên án 119A do Công an Quảng Bình xác lập. Trước đó, ngày 10/11, tại giai đoạn một, nhà chức trách đã bắt ba người mua bán, vận chuyển trái phép chất ma túy, thu khoảng 13 kg ma túy tổng hợp dạng ketamin. Trong ba người này một tài xế quốc tịch Lào.\r\n\r\nBan chuyên án nhận định hoạt động của đường dây này rất tinh vi, ngụy trang ma túy trong hàng hóa thông thường, giao nhận qua các dịch vụ chuyển phát nhanh...', 15, '2022-11-25 14:55:09', '2022-11-25 14:55:09', 1),
	(16, 'Nhập hộ khẩu thế nào khi chủ hộ đã qua đời?', 'hokhau.jpg', 'Do việc cần gấp, tôi muốn nhập hộ khẩu cho vợ nhưng chủ hộ là ông nội tôi mất đã lâu. Xin hỏi vợ chồng tôi phải làm gì? (Lâm Tùng)', '<h5>Luật sư tư vấn</h5> <br>\r\n\r\nTheo Luật cư trú 2020: "Sổ hộ khẩu được cấp cho từng hộ gia đình. Mỗi hộ gia đình cử một người có năng lực hành vi dân sự đầy đủ làm chủ hộ để thực hiện và hướng dẫn các thành viên trong hộ thực hiện quy định về đăng ký, quản lý cư trú. Trường hợp không có ng­ười từ đủ 18 tuổi trở lên hoặc có người từ đủ 18 tuổi trở lên nhưng bị mất hoặc hạn chế năng lực hành vi dân sự thì đ­ược cử một ng­ười trong hộ làm chủ hộ".\r\n\r\nVới quy định trên, ta có thể hiểu chủ hộ phải là người còn sống từ đủ 18 tuổi trở lên và phải có năng lực hành vi dân sự đầy đủ. Do đó, trong trường hợp của bạn phải làm thủ tục thay đổi về chủ hộ.\r\nĐiều kiện để thực hiện điều này được quy định tại khoản 1 Điều 10 Thông tư 55/2021/TT-BCA có quy định: <i> "Trường hợp có sự thay đổi về chủ hộ thì phải có ý kiến của chủ hộ trong Tờ khai thay đổi thông tin cư trú (trừ trường hợp đã có ý kiến đồng ý bằng văn bản) hoặc ý kiến thống nhất đề cử của các thành viên trong hộ gia đình hoặc văn bản của Tòa án quyết định chủ hộ. Trường hợp chủ hộ chết, mất tích, mất năng lực hành vi dân sự thì ngoài ý kiến thống nhất đề cử của các thành viên hộ gia đình phải có thêm Giấy chứng tử hoặc Quyết định của Tòa án tuyên bố chết, mất tích, mất năng lực hành vi dân sự"</i>.\r\n\r\nNhư vậy, muốn nhập hộ khẩu vợ bạn vào hộ gia đình bạn thì đầu tiên phải đi làm thủ tục thay đổi chủ hộ (do ông bạn đã mất).\r\n\r\nĐể thay đổi chủ hộ, việc đầu tiên phải họp toàn bộ nhân khẩu có chung hộ khẩu với ông bạn và lập biên bản về việc thống nhất cử người đứng ra làm chủ hộ thay. Sau đó, người đứng chủ hộ mới nộp hồ sơ đến cơ quan hành chính để hoàn thiện thủ tục thay đổi chủ hộ, nộp kèm theo cả giấy chứng tử của ông bạn.', 15, '2022-11-25 14:58:20', '2022-11-25 14:58:20', 3),
	(17, 'Vì sao cựu thứ trưởng Y tế Cao Minh Quang được đề nghị án nhẹ hơn cấp dưới?', 'caominhquang.jpg', 'Luật sư tranh luận nhiều về việc VKS đề nghị tòa tuyên ông Cao Minh Quang 30-36 tháng tù treo trong khi cấp dưới của bị cáo là tù giam, dù cùng tội danh.', 'Đây là một trong số các ý kiến được các luật sư nêu suốt 7 giờ tranh tụng trong ngày 22/11 - buổi thứ hai xét xử 8 bị cáo trong vụ thất thoát 3,94 triệu USD ngân sách xảy ra tại Công ty Cổ phần Dược Cửu Long và Bộ Y tế năm 2006.\r\n\r\nCựu thứ trưởng Y tế Cao Minh Quang vắng mặt do nhập viện, được VKS đề nghị 30-36 tháng tù treo do cao tuổi, sức khỏe kém, có nhiều thành tích trong công tác.\r\n\r\nCùng bị truy tố về tội Thiếu trách nhiệm gây hậu quả nghiêm trọng theo Điều 285 Bộ luật Hình sự năm 1999 như ông Quang, song 4 cựu cán bộ thuộc Bộ Y tế, bị VKS đề nghị 18 tháng tù treo đến 36 tháng tù giam. Trong đó nặng nhất là ông Nguyễn Việt Hùng (nguyên cục phó Quản lý Dược) với 30-36 tháng tù giam.\r\nÔng Hùng bị cáo buộc với tư cách trưởng Đoàn kiểm tra liên Bộ Y tế - Tài chính, kiểm tra việc mua, sản xuất và việc thanh lý hợp đồng giữa Bộ Y tế và Dược Cửu Long nhưng không chỉ đạo kiểm tra trực tiếp sổ sách, kế toán, báo cáo tài chính và hạch toán số tiền 3,84 triệu USD; không tổng hợp lấy ý kiến liên ngành để làm rõ số tiền 3,84 triệu USD mà chỉ dựa vào báo cáo của Dược Cửu Long.\r\n\r\nBảo vệ ông Hùng, luật sư Vũ Tuấn Nghĩa cho rằng thân chủ là cán bộ ngành dược nhưng ông Quang phân công làm Trưởng Đoàn kiểm tra các vấn đề tài chính là không đúng chuyên môn, nên đương nhiên "không thể phát hiện sai phạm về tài chính của doanh nghiệp".\r\n\r\nNói "không có ý so sánh" hình phạt VKS đề nghị cho ông Quang và cho thân chủ, song luật sư cho rằng mức đề nghị này chưa thực sự hợp lý bởi ông Quang là cấp trên của ông Hùng thì trách nhiệm phải cao hơn.\r\n\r\nĐối đáp, đại diện VKS phân tích, ông Hùng không tham gia Đoàn kiểm tra liên bộ với tư cách kiểm tra viên, mà là trưởng đoàn - tức có trách nhiệm quán xuyến, bao quát, nắm được nguyên tắc, quy trình và kết quả kiểm tra. Tuy nhiên, cuộc kiểm tra "hoàn toàn không rà soát các hợp đồng, tuyệt nhiên không rà soát các điều khoản về giảm giá và do đó đã báo cáo sai".\r\n\r\nKhi các thành viên của Đoàn kiểm tra, thuộc Bộ Tài chính, gửi văn văn bản đề nghị Bộ Y tế kiểm tra lại khoản 3,84 triệu USD Cửu Long được nợ, ông Quang tiếp nhận văn bản này và giao trách nhiệm xử lý cho ông Hùng nhưng ông Hùng vẫn không thực hiện.\r\n', 15, '2022-11-25 15:01:50', '2022-11-25 15:01:50', 4),
	(18, '4 cựu cán bộ Phòng giao dịch Sacombank Cam Ranh bị khởi tố', 'sacombank.jpg', 'Nguyễn Thị Thanh Hà, cựu phó phòng giao dịch Sacombank Cam Ranh, cùng 3 nhân viên bị cáo buộc huy động vốn của nhiều người rồi vỡ nợ.', 'Ngày 23/11, bà Hà, 47 tuổi; Ngô Thị Hồng Nhạn, 41 tuổi, nguyên thủ quỹ Phòng giao dịch Sacombank Cam Ranh, bị Công an tỉnh Khánh Hòa bắt tạm giam về tội Tham ô tài sản theo khoản 4 Điều 353 Bộ luật Hình sự.\r\n\r\nNguyễn Trà My, 23 tuổi và Ngô Nữ Hồng Hải, 47 tuổi (cựu nhân viên phòng giao dịch) cũng bị khởi tố cùng tội danh nhưng được tại ngoại.\r\nBốn bị can bị cáo buộc "dùng tư cách cá nhân" vay vốn bên ngoài và khách hàng rồi không có khả năng trả nợ. Sự việc bị phát giác khi người cho vay đến làm thủ tục rút tiền tại Chi nhánh Sacombank Cam Ranh nhưng không được nên tố cáo với cơ quan điều tra. Số tiền thiệt hại được cho là 100 tỷ đồng.\r\n\r\nLãnh đạo Ngân hàng Nhà nước chi nhánh Khánh Hòa đã chỉ đạo Sacombank kiểm tra, nếu cán bộ lợi dụng tín nhiệm của ngân hàng, chiếm dụng tiền của khách thì nhà băng có trách nhiệm hoàn trả. Vấn đề nào thuộc về thỏa thuận dân sự thì đề nghị chuyển hồ sơ lên cơ quan điều tra.\r\n\r\nSacombank sau đó sa thải 4 nhân viên trên. Do sự việc liên quan nhiều cá nhân, có dấu hiệu hình sự nên ngân hàng chuyển toàn bộ hồ sơ cho công an tỉnh điều tra.', 15, '2022-11-25 15:03:38', '2022-11-25 15:03:38', 1),
	(19, 'Bộ Công an bắt đường dây đánh bạc 30.000 tỷ đồng ở Sài Gòn', 'danhbac.jpg', 'Hàng trăm cảnh sát cơ động, hình sự... của Bộ Công an đồng loạt ập vào 15 điểm tại TP HCM và Đồng Nai, bắt đường dây đánh bạc ăn thua 30.000 tỷ đồng.', 'Ngày 22/11, Cục cảnh sát Hình sự (C02) Bộ Công an bắt 14 người để điều tra hành vi Tổ chức đánh bạc và Đánh bạc. Danh tính những người này chưa được cung cấp.\r\n\r\nTheo cơ quan điều tra, cầm đầu đường dây này là một số người Việt sống tại Campuchia. Còn tại Việt Nam có 7 người làm tổng đại lý, quản lý các tài khoản cá cược rồi cung cấp lại cho mạng lưới phía dưới nằm ở nhiều tỉnh thành.\r\nCảnh sát xác định, đường dây này hoạt động theo hình thức đánh bạc truyền thống, tín chấp, cá cược các môn bóng đá, đá gà, số đề trên các trang web Sobet.com, Bong88.com, SV288.com... Số tiền ăn thua rất lớn, mỗi tuần giao dịch lên đến hơn 100 tỷ đồng.\r\n\r\nNhững người trong băng nhóm được phân công nhiệm vụ cụ thể như: đảm bảo kỹ thuật nhà mạng, xử lý khiếu nại của người chơi, giao nhận tiền thắng thua (trực tiếp hoặc chuyển khoản với nội dung mang ký hiệu riêng)...\r\n\r\nMột cán bộ điều tra cho biết, trước khi khai mạc World Cup 2022, những nghi can cầm đầu tại TP HCM lánh mặt đi các tỉnh, điều hành đường dây từ xa. Nhóm này hoạt động trong khoảng 3 năm qua. Tính đến thời điểm bị bắt, số tiền thể hiện ăn thua ước tính gần 30.000 tỷ đồng.\r\n', 15, '2022-11-25 15:05:27', '2022-11-25 15:05:27', 4),
	(20, 'Phó chủ tịch tỉnh Quảng Ninh Phạm Văn Thành xin thôi chức', 'phamvanthanh.jpg', 'Ông Phạm Văn Thành xin thôi chức Phó chủ tịch tỉnh Quảng Ninh nhiệm kỳ 2021-2026, sau ba tháng bị cảnh cáo do vi phạm trong phòng chống dịch.', 'Ngày 25/11, Ban chấp hành Đảng bộ tỉnh Quảng Ninh quyết định cho ông Thành thôi tham gia Ban chấp hành Đảng bộ tỉnh khóa XV nhiệm kỳ 2020-2025 theo nguyện vọng cá nhân.\r\nTrước đó, Ban thường vụ Tỉnh ủy Quảng Ninh thống nhất chủ trương cho ông Thành thôi đảm nhiệm chức vụ Phó chủ tịch UBND tỉnh nhiệm kỳ 2021-2026 theo nguyện vọng để nhận nhiệm vụ khác.\r\n\r\nBan Thường vụ Tỉnh ủy giao Đảng đoàn HĐND tỉnh, Ban cán sự đảng UBND tỉnh chỉ đạo thực hiện quy trình miễn nhiệm chức vụ Phó chủ tịch UBND tỉnh đối với ông Phạm Văn Thành.\r\nÔng Phạm Văn Thành 44 tuổi, từng công tác tại Ban Quản lý đầu tư và xây dựng công trình trọng điểm, Ban quản lý Khu kinh tế Quảng Ninh, Văn phòng Tỉnh ủy... Qua cương vị Chủ tịch rồi Bí thư kiêm Chủ tịch UBND thị xã Đông Triều, năm 2021 ông được bầu làm Phó chủ tịch tỉnh Quảng Ninh.\r\n\r\nCuối tháng 8, ông Thành bị Ban chấp hành Đảng bộ tỉnh Quảng Ninh cảnh cáo do có vi phạm, khuyết điểm và chịu trách nhiệm người đứng đầu đối với vi phạm trong phòng, chống dịch Covid-19, đấu thầu, mua sắm kit xét nghiệm Covid-19 của Công ty Cổ phần Công nghệ Việt Á tại thị xã Đông Triều khi còn là người đứng đầu địa phương này.\r\n\r\nTháng 9, Phó thủ tướng thường trực Phạm Bình Minh ký quyết định cảnh cáo ông Thành.\r\nLiên quan sai phạm trong phòng chống dịch, mua kit xét nghiệm của Việt Á, hồi tháng 6, Ủy ban Kiểm tra tỉnh Quảng Ninh kỷ luật nhiều cán bộ ở thị xã Đông Triều. Sau đó, công an tỉnh này khởi tố vụ án, khởi tố bị can bốn người vì làm trái công vụ, ký hồ sơ đấu thầu, nghiệm thu không đúng số lượng kit xét nghiệm và kit tách chiết đã sử dụng trên thực tế để thanh toán cho Công ty Việt Á, gây thất thoát cho ngân sách Nhà nước.', 11, '2022-11-25 15:18:59', '2022-11-25 15:21:10', 4),
	(23, 'Lãnh đạo Đảng, Nhà nước dâng hương cố Thủ tướng Võ Văn Kiệt', 'danghuong.jpg', 'Thủ tướng Phạm Minh Chính, Thường trực Ban Bí thư Võ Văn Thưởng đến Khu lưu niệm ở huyện Vũng Liêm dâng hương cố Thủ tướng Võ Văn Kiệt nhân 100 năm ngày sinh của ông (23/11/1922 - 23/11/2022).', 'Trong đoàn dâng hương chiều 22/11 còn có nguyên Chủ tịch nước Nguyễn Minh Triết, Trương Tấn Sang; nguyên Thủ tướng Nguyễn Tấn Dũng; Chủ tịch Ủy ban Trung ương Mặt trận Tổ quốc Việt Nam Đỗ Văn Chiến; Phó thủ tướng Vũ Đức Đam và các lãnh đạo, nguyên lãnh đạo Đảng, Nhà nước, đại diện gia đình cố Thủ tướng Võ Văn Kiệt.\r\n\r\nĐoàn đã dâng hương, dâng hoa, mặc niệm, tưởng nhớ cố Thủ tướng Võ Văn Kiệt. Phát biểu tại lễ dâng hương, ông Phan Văn Giàu, Giám đốc Sở Văn hóa Thể thao và Du lịch tỉnh Vĩnh Long, cho biết cố Thủ tướng Võ Văn Kiệt là nhà lãnh đạo xuất sắc của Đảng, Nhà nước và nhân dân Việt Nam; suốt đời phấn đấu, cống hiến vì đất nước.\r\nSau lễ dâng hương, Thủ tướng Phạm Minh Chính và các lãnh đạo dự lễ phát hành bộ tem bưu chính nhân kỷ niệm 100 năm ngày sinh cố Thủ tướng Võ Văn Kiệt; tham quan nhà trưng bày "Vườn ông Sáu Dân" nằm trong Khu lưu niệm - nơi lưu giữ nhiều hiện vật, hình ảnh quá trình hoạt động cách mạng của cố Thủ tướng...\r\n\r\nCố Thủ tướng Võ Văn Kiệt tên khai sinh là Phan Văn Hòa (bí danh Sáu Dân, Chín Hòa), sinh ngày 23/11/1922 trong gia đình nông dân, ở xã Trung Hiệp, huyện Vũng Liêm, tỉnh Vĩnh Long. Ông trở thành đảng viên Đảng Cộng sản Đông Dương năm 17 tuổi và hoạt động cách mạng đến ngày thống nhất đất nước.\r\n\r\nNăm 1976, ông làm Chủ tịch UBND TP HCM, sau đó giữ chức Bí thư Thành uỷ TP HCM. Tháng 2/1987, ông làm Phó chủ tịch Thường trực, sau đó là Phó chủ tịch thứ nhất Hội đồng Bộ trưởng; Chủ tịch Hội đồng Bộ trưởng. Năm 1992, ông giữ chức Thủ tướng nhiệm kỳ 1992-1997. Từ 1997 đến 2001, ông làm cố vấn Ban chấp hành Trung ương Đảng. Ông mất ngày 11/6/2008.', 11, '2022-11-25 15:20:55', '2022-11-25 15:20:55', 4),
	(24, 'Tầm nhìn vượt thời đại của cố Thủ tướng Võ Văn Kiệt', 'tamnhin.jpg', 'Những quyết định "vượt thời đại" của cố Thủ tướng Võ Văn Kiệt trong điều hành kinh tế và ngoại giao giúp Việt Nam sớm đổi mới, chủ động hội nhập quốc tế.', 'Quan điểm trên được các lãnh đạo cấp cao, chuyên gia khẳng định tại hội thảo khoa học cấp quốc gia về nhà lãnh đạo xuất sắc Võ Văn Kiệt nhân kỷ niệm 100 năm ngày sinh của ông (23/11/1922 – 23/11/2022), sáng 22/11. Hội thảo do Học viện Chính trị Quốc gia Hồ Chí Minh, Ban Tuyên giáo Trung ương, Văn phòng Chính phủ, Thành uỷ TP HCM, và Tỉnh uỷ Vĩnh Long phối hợp tổ chức.\r\n\r\nBộ trưởng Kế hoạch và Đầu tư Nguyễn Chí Dũng nói trên các cương vị lãnh đạo, từ Bí thư Thành ủy TP HCM, Chủ nhiệm Ủy ban Kế hoạch Nhà nước (sau này là Bộ Kế hoạch và Đầu tư), cố Thủ tướng Võ Văn Kiệt luôn là người khởi động và thúc đẩy quá trình đổi mới nền kinh tế.\r\n\r\nNăm 1977, TP HCM rơi vào tình cảnh thiếu đói, với vai trò Bí thư Thành ủy, ông Kiệt đã lập "tổ thu mua lúa gạo", chỉ đạo "xé rào", tổ chức thu mua gạo theo "giá thỏa thuận" để kịp thời đáp ứng nhu cầu gạo của người dân thành phố. Hành động này dù đi ngược các quy định đương thời, thậm chí có thể bị kỷ luật nặng, song được nhiều người dân ủng hộ. Việc này về sau trở thành ví dụ tiêu biểu cho "đột phá" cơ chế kế hoạch hóa tập trung trong phân phối hàng hóa.\r\nSau này vào năm 1982, khi ra Hà Nội làm Phó chủ tịch Hội đồng Bộ trưởng kiêm Chủ nhiệm Ủy ban Kế hoạch Nhà nước năm 1982, ông Võ Văn Kiệt luôn tìm cách để đổi mới công tác kế hoạch hóa. Ông đã chỉ đạo Ủy ban Kế hoạch Nhà nước từng bước thay đổi việc quản lý theo hướng giảm các chỉ tiêu kế hoạch mang tính pháp lệnh, tăng tự chủ cho các tổ chức sản xuất, kinh doanh.\r\n\r\nĐại hội Đảng lần thứ VI năm 1986 đánh dấu bước tiến quan trọng trong cải cách kinh tế ở Việt Nam, chính thức khởi động quá trình đổi mới toàn diện nền kinh tế. Với vai trò Chủ nhiệm Ủy ban Kế hoạch Nhà nước, ông Kiệt cùng với các cộng sự đưa vào Văn kiện Đại hội những nội dung quan trọng, "nhằm khắc phục bệnh tập trung quan liêu, bao cấp trong kế hoạch hoá; phải vận dụng đúng đắn và rộng rãi quan hệ hàng hoá - tiền tệ...".\r\n\r\nMùa thu năm 1989, tại cuộc gặp với các chuyên gia kinh tế và Ngân hàng Nhà nước, ông Kiệt đã nêu quan điểm đổi mới ngân hàng chính là tổ chức hệ thống ngân hàng thành hai cấp: Ngân hàng Nhà nước quản lý hệ thống, các ngân hàng thương mại lo kinh doanh tiền tệ và tín dụng. Đây được xem là khâu đột phá trong quản lý, kinh doanh tiền tệ lúc đó chưa ai nghĩ đến.\r\nVề mặt quan hệ quốc tế, ông Đỗ Hùng Việt, Trợ lý Bộ trưởng Ngoại Giao nói cố Thủ tướng Võ Văn Kiệt là người "đi tiên phong" trong hoạch định đường lối đổi mới, để lại " di sản" đặc biệt trong bối cảnh Việt Nam bị bao vây, cấm vận. Một trong những sáng kiến "phá vây" lúc đó của ông là chiến dịch "hoa sen nở", đi từ trong ra, cải thiện quan hệ với các nước Đông Nam Á vốn có lợi ích sát sườn, đi đôi việc bình thường hóa quan hệ với nước láng giềng Trung Quốc.\r\n\r\nTừ đó, Việt Nam tạo ra vị thế mới cải thiện, thiết lập quan hệ với các nước ở vòng cung thứ hai thuộc khu vực Tây Thái Bình Dương như Nhật Bản, Hàn Quốc, Australia, New Zealand, tiếp đó vươn sang vòng cung xa hơn là EU. Thành công của những bước đi ấy tạo thuận lợi cho việc thúc đẩy Mỹ chấm dứt chính sách cô lập, cấm vận Việt Nam.\r\n\r\nNguyên Phó thủ tướng Vũ Khoan, lúc đó là Thứ trưởng Ngoại giao, từng kể: "Thật tình, tôi rất ngạc nhiên về cách đặt vấn đề mang tính chiến lược như vậy của anh Sáu - một người vốn chưa hoạt động đối ngoại nhiều. Càng về sau tôi càng nghiệm thấy rõ ràng, kinh nghiệm đấu tranh cách mạng và chỉ đạo chiến tranh đã hun đúc trong anh tầm nhìn chiến lược cả về đối ngoại".\r\n\r\nTừ tháng 10/1991, Thủ tướng Võ Văn Kiệt chủ động dẫn đầu Đoàn đại biểu Chính phủ Việt Nam đi thăm chính thức Indonesia, Thái Lan và Singapore, sau đó là Malaysia, Philippines và Brunei. Kết quả là quan hệ Việt Nam - ASEAN từ đối đầu trong hơn 10 năm chuyển sang hợp tác vì phát triển. Khi nội bộ còn rụt rè trong việc tham gia ASEAN, chính ông đã nhắc nhở: "Rụt rè, bỏ lỡ cơ hội này sẽ là thảm họa cho đất nước". Với tinh thần đó, năm 1995, Việt Nam gia nhập ASEAN, đánh dấu lịch sử trong quan hệ quốc tế ở Đông Nam Á.', 11, '2022-11-25 15:23:04', '2022-11-25 15:23:04', 1),
	(25, 'TP HCM sẽ lập Ban chỉ đạo giải quyết các dự án treo', 'duantreo.jpg', 'Thành phố sẽ lập tổ công tác để sớm hoàn thành dự án treo ở 22 địa phương và nâng cấp thành Ban chỉ đạo cấp để điều phối, giám sát, theo Chủ tịch TP HCM.', 'Nội dung được Chủ tịch UBND TP HCM Phan Văn Mãi nêu tại buổi làm việc với quận Bình Tân chiều 25/11, nhằm giải quyết dứt điểm các dự án treo nhiều năm ở địa phương và thành phố.\r\nChủ tịch UBND TP HCM Phan Văn Mãi cho biết thành phố sẽ đề nghị 21 địa phương lập tổ công tác giải quyết các vướng mắc ở dự án treo như Bình Tân đã làm. Về phía thành phố, Sở Nội vụ lập tổ riêng, dành 2-3 tháng rà soát các nhóm vấn đề tồn đọng của dự án treo và thêm chừng đó thời gian tìm cách giải quyết rồi bàn giao công việc cho cơ quan chủ trì. Tổ này tương lai sẽ được nâng cấp thành Ban chỉ đạo để giải quyết các khó khăn ở các dự án đình trệ nhiều năm.\r\n\r\nTrước đó, quận Bình Tân kiến nghị việc sớm giải quyết các dự án kéo dài lâu. Cụ thể như Khu dân cư Vĩnh Lộc; Khu công nghiệp Vĩnh Lộc; Khu dân cư Phúc Lâm C; Khu dân cư – Trung tâm thương mại xã Tân Nhựt, huyện Bình Chánh; dự án bãi chôn lấp Bàu Cát...\r\nGiám đốc Sở Tài nguyên và Môi trường Nguyễn Toàn Thắng cho biết vấn đề của các dự án này kéo dài, một mình Sở cũng không thể giải quyết, và đề xuất lập tổ công tác để tháo gỡ dứt điểm.\r\n\r\nTheo Sở Tài nguyên và Môi trường, từ 2016 đến nay HĐND TP HCM đã thông qua tổng cộng 1.445 dự án thu hồi đất các loại. Trong số này, 402 dự án đã hoàn thành (28%), 741 đang thực hiện (51%), và 302 dự án treo (21%) - tức quá ba năm nhưng chưa thu hồi đất.\r\n', 11, '2022-11-25 15:24:48', '2022-11-25 15:24:48', 2),
	(26, 'Diễn tập ngăn chặn máy bay xâm nhập trái phép sân bay Buôn Ma Thuột', 'dientap.jpg', 'Ủy ban An ninh hàng không dân dụng quốc gia tổ chức diễn tập tình huống khẩn nguy như máy bay xâm nhập, bạo loạn, bắt cóc con tin tại Buôn Ma Thuột.', 'Sáng 25/11, hơn 3.000 người cùng hàng trăm phương tiện, thiết bị từ lực lượng hàng không, cảnh sát đặc nhiệm, phòng chống khủng bố, an ninh mạng, phòng chống tội phạm sử dụng công nghệ cao, cảnh sát cơ động kỵ binh, công an, quân đội địa phương đã tham gia diễn tập.\r\n\r\nTình huống giả định đầu tiên là đối phương tấn công Đài Kiểm soát không lưu bằng thiết bị điện tử, đưa mã độc làm tê liệt hệ thống máy tính đài không lưu khiến rối loạn hệ thống, máy bay không thể đáp xuống sân bay Buôn Mê Thuột. Các đơn vị không lưu khu vực miền Trung đã phối hợp, đưa cán bộ kỹ thuật xử lý rà soát hệ thống để chiếm lại quyền kiểm soát hệ thống không lưu.\r\n\r\nTiếp theo là tình huống đối phương liên tiếp đưa nhiều máy bay không người lái, phương tiện bay siêu nhẹ xâm nhập vào vùng cấm bay của Buôn Mê Thuột. Lực lượng không quân đã phát hiện và gây nổ trực tiếp một máy bay không người lái.\r\nTình huống thứ ba là nhiều người dân mang gậy tẩm xăng tràn vào sân bay gây rối, tạo đám cháy và chống lực lượng chức năng. Cảnh sát cơ động đã đưa vòi rồng phun nước, chó nghiệp vụ giải tỏa đám đông, bắt giữ người quá khích.\r\n\r\nSau khi đám đông được giải tỏa, tình huống tiếp theo là một số kẻ quá khích trà trộn vào đám đông trong sân bay bắt cóc con tin, cài thuốc nổ và gây cháy một số khu vực để chiếm quyền điều hành sân bay.\r\n\r\nCảnh sát đã đột kích theo nhiều hướng vào nơi giam giữ con tin để khống chế toàn bộ kẻ bắt cóc. Sau đó, đơn vị rà soát bom tìm được khối thuốc nổ trong sân bay song thuốc nổ được cài giờ đã gây cháy. Đơn vị chữa cháy đã dập tắt đám cháy, cứu các nhân viên thoát nạn.\r\nTheo Thứ trưởng Giao thông Vận tải Lê Anh Tuấn, các tình huống giả định sát với thực tiễn, đánh giá đúng thách thức vấn đề an ninh mà các quốc gia, trong đó có Việt Nam đang phải đối mặt.\r\n\r\nCuộc diễn tập giúp nâng cao khả năng phối hợp, điều phối các lực lượng đối phó với hành vi can thiệp bất hợp pháp tại sân bay; hoàn thiện phương án tác chiến, rèn luyện kỹ năng chiến đấu của cán bộ chiến sĩ; kiểm tra phương tiện sẵn sàng ứng phó với tình huống.\r\n\r\nThứ trưởng Lê Anh Tuấn yêu cầu các lực lượng tham gia diễn tập rút kinh nghiệm quá trình tập luyện để bổ sung, hoàn thiện phương án xử lý tình huống. Trên cơ sở đó cần thường xuyên luyện tập, hoàn thiện kỹ năng chỉ huy, điều hành tác chiến để chủ động xử lý khi có tình huống xảy ra.', 11, '2022-11-25 15:26:13', '2022-11-25 15:26:13', 2),
	(28, 'Nông dân miền Tây tất bật vụ hoa Tết', 'tronghoatet.jpg', 'Nông dân làng hoa Sa Đéc, Đồng Tháp và Chợ Lách, Bến Tre đang vào vụ sản xuất Tết, dự báo sản lượng tăng so với năm ngoái.', 'Cuối tháng 11, nông dân làng hoa Sa Đéc vào vụ sản xuất lớn nhất năm. Trên các tuyến đường xe chở phân rơm, tro trấu, vật tư sản xuất tấp nập. Không khí mua bán cũng nhộn nhịp hơn hẳn cùng thời điểm năm ngoái khi Covid-19 vừa lắng dịu. Thương lái các nơi bắt đầu đổ về mua hoa bán dịp tết dương lịch.\r\nBà Lê Thị Kim Thanh, phường Tân Quy Đông, TP Sa Đéc, xuống giống 6.000 chậu hoa cát tường, 1.000 chậu cúc Đà Lạt tăng hơn 2.000 chậu so với năm trước. "Năm ngoái hoa trúng giá nên năm nay tăng lên thử coi sao", người phụ nữ có hơn 10 năm kinh nghiệm trồng hoa tết cho biết. Theo bà chi phí sản xuất năm nay tăng hơn 20% chủ yếu do phân rơm tăng từ 85.000 đồng lên 120.000 đồng mỗi bao; phân bón, thuốc bảo vệ thực vật tăng 15-20% so với năm trước.\r\n\r\nKhông chỉ tăng chi phí, vài ngày gần đây thời tiết mưa đêm liên tục khiến hoa bị sâu bệnh tấn công. Sau mỗi cơn mưa, nông dân phải phun phun thuốc vừa trị bệnh vừa phòng ngừa, nếu không bệnh dễ lây lan khắp vườn.\r\n\r\nGần đó anh Đặng Văn Vũ Linh đang trồng thử giống mới - cúc pico và cúc hoạ mi mỗi loại 500 giỏ, bên cạnh 2.000 giỏ cúc Đà Lạt. Theo anh, giống hoa mới giá cây giống khá cao - 2.000 đồng mỗi cây, kỹ thuật trồng đòi hỏi phải chong đèn vào ban đêm trong thời gian ba tháng.\r\n\r\n"Loại này nếu không chong đèn chúng ra hoa mà không lớn cây, rất ít lá nhìn còi cọc, không đẹp", anh Linh giải thích và cho biết trung bình mỗi giỏ cúc pico, cúc Đà Lạt giá bán hơn 20.000 đồng nông dân mới có lời song giá cả hoa tết, rất khó đoán định. "Nông dân không thể dự đoán loại nào hút hay dội chợ, cứ trồng đại đến tết rồi tính", anh Linh nói.\r\nTrong số các loại hoa tết, mâm xôi thuộc loại khó trồng và rủi ro nhất. Ông Nguyên Văn Tuấn năm nay trồng hơn 3.000 chậu cúc mâm xôi xong hư gần phân nửa do triều cường, số còn lại có thể trổ hoa trước Tết.\r\n\r\n"Thời tiết lạnh, cúc hơn 170 ngày mới nở còn nóng ẩm chừng 155-160 ngày nở bung rồi", ông nói. Đặc trưng của loại cúc mâm xôi phải nở đúng dịp tết bán mới có giá, sớm và trễ hơn nhà vườn khó thu hồi vốn. "Trồng hoa tết trời cho ăn mới có ăn là vậy", người đàn ông quê Sa Đéc chia sẻ.', 11, '2022-11-25 15:28:09', '2022-11-25 15:28:09', 1),
	(29, 'Ngư dân được mùa cá hố', 'caho.jpg', 'Vùng biển huyện Kỳ Anh đang vào mùa cá hố, ngư dân đánh bắt được 70-80 kg mỗi ngày, đem bán thu 1-5 triệu đồng.', 'Một tuần qua, tại vùng biển xã Kỳ Phú, huyện Kỳ Anh, hơn 100 tàu thuyền công suất 16-18 CV từ ngoài khơi về đậu cách bờ khoảng 30 m với đầy ắp khoang cá hố. Ngư dân bỏ cá vào âu nhựa hoặc túi lưới, lấy dây thừng neo lại, chia mỗi nhóm hai người dùng đòn gánh vận chuyển vào bờ.\r\n\r\nNgư dân Trần Xuân Tân, 56 tuổi, trú xã Kỳ Phú, cho biết mùa cá hố từ cuối tháng 10 đến hết tháng 12 âm lịch. Cá ngư dân đang đánh bắt là loại nhỏ, nặng 2-4 lạng, dài 20-30 cm, sống cách bờ 1-3 hải lý, ở độ sâu 30-50 m, giá giao dịch tại biển 10.000 đồng/kg. Tàu thuyền ra khơi lúc 5h, trở về lúc 9-10h cùng ngày.\r\nNgoài ra, còn có cá hố loại lớn gần một kg, dài 1-1,2 m, giá 80.00-100.000 đồng/kg. Cá lớn sống cách bờ khoảng 50 hải lý, phải dùng thuyền công suất trên 200 CV ra khơi, mỗi chuyến đi kéo dài 4-5 ngày. Tại huyện Kỳ Anh, người dân chủ yếu sắm thuyền nhỏ đánh bắt gần bờ, ít hộ đi xa dài ngày.\r\n\r\nHàng ngày, ngư dân điều khiển thuyền "đi săn", thả lưới khi phát hiện cá bơi phía dưới. Tiếp đó họ hợp sức thu lưới về, bỏ lên khoang thuyền. "Hôm nào may mắn bủa lưới trúng luồng cá lớn, có thể thu gần 500 kg. Mỗi lần ra khơi 4-5 tiếng, một thuyền gồm 2-3 lao động bắt được 100-120 kg cá hố", ông Tân nói.\r\n\r\nGiữa buổi sáng, hàng chục thương lái đã lái xe máy, mang theo xô, làn, âu nhựa đựng hải sản chờ tại bờ biển xã Kỳ Phú để thu mua cá hố. Khi thấy thuyền cập bờ, họ ra đứng sát mép nước, tiếp cận các ngư dân đang gánh cá lên bờ để trả giá. Một số cơ sở đông lạnh lớn cũng cử người đến giao dịch.\r\nBà Phan Thị Tâm, 50 tuổi, thương lái ở huyện Kỳ Anh, cho hay cá hố rất đắt hàng nên phải cố thu gom càng nhiều càng tốt để nhập cho đối tác. "Mỗi ngày tôi mua được 40-50 kg, nhiều hôm may mắn được 90-100 kg", bà Tâm nói.\r\n\r\nNgoài nhập cho thương lái với giá 10.000 một kg cá loại nhỏ, nhiều ngư dân bỏ cá vào xô nhựa, chở về nhà cất một ít làm thực phẩm, sau đó đưa ra các chợ trên địa bàn bán lẻ với giá cao hơn 4.000-5.000 đồng/kg so với bán tại bờ biển.\r\n\r\nTheo ông Nguyễn Kiên Quyết, Chủ tịch xã Kỳ Phú, dịp này mỗi lần ra khơi, trung bình một tàu thuyền thu về một triệu đồng. Ngoài ra, một vài gia đình đông người, có thuyền lớn, may mắn trúng đậm 700-800 kg cá hố, sau khi trừ các chi phí như nhân công, nhiên liệu, khấu hao ngư cụ, họ lãi 4-5 triệu đồng.\r\n\r\n"Toàn xã có 270 tàu thuyền các loại, trong đó có hơn 100 tàu đánh bắt cá hố. Nhờ trúng đậm cá hố nên người dân rất phấn khởi, ai cũng tranh thủ ra khơi để cải thiện thu nhập cho gia đình", ông Quyết nói.', 11, '2022-11-25 15:30:14', '2022-11-25 15:30:14', 4),
	(30, '\'Làm tốt truyền thông chính sách sẽ mở ra nguồn lực lớn\'', 'lamtot.jpg', 'Theo Thủ tướng, truyền thông chính sách được làm tốt sẽ mở ra nguồn lực, tạo sức mạnh lớn, nhưng ý tưởng tốt mà truyền thông không tốt thì cuối cùng cũng không làm được.', 'Ngày 24/11, phát biểu kết luận hội nghị trực tuyến toàn quốc, Thủ tướng Phạm Minh Chính khẳng định, truyền thông chính sách tốt là giải pháp quan trọng để đưa chính sách pháp luật vào cuộc sống và mang hơi thở cuộc sống vào quá trình xây dựng chính sách. Điều này sẽ gia tăng hiệu quả, hiệu lực của chính sách.\r\n\r\nTheo ông, thực tế cho thấy một số chủ trương chính sách đúng đắn, nếu làm truyền thông không tốt thì không tạo đồng thuận cao trong xã hội. Có khi ý tưởng tốt, mang lại hiệu quả cao, nhưng truyền thông không tốt thì cuối cùng cũng không làm được. "Tinh thần là phải làm được, nói được thì mới tốt. Muốn vậy phải thông qua truyền thông", lãnh đạo Chính phủ khẳng định.\r\n\r\nThủ tướng cho hay, một trong những mục tiêu của truyền thông phải là xây dựng được niềm tin của người dân, không để xảy ra khủng hoảng truyền thông. Truyền thông tốt để tạo niềm tin, cho người dân thấy rằng làm thế là đúng, không làm không được, và Nhà nước bảo vệ quyền lợi hợp pháp của người dân trong bất cứ hoàn cảnh nào.\r\n\r\nBên cạnh đó, truyền thông chính sách là nhiệm vụ chính trị của các cơ quan hành chính, cơ quan báo chí trong xây dựng, phổ biến, tổ chức thực hiện và đánh giá tác động để mọi chính sách đến với nhân dân. Người đứng đầu Chính phủ yêu cầu phải làm truyền thông cả trước, trong và sau ban hành chính sách. Tạo cơ chế, điều kiện để người dân, tổ chức, doanh nghiệp góp ý phản biện.\r\nThủ tướng yêu cầu các cơ quan chủ động cung cấp thông tin trung thực, khách quan, không nên chỉ coi truyền thông chính sách là công việc của các cơ quan báo chí, truyền thông. "Muốn truyền thông mà các bộ, ngành, địa phương ngại cung cấp thông tin thì lấy chất liệu đâu mà truyền thông", Thủ tướng nói.\r\n\r\nVì vậy, ông giao Bộ Nội vụ chủ trì xử lý vấn đề tăng cường đội ngũ truyền thông chính sách tại các bộ, ngành địa phương; đồng thời nhất trí giải quyết vấn đề kinh phí cho công tác này theo hướng đặt hàng.\r\n\r\nBộ Thông tin và Truyền thông phối hợp với Bộ Tư pháp và các bộ, cơ quan liên quan, rà soát, hoàn thiện quy định pháp luật bảo đảm thực hiện tốt nhiệm vụ truyền thông chính sách. Văn phòng Chính phủ và Bộ Thông tin và Truyền thông tiếp thu ý kiến đóng góp tại hội nghị để hoàn thiện, trình Thủ tướng ban hành Chỉ thị về tăng cường công tác truyền thông chính sách thời gian tới.\r\n\r\nPhát biểu tại hội nghị, Bộ trưởng Thông tin và Truyền thông Nguyễn Mạnh Hùng đánh giá công tác truyền thông là "một việc, một chức năng của chính quyền các cấp"; phải có bộ phận chuyên trách thực hiện.\r\n\r\nChính quyền các cấp cần có ngân sách dành riêng cho truyền thông, cả chi thường xuyên và chi đầu tư. "Tăng thêm cho truyền thông 0,2% ngân sách trong lúc này là cần thiết để báo chí cách mạng nâng cao năng lực cạnh tranh, cả về hạ tầng công nghệ và nhân lực", Bộ trưởng Hùng nói.\r\n\r\nThứ trưởng Bộ Tài chính Nguyễn Đức Chi cho hay, thực tế chưa có khoản chi ngân sách riêng cho hoạt động truyền thông chính sách. Bộ Tài chính sẽ tiếp thu vấn đề này trong quá trình nghiên cứu sửa đổi Luật Ngân sách Nhà nước.\r\n\r\n"Việc đặt hàng truyền thông chính sách trên thực tế đã có, nhưng chưa đủ, hoặc chưa đáp ứng được yêu cầu do ngân sách Nhà nước còn eo hẹp", ông Chi nói, cho biết, Bộ Tài chính sẽ tiếp tục phối hợp với các bộ, cơ quan liên quan để tăng nguồn ngân sách với các cơ quan truyền thông chính sách của Chính phủ.', 11, '2022-11-25 15:31:46', '2022-11-25 15:31:46', 1),
	(31, 'Việt Nam lần đầu cử sĩ quan làm việc tại Phái bộ Huấn luyện của EU', 'siquan.jpg', 'Chủ tịch nước cử hai sĩ quan thực hiện nhiệm vụ tại Phái bộ Huấn luyện của Liên minh châu Âu (EU) và 5 người tham gia giữ hòa bình Liên Hợp Quốc.', 'Lễ trao quyết định của Chủ tịch nước được Bộ Quốc phòng tổ chức chiều 24/11. Hai sĩ quan đầu tiên làm nhiệm vụ tại Phái bộ của EU (ở Trung Phi) là trung tá Vũ Thị Liên với vị trí Cố vấn cơ quan huấn luyện nhà trường và đại úy Lê Như Tiến, vị trí Cố vấn/Sĩ quan huấn luyện cấp phân đội tại phái bộ.\r\n\r\nHai quân nhân này đang là Trợ lý Phòng Huấn luyện và Trợ lý Trung tâm Huấn luyện, Cục Gìn giữ hòa bình Việt Nam.\r\nTrong năm sĩ quan của Cục Gìn giữ hòa bình Việt Nam nhận nhiệm vụ tham gia gìn giữ hòa bình Liên Hợp Quốc, có hai người sẽ đến phái bộ Nam Sudan. Đó là trung tá Bùi Thị Minh Nguyệt làm Sĩ quan Tham mưu tác chiến và thiếu tá Phạm Đăng Bình làm Sĩ quan Quan sát viên quân sự.\r\n\r\nHai người sẽ đến phái bộ Trung Phi gồm thiếu tá Nguyễn Thùy Dương với nhiệm vụ Sĩ quan Tham mưu huấn luyện và đại úy Trần Thị Huệ làm Sĩ quan Truyền thông.\r\n\r\nRiêng trung tá Ngô Trí Tuấn sẽ đảm nhận cương vị Sĩ quan Tham mưu Công binh hạng nhẹ tại phái bộ UNISFA (khu vực Abyei).\r\n\r\nThay mặt 7 sĩ quan, trung tá Vũ Thị Liên hứa sẽ chấp hành nghiêm kỷ luật của Quân đội Nhân dân Việt Nam và quy định của phái bộ, hoàn thành tốt mọi nhiệm vụ.\r\nThượng tướng Hoàng Xuân Chiến, Thứ trưởng Quốc phòng yêu cầu các sĩ quan thực hiện nghiêm đường lối đối ngoại của Đảng, Nhà nước, Quân đội; giữ gìn đoàn kết nội bộ, xây dựng đoàn kết với đồng nghiệp quốc tế và nhân dân địa bàn.\r\n\r\nTheo tướng Chiến, sau 8 năm tham gia gìn giữ hòa bình, Việt Nam được Liên Hợp Quốc, tổ chức quốc tế, phái bộ địa bàn đánh giá cao; là một trong những nước có tỷ lệ hoàn thành nhiệm vụ xuất sắc cao nhất (chiếm 31%).\r\n\r\n"Chúng tôi rất tự hào về những gì chiến sĩ gìn giữ hòa bình Việt Nam làm được. Đây là niềm tự hào trong đối ngoại đa phương và đối ngoại quốc phòng", Thứ trưởng Quốc phòng nói, khẳng định các sĩ quan đã mang màu cờ sắc áo, hình ảnh mũ nồi xanh Việt Nam, tạo dấu ấn trên bản đồ thế giới.', 11, '2022-11-25 15:33:48', '2022-11-25 15:33:48', 2),
	(32, 'TP HCM có tân Giám đốc Sở Tài chính', 'sotaichinh.jpg', 'Nguyên Trưởng Cục Thuế TP HCM Lê Duy Minh, 50 tuổi, được bổ nhiệm làm Giám đốc Sở Tài chính, thay bà Phạm Thị Hồng Hà đã chuyển công tác.', 'Quyết định bổ nhiệm ông Minh làm Giám đốc Sở Tài chính TP HCM được Chủ tịch UBND thành phố Phan Văn Mãi ký ngày 24/11, hai tuần sau khi ông được Bộ Tài chính cho chuyển công tác từ Cục Thuế sang UBND thành phố.', 11, '2022-11-25 15:35:15', '2022-11-25 15:35:15', 4),
	(33, 'Trồng cây trên 120 ha đồi trọc đầu nguồn sông Thạch Hãn', 'thachhan.jpg', '120 ha đất trống, đồi trọc ở đầu nguồn sông Thạch Hãn được trồng phục hồi bằng cây bản địa như trẩu, xoan nhừ, dổi, lát hoa, gáo vàng, ươi...', 'Ngày 25/11, tại xã Tà Rụt, huyện Đakrông, công ty TNHH xã hội Trồng và Phục hồi rừng Việt Nam (VARS) phối hợp với Ban quản lý dự án đầu tư bảo vệ và phát triển rừng huyện Đakrông khởi động dự án trồng và phục hồi rừng đầu nguồn sông Thạch Hãn.\r\n\r\nNgười dân thôn A Đăng (xã Tà Rụt) đã cuốc những hố đất đầu tiên để xuống giống các cây bản địa. Tham gia dự án này, các hộ dân được hỗ trợ cây giống, phân bón, kỹ thuật và 12 triệu đồng mỗi ha trong ba năm đầu tiên. Đổi lại, họ phải cam kết trồng và bảo vệ rừng trong 10 năm.\r\nÔng Ngô Văn Hồng, Phó giám đốc VARS, cho hay trong hai năm 2022 và 2023, dự án sẽ trồng phục hồi gần 120 ha tại hai xã Tà Rụt (huyện Đakrông) và Hướng Việt (Hướng Hóa). Các loại cây trồng đều đa mục đích, vừa phòng hộ, bảo vệ không gian sinh tồn của cộng đồng, vừa tạo sinh kế, cho phép người dân khai thác lâm sản ngoài gỗ, nuôi ong, lấy mật ong rừng...\r\n\r\nTrong hai năm 2021-2022, tổ chức xã hội này đã trồng được 184.000 cây, tương đương 165 ha rừng tại Quảng Bình; đang phấn đấu trồng thêm 200 ha rừng tại Quảng Bình và Quảng Trị trước tháng 3/2023.\r\n', 11, '2022-11-25 15:36:35', '2022-11-25 15:36:35', 1),
	(35, 'Gần 10 năm chưa khai thác được đất hiếm', 'dathiem.jpg', 'Bộ Tài nguyên và Môi trường cấp phép cho doanh nghiệp khai thác hai mỏ đất hiếm ở Lai Châu và Yên Bái từ năm 2014, nhưng đến nay chưa mỏ nào hoạt động.', 'Với sự hợp tác của Nhật Bản, Tổng cục Địa chất và Khoáng sản (Bộ Tài nguyên và Môi trường) đã tìm thấy đất hiếm dạng hấp phụ ion ở xã Gia Phú, huyện Bảo Thắng, tỉnh Lào Cai. Sau đó, nhiều mỏ đất hiếm được tìm thấy ở Yên Bái, Hà Giang, Cao Bằng, Lạng Sơn, Nghệ An, Kon Tum, Lâm Đồng.\r\n\r\nĐến tháng 12/2014, Bộ Tài nguyên và Môi trường cấp phép cho Công ty Cổ phần Đất hiếm Lai Châu (Tập đoàn Than, Khoáng sản Việt Nam) cùng đối tác Nhật Bản khai thác mỏ đất hiếm kiểu quặng gốc ở Đông Pao, xã Bản Hon, huyện Tam Đường, tỉnh Lai Châu. Lý do mỏ này có trữ lượng lớn nhất cả nước, có thể khai thác ngay theo quy mô công nghiệp.\r\n\r\nNgoài Đông Pao, Bộ đã cấp phép cho mỏ đất hiếm ở xã Yên Phú, huyện Văn Yên, tỉnh Yên Bái. Mỏ này trữ lượng ít hơn và cũng chưa khai thác.\r\n\r\n<h5> Mỏ đất hiếm án binh bất động </h5> <br>\r\n\r\nMỏ Đông Pao là các dãy núi liền kề rộng hơn 132 ha, nằm cách thị trấn Tam Đường, tỉnh Lai Châu, khoảng 10 km. Để quản lý, cơ quan chức năng ký hiệu bốn khu vực đồi dựa trên trữ lượng, khu lớn nhất 60 ha, nhỏ nhất 8,7 ha.\r\n\r\nCuối tháng 11, tức 8 năm sau khi được cấp phép khai thác, mỏ Đông Pao vẫn án binh bất động, không có sự xuất hiện của máy móc, công trường. Trên các dãy núi, xen lẫn cây rừng là những nương ngô, chè, nhà sàn của đồng bào Lự.\r\n\r\nCách khu đất hiếm lớn nhất khoảng 2 km là trạm gác bảo vệ được xây dựng từ năm 2014. Trong trạm gác, ngoài nhà ở của bảo vệ là nhà kho luôn khóa cửa. Đây là nơi chứa các mẫu đất được thu về phục vụ công tác nghiên cứu hơn 10 năm trước. Do lâu ngày không sử dụng, mối mọt đã xông lên ở nhiều vị trí.\r\n\r\nCông ty Cổ phần Đất hiếm Lai Châu duy trì đội bảo vệ gồm 6 người bản địa. Bảo vệ Lò Văn Én cho hay, trước năm 2015, tình trạng xúc trộm đất đi bán diễn ra phổ biến, mỗi kg bán được 2.000-3.000 đồng. Đội anh Én chia làm ba nhóm thay nhau tuần tra ngày đêm, điểm gần đi bộ đường rừng một km, điểm xa hơn 5 km.\r\n\r\n"Có lúc chúng tôi phát hiện cả chục người đang xúc đất hiếm vào bao tải đem bán. Nhưng anh em cũng chỉ gọi công an đến cùng phối hợp thu giữ phương tiện, chứ không bắt được người", anh Én kể.', 11, '2022-11-25 15:38:52', '2022-11-25 15:38:52', 1),
	(36, 'TP HCM cắt giảm 38.000 cán bộ tổ dân phố ra sao?', 'catgiam.jpg', 'Thành phố muốn gộp tổ dân phố - nhân dân vào khu phố - ấp sau gần 40 năm tồn tại, giúp giảm hơn 38.000 nhân sự ở cơ sở.', 'Ban Thường vụ Thành uỷ TP HCM vừa thông qua chủ trương sắp xếp cán bộ tổ dân phố, tổ nhân dân từ nay đến hết quý 1/2025. Động thái được đưa ra trong bối cảnh thành phố là địa phương duy nhất cả nước tồn tại mô hình tổ chức hai cấp dưới xã, phường, gồm khu phố - ấp và tổ dân phố - tổ nhân dân. Mô hình này có từ 37 năm trước, hiện chưa phù hợp với Nghị quyết 18, theo đó dưới xã, phường chỉ có một cấp là thôn, tổ dân phố.\r\nSố người hoạt động cũng giảm từ hơn 64.000 người còn hơn 26.000 người, tương đương 59%. Trong số này đã có hai chức danh bổ sung cho khu phố - ấp sau khi sáp nhập là chi hội trưởng phụ nữ và bí thư chi đoàn thanh niên. Mỗi khu phố - ấp sẽ có một đội trưởng, dân quân tại chỗ; một tổ bảo vệ dân phố.\r\n\r\nNhư vậy, số lượng chức danh hưởng phụ cấp hàng tháng ở mỗi địa bàn giảm từ 13 xuống 3-5. Nhờ đó tổng kinh phí giảm từ 527 tỷ đồng xuống gần 483 tỷ đồng, tương đương 8%. Điều này giúp TP HCM tiết kiệm hơn 44 tỷ đồng mỗi năm.\r\n\r\nUBND TP HCM đánh giá mô hình mới bước đầu sẽ lúng túng bởi số người tham gia điều hành địa bàn giảm đột ngột quá lớn, khoảng 2,5 lần so với hiện tại. Sau khi không còn tổ dân phố - tổ nhân dân, khu phố chỉ còn 5 người hoạt động không chuyên trách, quản lý 350-450 hộ dân, tức bình quân mỗi người quản lý 70-90 hộ. Do đó, những người này cần có khả năng sử dụng công nghệ như Zalo, Viber...\r\n\r\nChủ tịch Phan Văn Mãi cho biết sau sắp xếp, ngoài kinh phí hoạt động cho các khu phố - ấp, thành phố sẽ có thêm phụ cấp hỗ trợ. "Nếu nói đủ sống thì rất khó, nên cần sự chia sẻ vì trách nhiệm cộng đồng", ông nói.\r\n\r\nPhó bí thư Thành uỷ TP HCM Nguyễn Hồ Hải nói chủ trương này "không phải xoá bỏ, mà là tổ chức lại các tổ dân phố, tổ nhân dân". Những người đang công tác ở đây vẫn có thể làm tiếp tại khu phố, ấp sau sắp xếp.\r\n\r\nTheo ông Hải, lợi ích của thay đổi này là tinh gọn bộ máy, nhờ đó chính sách hỗ trợ cho 5 chức danh ở khu phố - ấp sẽ tăng lên. Ban Thường vụ Thành uỷ yêu cầu UBND thành phố xây dựng đề án chi tiết, có lộ trình từng giai đoạn. Việc sắp xếp căn cứ quy định khung của Bộ Nội vụ, trên nguyên tắc xác định quy mô số hộ của khu phố - ấp phù hợp địa bàn dân cư (nông thôn, đô thị, nhà phố, chung cư).\r\n\r\n"Ví dụ các chung cư có thể lập tổ tự quản. Đây giống như ăng-ten của khu phố", ông Hải nói. Ngoài ra, địa phương cần phối hợp với công an, quân đội để phân chia địa bàn hợp lý, đảm bảo an ninh trật tự.\r\nĐánh giá về mô hình mới, một chủ tịch phường hơn 100.000 dân tại TP HCM băn khoăn bởi vai trò dân vận, cầu nối giữa xã, phường tới người dân của tổ dân phố, tổ nhân dân hiện rất lớn. Cán bộ cơ sở giúp xác minh nhân thân, tạm trú, hồ sơ nhà đất; thống kê dân số, nhu cầu lao động; thông báo hồ sơ, đăng ký thuế...\r\n\r\nQuy mô địa bàn lớn nhưng chỉ có 30 cán bộ nên lãnh đạo phường cho biết tất cả lĩnh vực khi triển khai xuống phải nhờ sự hỗ trợ của tổ dân phố. Tổ trưởng, tổ phó tổ dân phố có vai trò quan trọng trong hoà giải các xích mích xóm giềng mà "lên phường chưa chắc hiệu quả bằng người có uy tín nói".', 11, '2022-11-25 15:41:20', '2022-11-25 15:41:20', 1),
	(37, 'Thành Long đóng phim cùng Ngô Kinh', 'ngokinh.jpg', 'Sao hành động Thành Long lần đầu hợp tác đàn em Ngô Kinh trong phim chiếu Tết - "Tinh thần long mã".', 'Theo Sina, Ngô Kinh đảm nhận vai khách mời, nhân vật chưa được tiết lộ trong khi Thành Long đóng vai chính - Lão La. Ông vốn theo nghiệp võ, về già sống đơn độc, bầu bạn với một con ngựa. Lão La nguy cơ mất ngựa vì nợ nần. Trong lúc túng bấn, ông liên lạc với con gái Tiểu Bảo (Lưu Hạo Tồn đóng) - người xa lánh bố vì những hiểu lầm - nhờ giúp đỡ.\r\nPhim thể loại hành động, hài, bấm máy hồi tháng 9/2021, dự kiến ra mắt vào ngày 31/12. Thành Long cho biết tác phẩm nói về cuộc sống, tinh thần thép của các "võ sư long hổ" - những người chuyên thực hiện cảnh nguy hiểm ở phim hành động. Tài tử nói cả ông và Ngô Kinh đều xuất thân từ đây, vì thế tâm đắc với kịch bản. Tác phẩm là lời cảm ơn cho những đóng góp thầm lặng mà không thể thiếu của võ sư long hổ với phim võ thuật.\r\nỞ buổi quảng bá phim hôm 11/11, Thành Long nói quá trình ghi hình làm ông nhớ lại quãng thời gian là diễn viên vô danh, chịu bị đánh, liều mạng ở phim trường. "Nếu không liều lĩnh như thời đó, nếu không thực hiện những cảnh quay người khác không làm được, nếu không kiên trì, sẽ không có tôi và cả êkíp Thành gia ban (đội ngũ làm phim hành động của Thành Long) như bây giờ", diễn viên nói.\r\n', 12, '2022-11-25 15:44:31', '2022-11-25 15:44:31', 1),
	(38, 'Ngô Diệc Phàm bị phạt 84 triệu USD', 'ngodiecpham.jpg', 'Diễn viên Ngô Diệc Phàm phải đóng phạt và nộp khoản thuế thiếu, tổng cộng 600 triệu nhân dân tệ (gần 84 triệu USD).', 'Theo CCTV ngày 25/11, tổng cục thuế thành phố Bắc Kinh hoàn tất điều tra các hoạt động kinh doanh của Ngô Diệc Phàm, kết luận từ năm 2019 tới 2020, nam diễn viên ngụy tạo báo cáo tài chính nhằm che giấu thu nhập thực tế. Thông qua các công ty trong và ngoài Trung Quốc, Ngô Diệc Phàm gian lận số tiền thuế là gần 100 triệu nhân dân tệ (gần 14 triệu USD). Diệc Phàm còn nộp thiếu khoản thuế 84 triệu nhân dân tệ (11,7 triệu USD).\r\n\r\nAnh bị phạt và yêu cầu nộp khoản còn thiếu, tổng số tiền 600 triệu tệ. Cơ quan chức năng đã gửi quyết định xử phạt tới nam diễn viên.\r\nĐại diện cục thuế thành phố Bắc Kinh nói cơ quan chức năng tiếp tục theo dõi sát sao việc đóng thuế trong lĩnh vực giải trí, xử lý nghiêm khắc hành vi gian lận của nghệ sĩ.\r\n\r\nCùng ngày, tòa án quận Triều Dương, Bắc Kinh mở phiên sơ thẩm vụ Ngô Diệc Phàm bị tố cáo cưỡng dâm. Anh bị phạt 13 năm tù vì cưỡng bức ba cô gái, tổ chức "dâm loạn tập thể". Sau khi mãn hạn tù, Ngô Diệc Phàm sẽ bị trục xuất khỏi Trung Quốc.\r\n\r\nNgô Diệc Phàm bị bắt từ tháng 7/2021, sau khi Đô Mỹ Trúc - lúc đó là sinh viên Đại học Truyền thông - tố cáo anh. Đô Mỹ Trúc còn nói Ngô Diệc Phàm "dụ dỗ phụ nữ trẻ lên giường, trong đó có người vị thành niên". Theo Mỹ Trúc, nam ca sĩ từng chuốc say, gây mê nạn nhân.', 12, '2022-11-25 15:45:30', '2022-11-25 15:45:30', 2),
	(39, 'The Rock: \'Henry Cavill là Superman vĩ đại nhất\'', 'therock.png', 'The Rock giải thích lý do nhất quyết yêu cầu ban lãnh đạo DC mời Henry Cavill đóng Superman trong "Black Adam".', 'Trước Black Adam, tương lai của Henry Cavill trong Vũ trụ điện ảnh DC là dấu hỏi lớn với người hâm mộ. Tài tử không xuất hiện trong các tác phẩm mới của thương hiệu trong 5 năm, tính từ Justice League (2017).\r\n\r\nTheo Cinema Blend, The Rock nói về việc đấu tranh để mời Cavill trở lại với nhân vật Man of Steel: "Nếu muốn khẳng định Black Adam là thế lực mạnh mẽ, không thể ngăn cản mới của DC, chúng tôi cần đưa trở lại nhân vật có sức mạnh tương xứng. Đó là Superman với màn thể hiện của Henry Cavill. Hãng phim từng cho biết không thể đưa anh ấy trở lại. Nhưng chúng tôi không chấp nhận câu trả lời đó".\r\nTrên trang cá nhân, The Rock nói thêm: "Không có cách nào khả thi để xây dựng Vũ trụ điện ảnh DC và bỏ nhân vật mạnh mẽ nhất ra ngoài. Bạn phải đưa Superman vào phim. Và Henry Cavill là siêu nhân của thế hệ chúng ta. Theo tôi, anh ấy cũng là Superman vĩ đại nhất. Tôi muốn nói điều này theo một cách kính trọng với các diễn viên khác, đặc biệt là Christopher Reeve".\r\n\r\nĐể có cảnh Superman và Black Adam chạm trán cuối phim, The Rock đã tìm mọi cách thuyết phục đồng nghiệp trở lại DC. Trên trang cá nhân, Cavill nói cảnh phim chỉ là màn khai vị cho những điều tuyệt vời sắp đến. Một số tin đồn cho biết DC đang tập trung cho kịch bản các phần tiếp theo của Superman. Tuy nhiên, hãng hiện không tiết lộ tương lai nhân vật siêu anh hùng được yêu thích bậc nhất của họ.\r\n\r\nBlack Adam là bộ phim thứ 11 của vũ trụ điện ảnh DC, dựa trên nhân vật phản anh hùng cùng tên của DC Comic. Nội dung xoay quanh Teth Adam (The Rock) - người được ban tặng sức mạnh vô song của sáu vị thần Ai Cập.\r\nGần 5.000 năm sau khi bị cầm tù, Teth Adam được Adrianna Tomaz (Sarah Shahi) giải cứu. Amanda Waller (Viola Davis) - giám đốc tổ chức A.R.G.U.S và chỉ huy Task Force X - thành lập đội siêu anh hùng Justice Society of America nhằm chống lại Teth Adam. Tuy nhiên, một thế lực nguy hiểm xuất hiện khiến Adam và đội Justice Society of America phải hợp tác để ngăn chặn hiểm họa.', 12, '2022-11-25 15:47:36', '2022-11-25 15:48:05', 4),
	(40, '\'We Are One (Ole Ola)\' - khúc ca World Cup rộn ràng', 'cakhuc.jpg', '"We Are One (Ole Ola)" do Pitbull hát chính, mang âm hưởng samba sôi động của kỳ World Cup 2014 ở Brazil.', 'Nhân mùa Giải bóng đá Vô địch thế giới 2022, trang Billboard Latin mở cuộc bình chọn ca khúc được yêu thích nhất qua các mùa World Cup. We Are One (Ole Ola) đứng đầu với 41,76% phiếu, vượt qua The Cup of Life của Ricky Martin (28,54%) và Waka Waka của Shakira (21,35%).\r\nPitbull - người có biệt danh Mr. International (Quý ông Quốc tế) - hát cùng Jennifer Lopez và ca sĩ Brazil Claudia Leitte. Bài hát mang đậm văn hóa Brazil, với giai điệu samba đặc trưng, tiếng trống của nhóm nhạc nổi tiếng nước chủ nhà - Olodum. Các ca sĩ hát bằng tiếng Anh, Bồ Đào Nha, Tây Ban Nha.\r\n\r\nPitbull nói khi We Are One (Ole Ola) ra đời: "Tôi rất vinh dự khi cùng Jennifer Lopez, Claudia Leitte trở thành một phần của FIFA World Cup lần này. Tôi yêu bóng đá và sức mạnh của âm nhạc sẽ kết nối mọi người. Chúng ta ở đây để phá bỏ mọi rào cản. Thông qua thể thao và âm nhạc, cả thế giới hòa vào làm một".\r\n\r\nClaudia Leitte cho rằng We Are One (Ole Ola) góp phần thể hiện được tình yêu giữa âm nhạc và thể thao của đất nước cô. "Âm nhạc là đam mê của tôi, bóng đá là đam mê của người Brazil. Chúng tôi muốn thể hiện tình yêu, văn hóa của đất nước mình", Claudia Leitte nói.\r\nNhạc phẩm nhận đánh giá tích cực từ giới chuyên môn. Trang Fuse nhận xét giống như Waka Waka, bài hát pha trộn giai điệu âm nhạc truyền thống của quốc gia đăng cai với âm hưởng pop đương đại, dễ nghe và cuốn hút. Carl Williott của Idolator cho rằng We Are One (Ole Ola) thậm chí hấp dẫn hơn cả Waka Waka. Tờ Spin đánh giá tác phẩm truyền tải được tinh thần đoàn kết mà môn thể thao vua hướng tới.\r\n\r\nTuy nhiên, một bộ phận người Brazil cho rằng nên để nhiều nghệ sĩ nước họ thể hiện ca khúc hơn. Ngoài ra, họ không tán thành việc video góp phần tạo định kiến về hình ảnh đất nước Brazil là nơi những đứa trẻ đi chân trần đá bóng và phụ nữ bán khỏa thân nhảy samba. Theo Billboard, điểm trừ của bài hát là điệp khúc không tạo điểm nhấn, khó hát theo.\r\n\r\nWe Are One (Ole Ola) vào top 20 trên bảng xếp hạng Áo, Pháp, Đức, Italy, Tây Ban Nha và Brazil, đạt vị trí quán quân tại Bỉ, Hungary, Thụỵ Sĩ. Theo Nielsen SoundScan, sau lễ khai mạc World Cup 2014, MV đạt 131 triệu lượt xem trên Youtube, ba tuần có mặt trong bảng xếp hạng Billboard Hot 100 của Mỹ. Qua tám năm, We Are One (Ole Ola) nhiều lần vào danh sách bài hát cổ vũ World Cup được yêu thích của nhiều tạp chí.', 12, '2022-11-25 15:49:40', '2022-11-25 15:49:44', 3),
	(41, 'CĐV Anh hết lời ngợi khen Maguire', 'khen.jpg', 'CĐV Anh ấn tượng với sự chắc chắn của trung vệ Harry Maguire trong trận hòa Mỹ tại World Cup 2022, và ước anh thể hiện được điều đó tại Man Utd.', '"Tôi phải dành lời khen cho Maguire. Anh ấy thực sự rất chắc chắn tối nay", tài khoản @darealjuventino viết trên Twitter.\r\n\r\nMột số khác dùng từ "tuyệt vời", "ngưỡng mộ"... để nói về màn trình diễn của trung vệ 29 tuổi. @cantonasCallar - có vẻ như là một CĐV Man Utd - viết: "Ước gì Maguire có thể chơi cho Man Utd tốt như thế".\r\n\r\nTheo @Statman Dave, trong trận hòa Mỹ, Maguire chạm bóng 94 lần, hoàn thành 71 đường chuyền với đạt tỷ lệ chính xác 81%, tám đường chuyền dài, tám lần phá bóng giải nguy và thắng ba trong bốn lần không chiến. Cựu danh thủ Chris Sutton chấm điểm 6 trên 10 cho Maguire, điểm cao nhất trong các tuyển thủ Anh trận này.\r\nBên cạnh phòng ngự, Maguire còn gây ấn tượng với pha đột phá qua người trong vòng cấm Mỹ ở phút 11. Nếu hậu vệ Mỹ không kịp phá đường chuyền cuối cùng của trung vệ CLB Man Utd, Anh có thể đã ghi bàn.\r\n\r\n"Trong một pha đột phá điên cuồng, Maguire đã biến thành Neymar", một CĐV viết. Người khác lại khen: "Cậu ấy rê bóng như Messi".\r\n\r\n<strong>Maguire đến World Cup trong tâm thế của một đội trưởng mất suất đá chính tại Man Utd.</strong> Từ đầu mùa 2022-2023, anh chỉ được HLV Erik Ten Hag sử dụng chín trận, trong đó có bốn trận Ngoại hạng Anh. Dù vậy, HLV tuyển Anh Gareth Southgate vẫn triệu tập và xếp Maguire đá chính hai trận World Cup 2022. Ở trận ra quân, Maguire cùng đồng đội thắng Iran 6-2.\r\n\r\nHôm qua là trận thứ 50 của Maguire trong màu áo tuyển Anh. Trung vệ đắt nhất thế giới cho hay: "Tôi rất tự hào. Điều này có ý nghĩa rất lớn đối với tôi. Nó bắt nguồn từ sự chăm chỉ và tin vào bản thân".\r\nVề kết quả hòa 0-0, Maguire thừa nhận anh và đồng đội không đạt phong độ tốt nhất, đồng thời cho rằng Mỹ cũng là đội tuyển mạnh và chơi với nhịp độ tốt. "Sau cùng, chúng tôi vẫn có được một điểm. Nó giúp chúng tôi củng cố vững chắc vị trí trong bảng đấu", Maguire nói.\r\n\r\nAnh hiện sở hữu bốn điểm, hơn nhì bảng Iran một điểm. Mỹ xếp thứ ba với hai điểm, còn Xứ Wales đứng cuối bảng với một điểm. Ở lượt cuối, Anh cần thắng Xứ Wales để vào vòng 1/8 với tư cách nhất bảng.', 14, '2022-11-26 18:14:41', '2022-11-26 18:14:41', 1),
	(42, 'Australia hồi sinh ở World Cup 2022', 'uc.jpg', 'Thầy trò HLV Graham Arnold đả bại Tunisia 1-0 ở lượt hai bảng D chiều 26/11, thắp lại hy vọng đi tiếp ở World Cup 2022.', 'Sau khi thua Pháp ở trận ra quân, Australia buộc phải thắng để cứu vãn cơ hội hôm nay. Quyết tâm đó được đại diện khu vực châu Á chuyển hoá thành những đợt tấn công liên tục ở nửa đầu hiệp một. Trong đó, một phần không nhỏ là những quả tạt vào vòng cấm Tunisia.\r\n\r\nTừ một tình huống như vậy ở phút 24, họ mở tỷ số. Sau đường tạt của Craig Goodwin, bóng chạm một hậu vệ Tunisia bay vào vòng cấm. Mitchell Duke có mặt đúng lúc đúng chỗ, đánh đầu ngược vào góc xa. Như vậy, Australia đã mở tỷ số trong cả hai của họ ở World Cup 2022 - bằng toàn bộ những gì làm được trong 16 trận ở các kỳ trước.\r\nỞ trận ra quân, họ chọc thủng lưới Pháp ngay phút thứ 9. Tuy nhiên, những sai lầm ở hàng thủ khiến đội bóng xứ chuột túi bị dẫn ngược 2-1 ngay trong hiệp một rồi thua chung cuộc 1-4. Nhưng lần này, trước một Tunisia yếu kém hơn, Australia đã giữ được ba điểm ở lại.\r\n\r\nTunisia tiếp cận trận đấu theo cách trái ngược với Australia, sau khi có một điểm ở trận hòa Đan Mạch 0-0. Họ chủ động lùi sâu đội hình, kiểm soát trung lộ và đánh chặn các pha tấn công của Australia. Mãi đến phút 21, Tunisia mới có pha dứt điểm đầu tiên.\r\n\r\nSau bàn thua, đội bóng Bắc Phi chấp nhận mạo hiểm. Tuy nhiên, vận may không mỉm cười với họ. Phút 41, Drager sút từ 10 m đập chân hậu vệ Souttar. Bảy phút sau đó, tiền đạo đội trưởng Youssef Msakni dứt điểm chệch cột. Trong cả hai tình huống, khung thành Australia đều rộng mở.\r\nSau giờ nghỉ, Australia đổi chiến thuật. HLV Graham Arnold cho các cầu thủ lùi sâu, chơi phòng ngự phản công. Họ biến lợi thế dẫn bàn thành một thứ vũ khí để thử thách đối thủ.\r\n\r\nMất không gian tấn công rộng mở, Tunisia không còn những cơ hội rõ ràng như cuối hiệp một. Đại diện châu Phi chỉ biết dồn bóng cho Msakni. Phút 72, tiền đạo này một lần nữa dứt điểm trong vòng cấm. Nhưng thủ môn Ryan không gặp nhiều khó khăn để hóa giải. Trong một pha tấn công khác, Sliti không thể vượt qua Souttar để đối mặt Ryan.\r\n', 14, '2022-11-26 18:16:45', '2022-11-26 18:16:45', 1),
	(43, 'De Bruyne thất vọng vì Bỉ không đá giống Man City', 'kevin.jpg', 'Theo tiền vệ Kevin De Bruyne, Bỉ không thể sao chép phong cách thi đấu của Man City tại World Cup 2022, và điều đó buộc anh phải thích nghi.', '"Bỉ không thể đá giống Man City. Trở về đội tuyển, bạn phải thích nghi với những cầu thủ xung quanh", De Bruyne nói trong buổi họp báo ngày 25/11, trước trận gặp Morocco ở lượt hai bảng F. "Điều đó đôi khi khiến tôi thất vọng. Nhưng tôi nên thể hiện điều đó ít hơn, dù đó là sự hoàn hảo mà tôi hướng tới".\r\n\r\nỞ lượt mở màn ngày 23/11, Bỉ thắng chật vật Canada. Đại diện châu Âu thoát thua ở phút 11, khi thủ thành Thibault Courtois cản được quả phạt đền của Alphonso Davies. Tới phút cuối hiệp một, Michy Batshuayi bứt tốc rồi sút một chạm ghi bàn duy nhất trận đấu cho Bỉ.\r\n\r\nTrận này, De Bruyne nhiều lần nổi nóng với đồng đội. Tiền vệ 31 tuổi tranh cãi với Toby Alderweireld ngay trên sân, không ăn mừng bàn thắng của Batshuayi và thậm chí tỏ rõ sự không hài lòng trong cuộc trao đổi với HLV Robert Martinez ở sát đường biên dọc. Sau trận, Martinez tiết lộ De Bruyne bực tức vì Bỉ thi đấu dưới sức.\r\nDe Bruyne thừa nhận bản thân phản ứng thái quá và cần kiềm chế cảm xúc tốt hơn. Anh cũng khẳng định phải thích nghi với các đồng đội để giúp cải thiện lối chơi tập thể. "Thỉnh thoảng tôi bùng nổ cảm xúc, nhưng đó cũng là một phần của bóng đá. Tôi biết rằng phản ứng của mình không phải lúc nào cũng tốt", cầu thủ 31 tuổi nói thêm. "Tôi cũng tìm kiếm những khoảnh khắc sau đó để lấy lại bình tĩnh, nhưng mọi người biết rằng đó là cách tôi giúp đội chơi tốt hơn và tận dụng tối đa trận đấu".\r\n\r\nTại Man City, De Bruyne là hạt nhân trong cỗ máy trơn tru dưới trướng Pep Guardiola. CLB thành Manchester luôn kiểm soát bóng vượt trội, ngay cả khi đụng những đối thủ cạnh tranh ngôi vô địch. Dù vậy, De Bruyne cũng nhiều lần nổi nóng ở cấp CLB. Guardiola từng nói: "Màn trình diễn của De Bruyne đôi khi phụ thuộc vào tâm trạng của cậu ấy. Đôi khi De Bruyne bực mình, lúc thì không hài lòng".\r\n\r\nNgày 23/11, Guardiola gia hạn hợp đồng đến tháng 6/2025, kéo dài thời gian gắn bó với chủ sân Etihad lên chín năm. Vì thế, Man City sẽ trở thành đội bóng mà Guardiola gắn bó lâu nhất trong sự nghiệp cầm quân. Thông tin này khiến De Bruyne hạnh phúc. Anh nói: "Điều đó làm cho cuộc sống của tôi dễ dàng hơn rất nhiều. Cách ông ấy dẫn dắt đội rất rõ ràng và tôi cảm thấy rất thoải mái với Guardiola. Cả hai hợp đồng của chúng tôi đều có thời hạn đến năm 2025, vì vậy điều đó sẽ dễ dàng hơn".\r\n\r\nBỉ đón cú hích khi Romelu Lukaku trở lại tập luyện và có thể thi đấu trận gặp Morocco. Theo De Bruyne, sự có mặt của Lukaku sẽ cải thiện lối chơi của Bỉ và "gieo rắc nỗi sợ hãi" cho hàng thủ đối phương. De Bruyne cũng tin đội trưởng Eden Hazard sẽ tìm lại phong độ cao nhất, sau khoảng thời gian dự bị và chật vật với chấn thương tại Real.\r\n', 14, '2022-11-26 18:21:11', '2022-11-26 18:21:13', 4),
	(44, 'Neymar \'quẫn trí\' vì chấn thương', 'neymar.jpg', 'Tiền đạo Neymar khóc vì chấn thương mắt cá chân sau khi cùng Brazil thắng Serbia 2-0 ở trận ra quân bảng G World Cup 2022 ngày 24/11.', 'Neymar chấn thương sau pha vào bóng của hậu vệ Serbia Nikola Milenkovic, và được HLV Tite rút ra ở phút 79. Ảnh chụp cho thấy mắt cá chân phải của Neymar sưng phồng.\r\n\r\nTheo một nguồn tin của tờ Sun, Neymar "gần như quẫn trí và khóc khi rời sân". Sau đó, anh phải chườm đá quanh vết thương. Tiền đạo thuộc biên chế PSG về chỗ ngồi trong khu vực kỹ thuật của Brazil với đôi mắt đỏ hoe và được các đồng đội ngồi bên liên tục an ủi, động viên.\r\n\r\nTrận này, Neymar bị phạm lỗi chín lần, nhiều hơn bất kỳ cầu thủ nào khác ở 15 trận đầu vòng bảng World Cup 2022.\r\nTheo các bác sĩ tuyển Brazil, Neymar bị bong gân mắt cá chân. Họ cần từ 24 đến 48 giờ để đánh giá mức độ nghiêm trọng. HLV Tite vẫn tỏ ra lạc quan. Ông khẳng định: "Đừng lo, Neymar sẽ tiếp tục thi đấu ở World Cup. Bạn có thể chắc chắn về điều đó".\r\n\r\nChấn thương là ác mộng với Neymar ở World Cup. Năm 2014, anh từng gặp vấn đề ở lưng trong trận tứ kết với Colombia, dẫn đến vắng mặt ở bán kết - nơi Brazil thua thảm Đức 1-7.\r\n\r\nNeymar sở hữu 75 bàn sau 122 trận khoác áo Brazil, chỉ còn cách hai bàn so với kỷ lục của Pele.\r\nCác nhà cái đều xem Brazil là ứng cử viên số một cho chức vô địch World Cup 2022. Tuy nhiên, điều này có thể thay đổi nếu Neymar không thể thi đấu tiếp.\r\n\r\nBrazil đang giữ kỷ lục năm lần vô địch. Trước World Cup 2022, Neymar gây tranh cãi khi mặc bộ quần áo đội tuyển in sáu ngôi sao, một cách nhắc đến lần vô địch thứ sáu. Tờ Bild của Đức sau đó gọi Neymar là "kẻ kiêu ngạo". Richarlison khi đó đã công khai bảo vệ Neymar: "Bild mới thật kiêu ngạo. Chúng tôi chỉ là những kẻ mơ mộng. Chúng tôi đang mơ về ngôi sao thứ sáu. Chúng tôi sẽ giành nó, cho dù họ có thích hay không".\r\n\r\nTrong trận mở màn, Brazil thắng Serbia 2-0. Tiền đạo Richarlison ghi cả hai bàn ở phút 62 và 73.', 14, '2022-11-26 18:23:31', '2022-11-26 18:23:31', 3),
	(45, 'Mbappe cân bằng kỷ lục của Pele', 'mbappe.jpg', 'Tiền đạo Kylian Mbappe cân bằng kỷ lục ghi nhiều bàn nhất tại World Cup trước 24 tuổi của "Vua bóng đá" Pele, điều không ai làm được hơn 50 năm qua.', 'Mbappe lập cú đúp trong trận thắng Đan Mạch 2-1 ở lượt hai bảng D World Cup 2022 tối 27/11. Ở 23 tuổi 340 ngày, anh đã chạm mốc bảy bàn tại World Cup. Trong lịch sử, chỉ Pele có thành tích tương tự trước sinh nhật lần thứ 24.\r\n\r\nMbappe có thể độc chiếm ngôi đầu, thậm chí phá sâu kỷ lục này, khi sẽ đá ít nhất hai trận nữa, gồm trận cuối vòng bảng gặp Tunisia ngày 30/11 và trận thuộc vòng 1/8, do Pháp đã chắc chắn vượt qua vòng bảng World Cup 2022.\r\n\r\nĐứng sau Mbappe và Pele ở thống kê này là Gyula Zsengeller (Hungary), James Rodriguez (Colombia) với cùng sáu bàn, Oscar Miguez (Uruguay), Johan Neeskens (Hà Lan), Emilio Butragueno (Tây Ban Nha), Teofilo Cubillas (Peru) với cùng năm bàn.\r\nTrong kỳ World Cup đầu tiên năm 2018, Mbappe - ở tuổi 19 - góp bốn bàn và một kiến tạo, giúp Pháp vô địch. Khi đó, anh cũng cân bằng hai kỷ lục khác của Pele, gồm việc trở thành cầu thủ tuổi teen thứ hai trong lịch sử ghi bàn ở trận chung kết World Cup và là cầu thủ tuổi teen thứ hai lập cú đúp trong một trận đấu tại giải.\r\n\r\nVới bảy bàn tại World Cup, Mbappe có thành tích ngang Lionel Messi, Luis Suarez, hơn Harry Kane, Neymar (sáu bàn), và kém một bàn so với Cristiano Ronaldo. Chỉ 30 cầu thủ trong lịch sử ghi nhiều bàn hơn Mbappe tại World Cup. Hiện Miroslav Klose là cầu ghi nhiều bàn nhất World Cup với 16 bàn, xếp sau lần lượt là Ronaldo Nazario (15), Gerd Muller (14), Just Fontaine (13), Pele, Sandor Kocsis, Jurgen Klinsmann (11).\r\n\r\nKlose lập kỷ lục ghi bàn qua 24 trận tại World Cup, trong khi Mbappe mới đá chín trận. Kênh Eurosport nhận định tiền đạo của Pháp có thể phá kỷ lục của Klose, nếu tham dự kỳ World Cup thứ năm vào 2034 - khi anh... 35 tuổi.\r\n\r\nMbappe hiện sở hữu 31 bàn qua 61 trận ở tuyển Pháp, đạt hiệu suất 0,51 bàn mỗi trận. Trong danh sách những tuyển thủ Pháp ghi nhiều bàn nhất, tiền đạo 23 tuổi có thành tích ngang huyền thoại Zinedine Zidane, chỉ kém David Trezeguet (34 bàn), Karim Benzema (37), Michel Platini (41), Antoine Griezmann (42), Thierry Henry, Olivier Giroud (51). Trong số này, Mbappe, Benzema, Griezmann và Giroud còn có thể nâng cao thành tích.\r\n\r\nXuất phát trong trận gặp Đan Mạch, Mbappe còn trở thành cầu thủ PSG khoác áo tuyển Pháp nhiều nhất, với 56 trận. Kỷ lục này trước đây thuộc về Blaise Matuidi với 55 lần ra sân cho đội tuyển.\r\n\r\nVà anh đón chào các kỷ lục bằng bàn mở tỷ số từ cú đá chéo góc cận thành phút 61. Sau khi Andreas Christensen quân bình tỷ số cho Đan Mạch, tiền đạo 23 tuổi lại tỏa sáng với pha dứt điểm bằng đùi từ quả tạt của Griezmann, ấn định tỷ số 2-1.\r\n\r\nĐánh bại Đan Mạch, Pháp đạt sáu điểm, đứng đầu bảng D sau hai lượt trận. Australia - bại tướng của Pháp ở trận ra quân - đứng thứ hai với 3 điểm. Do Đan Mạch và Tunisia chia nhau hai vị trí còn lại với mỗi đội một điểm, điểm số hiện tại giúp Pháp chắc suất đi tiếp sớm một lượt, và là đội tuyển đầu tiên vượt qua vòng bảng World Cup 2022.\r\n\r\nPháp do đó trở thành nhà đương kim vô địch đầu tiên vượt qua vòng bảng kể từ Brazil năm 2006. Nếu giúp Pháp bảo vệ Cup vàng, Didier Deschamps sẽ tái lập kỷ lục vô địch World Cup hai lần liên tiếp của cố HLV Vittorio Pozzo - người từng dẫn dắt Italy tới hai chức vô địch thế giới năm 1934 và 1938.', 14, '2022-11-27 06:14:48', '2022-11-27 06:14:48', 1),
	(46, 'HLV Renard - từ cú vấp Nam Định đến ấn tượng World Cup', 'hlvrenard.jpg\r\n', 'Cá tính mạnh khiến Herve Renard không phù hợp ở V-League, nhưng là kim chỉ nam trên hành trình vươn lên, với điểm nhấn mới nhất là chiến thắng trước Argentina tại World Cup 2022.', 'Hình ảnh quen thuộc của Renard bên đường biên nhiều năm qua là một người đàn ông ngoài 50 tráng kiện trong chiếc quần âu và sơ-mi trắng. <br>\r\n<br>\r\nPhong thái lịch lãm này thực chất là một sự... mê tín của Renard. Khi đá giải vô địch châu Phi (AFCON) 2010, Zambia gặp Cameroon và thua 2-3. Trận đó, ông mặc một chiếc áo xanh nhạt. Sang trận sau, Renard mặc áo trắng, và Zambia chiến thắng rồi leo lên nhất bảng, vượt qua chính Cameroon. "Từ khi mặc áo trắng, đương nhiên tôi cũng thua trận. Nhưng tôi thắng rất nhiều và thích phong cách này", Renard nói. "Tất nhiên, để mặc như vậy, thời tiết phải đẹp. Như ở Anh, mặc áo sơ-mi vào tháng 12 là không tưởng. Hoặc có khi vì thế mà tôi không thành công ở Anh chăng?".\r\n<br>\r\nCâu trả lời hài hước của Renard nhắc về thời ông mới khởi nghiệp tại Anh, nhưng không phải Ngoại hạng Anh. Đội bóng ông dẫn dắt khi đó là Cambridge United thuộc hạng Năm. Đây là trải nghiệm không thành công, Renard bị sa thải chỉ sau một thời gian. Nhưng ông vẫn để lại ký ức sâu đậm với các cầu thủ về sự chuyên nghiệp và nhất là giáo án tập luyện nặng nề.\r\n\r\nCựu cầu thủ John Ruddy kể lại trên tờ Guardian: "Một trong những ký ức đáng nhớ nhất về Herve là ông ấy không chỉ đòi hỏi cống hiến trên sân, mà cả trong phòng tập. Giai đoạn tập huấn trước mùa giải cùng ông ấy vẫn là kỳ tập luyện nặng nhất mà tôi từng trải qua. Herve từng bắt chúng tôi tập plank hai phút liền và khiến tôi rung người như một chú chó. Trong khi đó, ông ấy chỉ cười và hô: ‘Cố lên nào John!’. Herve vẫn thường tập luyện cùng chúng tôi và giữ cơ thể ở trạng thái tuyệt hảo. Có vẻ đến bây giờ, ông ấy vẫn giữ nguyên phong độ ngày nào".\r\n\r\nSau khi rời Cambridge, HLV người Pháp lang bạt sang Đông Nam Á, và trở thành tân thuyền trưởng Nam Định. Nhưng ông cũng không trụ lại lâu và rời đội bóng chỉ sau ba tháng nắm quyền, ngay trước khi V-League 2004 khởi tranh. Theo lời kể của người trong cuộc, Renard để lại ấn tượng về sự thân thiện, chuyên nghiệp nhưng không được lòng nhiều cầu thủ của đội bóng thành Nam vì cá tính mạnh mẽ. <br>\r\nNhìn lại từ đầu sự nghiệp, HLV sinh năm 1968 không có duyên làm ở cấp CLB. Sau Cambridge United rồi Nam Định, ông còn huấn luyện AS Cherbourg (giai đoạn 2005-2007), USM Alger (2011), Sochaux (2013-2014) và Lille (2015). Trong số này, Sochaux xuống hạng, còn Lille cũng sớm chia tay Renard khi chỉ kiếm được 13 điểm trong 13 vòng đầu tiên.\r\n\r\nNgược lại, khi làm ở cấp ĐTQG, Renard như có bàn tay vua Midas và biến mọi thứ ông chạm vào thành vàng. Ông bắt đầu làm trợ lý cho Claude Le Roy tại Ghana, trước khi tự mình dẫn dắt Zambia lần đầu lọt vào tứ kết AFCON 2010 sau 14 năm. Hai năm sau, chú ngựa ô Zambia lần lượt đánh bại Senegal, Ghana trước khi khuất phục Bờ Biển Ngà của Didier Drogba để tạo nên bất ngờ bậc nhất lịch sử AFCON.\r\n\r\nSau khi vô địch, Zambia của Renard không thể vượt qua vòng bảng tại giải đấu kế tiếp. HLV người Pháp nhận hết lỗi về bản thân và từ chức. Năm 2014, ông quay lại châu Phi để dẫn dắt Bờ Biển Ngà và lập tức đưa đội tuyển này đăng quang. Đó là một chiến thắng nghẹt thở khi trận đấu phải phân định bằng loạt sút luân lưu, nơi Bờ Biển Ngà đá hỏng hai lượt đầu nhưng vẫn thắng 9-8. Sau giải đó, Renard trở thành HLV đầu tiên vô địch cùng hai đội tuyển khác nhau ở châu Phi.\r\n\r\nSau này, Renard còn góp công đưa Morocco vào vòng chung kết World Cup 2018. Và ở vòn chung kết thế giới đầu tiên sau 20 năm, Morocco đã có màn trình diễn không tệ trong bảng đấu góp mặt hai đại gia Tây Ban Nha và Bồ Đào Nha.\r\n\r\nSau khi rời Morocco năm 2019, Renard chuyển sang dẫn dắt Saudi Arabia. Dưới trướng Renard, đội tuyển Tây Á gây ấn tượng mạnh với lối chơi pressing khoa học, và bước vào World Cup 2022 với tư cách đội đầu bảng B vòng loại cuối cùng, xếp trên cả Nhật Bản lẫn Australia.\r\n', 14, '2022-11-27 06:18:15', '2022-11-27 06:18:15', 2),
	(47, 'Lewandowski khóc vì bàn thắng lịch sử', 'lewandowski.jpg', 'Robert Lewandowski xem pha lập công vào lưới Saudi Arabia là giấc mơ thành sự thật ở kỳ World Cup có thể là cuối cùng trong sự nghiệp.', 'Lewandowski ấn định chiến thắng 2-0 cho Ba Lan trước Saudi Arabia ở phút 82, lượt trận thứ hai bảng C World Cup 2022 chiều 26/11. Tiền đạo 34 tuổi bật khóc sau khoảnh khắc này khi lần đầu tiên ghi bàn ở đấu trường danh giá nhất đời cầu thủ.\r\n\r\n"Càng lớn tuổi, cảm xúc của tôi càng mạnh", Lewandowski nói sau trận đấu trên sân Education City. "Tôi biết đây có thể là World Cup cuối cùng trong đời và tôi khao khát ghi bàn. Tất nhiên, chiến thắng chung của đội vẫn là điều quan trọng nhất". <br>\r\nLewandowski khoác áo tuyển Ba Lan từ năm 2009. Cho tới trước trận gặp Saudi Arabia, anh chơi 135 trận, ghi 76 bàn cho Ba Lan nhưng không có bàn nào tại World Cup. Ở trận ra quân World Cup 2022 với Mexico, Lewandowski lỡ cơ hội khi sút hỏng phạt đền. Phải đến trận đấu thứ 136, siêu sao thuộc biên chế Barca mới giải được cơn khô hạn bàn thắng này.\r\n\r\n"Tôi đã dằn vặt sau quả phạt đền hỏng ăn trận trước", Lewandowski nói thêm. "Ghi bàn ở World Cup luôn là giấc mơ từ bé của tôi. Tôi đã phung phí khá nhiều cơ hội nhưng vui vì đã học từ các sai lầm đó. Ba Lan đang cống hiến hết sức và điều đó có ý nghĩa lớn nhất".\r\n\r\nBàn của Lewandowski giúp Ba Lan đứng đầu bảng C sau lượt trận thứ hai với bốn điểm. Họ chỉ cần hòa Argentina ở lượt cuối là chắc chắn có vé vào vòng knock-out, điều chưa xảy ra từ World Cup 1986. <br>\r\n\r\nTại World Cup 2018, Lewandowski đá cả ba trận vòng bảng nhưng không ghi bàn. Ba Lan khi đó thua hai trận đầu, dưới tay Senegal và Colombia, rồi thắng Nhật Bản ở lượt trận cuối khi đã chắc chắn bị loại.', 14, '2022-11-27 06:19:32', '2022-11-27 06:19:32', 4),
	(48, 'Ván cờ của Ronaldo và Messi', 'vanco.jpg', 'Cristiano Ronaldo đóng vai Vua cờ Magnus Carlsen, còn Lionel Messi trong vai streamer cờ vua số một Hikaru Nakamura, trong ảnh chụp hai ngôi sao bóng đá hôm 19/11.', 'Tác phẩm được coi là "bức ảnh thế kỷ" của Ronaldo và Messi chụp bên bàn cờ vua tái hiện thế cờ trong ván đấu giữa Carlsen và Nakamura tại siêu giải Na Uy ngày 8/6/2017. Messi ngồi ở bên cầm quân đen, tương ứng với Nakamura, còn Ronaldo cầm quân trắng giống Carlsen. <br>\r\nCarlsen và Nakamura cũng ám chỉ thế cờ trong ảnh giống với ván đấu của họ năm 2017. Nakamura phản hồi trên Twitter: "Tôi đang tự hỏi tại sao thế cờ này quen thuộc đến vậy". Còn Carlsen viết: "Sự kình địch vĩ đại thứ hai lịch sử bắt chước sự kình địch vĩ đại nhất".\r\n\r\nVán đấu của Carlsen và Nakamura diễn ra ở vòng ba cờ tiêu chuẩn, siêu giải Na Uy 2017. Không lúc nào trong ván đấu này Carlsen bị lép vế so với Nakamura. Vua cờ người Na Uy thậm chí còn có ưu thế lớn để thắng, nhưng anh đi những nước yếu, để Nakamura phản công và tấn công thành. Cuối cùng, Nakamura quyết định thí mã chiếu bất biến cầu hoà, nếu không anh sẽ thất bại. Còn Carlsen cũng không có cách nào khác là để lặp lại thế bất biến.\r\n\r\nThế cờ trong ảnh của Ronaldo và Messi mô phỏng nước 38 hoặc 40, vì thế cờ hai nước này giống nhau. Thế cờ xảy ra ngay sau khi Nakamura vừa thí mã. Chung cuộc, ván đấu hoà sau 40 nước đi, theo luật bất biến ba lần, tức là một thế cờ lặp lại ba lần. <br>\r\n', 14, '2022-11-27 06:22:43', '2022-11-27 06:22:43', 2),
	(49, 'HLV Bồ Đào Nha: \'50 năm nữa CĐV vẫn nhắc đến Ronaldo, Messi\'', 'ronaldomessi.jpg', 'Sau trận ra quân thắng Ghana ở World Cup 2022, HLV Fernando Santos xếp Cristiano Ronaldo vào nhóm huyền thoại bóng đá như Pele, Diego Maradona và Lionel Messi.', 'Trong họp báo sau trận thắng Ghana 3-2 tối 24/11, một phóng viên hỏi Santos về việc Ronaldo thành cầu thủ đầu tiên ghi bàn ở năm kỳ World Cup. Ông trả lời: "Ronaldo là thiên tài giống như Pele, Diego Maradona hay Messi. Chúng ta vẫn sẽ nói về họ trong 50 năm nữa. Giống như đối thủ của tôi ngày trước, huyền thoại Eusebio. Sau 50 năm, người hâm mộ ở Bồ Đào Nha hay trên thế giới vẫn nói về ông ấy".\r\n\r\nRonaldo vượt qua chính Messi, Pele, Uwe Seeler và Miroslav Klose để lập kỷ lục ghi bàn ở năm kỳ World Cup. Anh nói sau trận: "Kỷ lục cá nhân và chiến thắng cho Bồ Đào Nha là tất cả những gì tôi quan tâm".\r\n\r\nRonaldo kiếm phạt đền rồi sút thành công để mở tỷ số cho Bồ Đào Nha, nhưng thủ quân Ghana Andre Ayew gỡ hoà chỉ sau đó tám phút. Joao Felix và Rafael Leao lần lượt ghi thêm bàn trong hai phút, giúp Bồ Đào Nha dẫn 3-1. Ghana chỉ gỡ lại một bàn nữa do công của Osman Bukari, trước khi trung phong Inaki Williams bỏ lỡ cơ hội quý ở phút cuối.\r\n\r\nSantos cho rằng tỷ số 3-2 không đúng chút nào, vì Bồ Đào Nha áp đảo Ghana. "Ghana không thử gây khó khăn cho chúng tôi, khi họ chỉ phòng ngự chặt ở sân nhà", HLV 68 tuổi nói với CNN Bồ Đào Nha.<br>\r\n\r\nTrong hiệp một, Ghana không dứt điểm lần nào, còn Bồ Đào Nha cũng không tạo được cơ hội nguy hiểm. Santos nói rằng ông thúc giục học trò chơi mạo hiểm hơn trong hiệp hai, nếu không sẽ rơi điểm. "Cầu thủ hiểu và thay đổi lối chơi", ông nói thêm. "Nhưng chúng tôi lại thủng lưới từ tình huống không đáng có".<br>\r\n\r\nCầu thủ Bồ Đào Nha hú vía khi suýt rơi hai điểm trong những phút cuối, khi Ghana tràn lên tấn công. Nhưng tiền vệ Bruno Fernandes cho rằng họ không hề run rẩy. "Đấy là trạng thái lo lắng thường thấy ở những trận đầu tiên tại một giải lớn", cầu thủ kiến tạo hai bàn trận này nói. "Chúng tôi biết cách bảo vệ lợi thế dẫn bàn và điều đó mới quan trọng".<br>\r\n\r\nVới ba điểm trước Ghana, khi Bồ Đào Nha chỉ cần thêm một trận thắng để vượt qua vòng bảng World Cup 2022. Thầy trò Santos sẽ gặp Uruguay ở lượt thứ hai trên sân Lusail tối 28/11.', 14, '2022-11-27 06:24:44', '2022-11-27 06:24:48', 1),
	(50, 'Ronaldo lập kỷ lục ghi bàn ở năm kỳ World Cup', 'kyluc.png', 'Tiền đạo 37 tuổi Cristiano Ronaldo thành cầu thủ nam đầu tiên ghi bàn ở năm kỳ World Cup, với bàn mở tỷ số vào lưới Ghana tối 25/11.', 'Phút 61, tiền đạo Joao Felix giật gót cho Ronaldo chạy chéo xuống cấm địa, nhanh hơn trung vệ Mohammed Salisu một nhịp. Thủ quân Bồ Đào Nha gẩy bóng đi rồi ngã xuống khi có tác động từ trung vệ Ghana. Trọng tài người Mỹ Ismail Elfath ngay lập tức thổi phạt đền. Ông không cần xem lại tình huống, và giữ nguyên quyết định ban đầu. <br>\r\nTrước khi sút phạt đền, Ronaldo hôn lên bóng, lên tục thở bằng mồm. Anh chạy đà nhanh rồi sút căng tầm cao về góc trái, khiến thủ môn Lawrence Ati-Zigi không thể với tới. Siêu sao 37 tuổi nhảy xoay mừng bàn theo phong cách quen thuộc. Anh thành cầu thủ đầu tiên ghi bàn ở năm kỳ World Cup.\r\n\r\nRonaldo lần đầu chơi ở World Cup năm 2006, ở tuổi 21, ghi một bàn ấn định tỷ số 2-0 trước Iran ở vòng bảng. Bốn năm sau ở Nam Phi, anh tiếp tục ghi một bàn ở trận thắng Triều Tiên 7-0. Cũng ở vòng bảng World Cup 2014 tại Brazil, thủ quân Bồ Đào Nha ghi bàn ấn định thắng lợi 2-1 trước Ghana ở vòng bảng. Đến World Cup 2018 ở Nga, Ronaldo lập hat-trick trong trận hoà Tây Ban Nha 3-3 tại bảng B. Anh đã ghi tổng cộng tám bàn ở World Cup, đều trong vòng bảng.<br>\r\n\r\nGóp mặt ở trận này, Ronaldo đã cân bằng kỷ lục chơi ở năm kỳ World Cup, cùng sáu cầu thủ khác, trong đó có Lionel Messi. Sau Ronaldo, có bốn cầu thủ đã ghi bàn ở bốn kỳ World Cup, gồm Pele, Uwe Seeler, Miroslav Klose và Messi. Siêu sao Argentina không ghi bàn nào tại World Cup 2010, dù đội nhà ghi 10 bàn năm đó.<br>\r\n\r\nPha mở tỷ số vào lưới Ghana cũng giúp Ronaldo cải thiện kỷ lục ghi bàn cho một ĐTQG, với 118 bàn qua 192 trận. Nhưng anh vẫn còn ít hơn một nửa kỷ lục ghi 16 bàn của Klose ở World Cup.<br>\r\n\r\nBồ Đào Nha thắng Ghana chung cuộc 3-2, để vươn lên dẫn đầu bảng H. Ở hai trận còn lại, họ lần lượt gặp Uruguay và Hàn Quốc.', 14, '2022-11-27 06:27:53', '2022-11-27 06:28:12', 1),
	(51, 'Alcaraz và Nadal tạo lịch sử cho Tây Ban Nha', 'nadal.jpg', 'Lần đầu tiên sau 26 năm, bảng điểm ATP được thống trị bởi hai tay vợt tới từ cùng quốc gia, là Carlos Alcaraz và Rafael Nadal.', 'Lần gần nhất có hai tay vợt đồng hương làm điều này là năm 1996, khi Pete Sampras và Michael Chang chia sẻ các vị trí dẫn đầu bảng điểm ở thời điểm kết thúc mùa giải. <br>\r\nThất bại của Casper Ruud ở chung kết ATP Finals dưới tay Novak Djokovic ngày 20/11 giúp Nadal bảo toàn vị trí thứ hai. Carlos Alcaraz thì được vinh danh ở vị trí số một ngay khi Nadal bị loại ở vòng bảng ATP Finals. Alcaraz hơn Nadal 800 điểm, còn Nadal hơn Ruud 200 điểm. Bảng điểm năm nay sít sao, khi người dẫn đầu là Alcaraz chỉ kiếm được 6.820 điểm, kém xa mức thông thường là khoảng 10.000 điểm ở những mùa trước.\r\n\r\nAlcaraz và Nadal sa sút ở hai tháng cuối năm. Họ không vào chung kết và thường bị loại sớm. Alcaraz xuống phong độ từ khi giành Mỹ Mở rộng và lên số một thế giới hồi tháng 9. Tay vợt 19 tuổi dính chấn thương cơ nên bỏ lỡ ATP Finals. Trước đó Alcaraz có nửa đầu mùa giải ấn tượng khi vô địch hai Masters 1000 và giữ phong độ ổn định ở các Grand Slam.\r\n\r\nNadal cũng giành bốn danh hiệu đều ở sáu tháng đầu năm, nổi bật là Australia Mở rộng và Roland Garros. Anh cũng vào bán kết Wimbledon và chung kết BNP Paribas Open. Chấn thương bụng khiến Nadal lỡ nhiều giải ở nửa sau mùa, liên tiếp thua các trận sân cứng trong nhà.\r\n\r\nDjokovic thì leo ba bậc lên vị trí thứ năm sau khi đăng quang ở ATP Finals, đẩy Daniil Medvedev và Andrey Rublev xuống vị trí thứ bảy và tám. Top 10 còn có Taylor Fritz và Hubert Hurkacz.<br>\r\n\r\nTrong cuộc phỏng vấn sau ATP Finals, Djokovic tiết lộ luôn coi bản thân là số một. Anh nói: "Tôi dĩ nhiên không phải là số một năm nay. Người chơi tốt nhất năm là Alcaraz, còn tôi chỉ đứng thứ năm. Nhưng tôi luôn nghĩ trong đầu bản thân là số một, bất kể bên kia lưới là ai, bất kể mặt sân, bất kể giải đấu. Với tôi, tham vọng càng lớn càng tốt".<br>\r\n\r\nQuần vợt nam còn sự kiện tranh vô địch giải đồng đội Davis Cup cuối tháng này. Tám đội vào tứ kết là Italy, Mỹ, Đức, Canada, Hà Lan, Australia, Tây Ban Nha và Croatia. Tây Ban Nha thiếu cả Alcaraz và Nadal, còn Serbia chưa chắc có sự phục vụ của Djokovic.', 14, '2022-11-27 06:33:40', '2022-11-27 06:33:40', 1),
	(52, 'Nhật Bản thua sốc Costa Rica', 'thuasoc.jpg', 'Keysher Fuller ghi bàn duy nhất cho Costa Rica, ở trận thắng Nhật Bản 1-0 tại lượt 2 bảng E World Cup 2022.', 'Sau hai lượt trận đầu, Costa Rica chỉ dứt điểm trúng hướng cầu môn đúng một lần. Nhưng chừng đó là đủ để họ có cùng ba điểm như Nhật Bản. Bàn thắng quý như vàng của Keysher Fuller ở phút 81 giúp đại diện châu Mỹ bất ngờ sống lại hy vọng đi tiếp tại bảng E.<br>\r\nSau khi thua bạc nhược 0-7 trước Tây Ban Nha, Costa Rica tiếp tục bị Nhật Bản dồn ép trong suốt 90 phút. Nhưng lần này, các học trò Luis Fernando Suarez phòng ngự tốt hơn hẳn. Họ chơi tập trung, liên tục áp sát và không ngại đá rắn trước đại diện châu Á. Bên phía Nhật Bản, những cầu thủ tấn công của họ không còn sắc sảo như ở trận gặp Đức. Yuki Soma, Yunya Ito hay Daichi Kamada đều đã có những cơ hội ngon ăn, nhưng không thể ghi bàn.\r\n\r\nThời tiết nắng nóng khi trận đấu diễn ra lúc 13h tại Al Rayyan ảnh hưởng đến cách nhập cuộc của hai đội. Nhật Bản đá chậm, còn Costa Rica lùi sâu với sơ đồ 5-4-1. HLV Hajime Moriyasu thay đổi ba người trên hàng công Nhật Bản, chỉ giữ lại Kamada. Nhưng những Ritsu Doan, Ayase Ueda hay Ueki Soma không tạo ra áp lực đủ lớn. Đại diện châu Á không có cơ hội nguy hiểm nào trong 45 phút đầu.\r\n\r\nHiệp hai chứng kiến một Nhật Bản hoàn toàn khác, khi Takuma Asano vào sân. Tiền đạo từng ấn định chiến thắng 2-1 cho Nhật Bản trước Đức tạo ra luồng gió mới cho hàng công đội bóng áo xanh. Chỉ trong 4 phút đầu hiệp hai, Nhật Bản dứt điểm tới 4 lần. Những cơ hội xuất hiện với tần suất dày đặc hơn khi Kaoru Mitoma, rồi Yunya Ito vào sân. Nhật Bản có tới hai cơ hội đá phạt ngon ăn ở rìa vòng cấm, nhưng Soma và Kamada đều bỏ lỡ.<br>\r\nChỉ số bàn thắng kỳ vọng của Costa Rica chỉ là 0,12, còn của Nhật Bản là 1,05. Quy luật nghiệt ngã của bóng đá xảy đến ở phút 81. Hiroki Ito, cầu thủ vào thay Nagatomo, mắc sai lầm trong khâu bẫy việt vị, để Keysher Fuller trống trải ở rìa cấm địa. Cầu thủ Costa Rica cứa lòng hiểm hóc bằng chân trái, mở tỷ số cho Costa Rica. Sau 171 phút tại World Cup, Costa Rica mới lần đầu dứt điểm trúng hướng cầu môn nhưng họ ngay lập tức có bàn thắng.<br>\r\n\r\nBàn thắng giúp Costa Rica thêm thuận lợi trong việc thi triển lối đá phòng ngự phản công. Trước hàng phòng ngự số đông của đối thủ, Nhật Bản không có thêm cơ hội rõ rệt, dù Mitoma và Yunya Ito rất nỗ lực tạo ra sự khác biệt. Sự xuất sắc của thủ môn Keylor Navas cũng khiến hàng công Nhật Bản tịt ngòi. Ở trận gặp Tây Ban Nha, thủ môn PSG không cản phá được lần nào trong 7 pha dứt điểm của đối thủ. Nhưng hôm nay, anh cứu thua 3 lần, giúp Costa Rica trắng lưới.<br>\r\nChiến thắng gây sốc của Costa Rica giúp họ có cùng ba điểm như Nhật Bản và Tây Ban Nha, và còn nguyên cơ hội vào vòng knock-out. Nhật Bản chỉ còn biết tự trách mình, khi tự làm hẹp đi cơ hội tiến xa. Ở lượt trận cuối, Costa Rica gặp Đức, còn Nhật Bản gặp Tây Ban Nha - đội gây ấn tượng mạnh bậc nhất từ đầu World Cup.', 14, '2022-11-27 16:07:36', '2022-11-27 16:07:42', 4),
	(53, 'Dân Ukraine lũ lượt rời khỏi thành phố Kherson', 'danuk.jpg', 'Người dân tại Kherson lũ lượt rời Kherson trong bối cảnh thành phố bị lực lượng Nga pháo kích dữ dội sau khi Ukraine tái kiểm soát.', 'Đoàn xe tải, xe van và ôtô con của những người sơ tán khỏi Kherson kéo dài hơn một km bên trên tuyến đường ở ngoại ô thành phố ngày 27/11. Một số xe kéo theo rơ-moóc hoặc chở vật nuôi cùng đồ đạc của những người đi sơ tán.\r\n\r\n"Thật buồn khi chúng tôi phải rời nhà của mình. Chúng tôi phải rời đi vì pháo kích và đã có dân thường thiệt mạng", Yevhen Yankov, một người sống tại thành phố Kherson, cho biết.\r\n\r\nSvitlana Romanivna, một cư dân Kherson khác, cho biết "chúng tôi đã nếm trải địa ngục thật sự, khu phố của chúng tôi đang bốc cháy, đó là một cơn ác mộng. Mọi thứ chìm trong biển lửa".<br>\r\nĐợt di tản diễn ra trong bối cảnh lực lượng Nga gần đây pháo kích dữ dội thành phố Kherson, nơi quân đội Ukraine tái kiểm soát hồi đầu tháng. Các đợt tập kích chủ yếu nhằm vào hạ tầng thiết yếu tại Kherson, cũng như nhiều thành phố khác tại Ukraine, đôi khi gây ra thương vong cho dân thường.\r\n\r\nGiới chức Ukraine ngày 19/11 truyên bố đã có những chuẩn bị cần thiết để sơ tán dân sống tại thành phố Kherson và khu vực lân cận, khẳng định đây là hoạt động mang tính tự nguyện.<br>\r\n\r\nUkraine thông báo có một số lựa chọn, trong đó có sử dụng thành phố Mykolaiv làm điểm trung chuyển trước khi đưa người dân tới những khu vực an toàn hơn ở miền tây.<br>\r\n\r\nTrước khi rút quân khỏi thành phố Kherson và khu vực lân cận ở bờ tây sông Dnieper, giới chức Nga thông báo đã sơ tán hơn 70.000 người sống tại khu vực. Họ cho biết con số thực tế có thể lớn hơn do nhiều người tự đi thuyền qua sông Dnieper thay vì sử dụng phà do giới chức Nga bố trí.<br>\r\n\r\nGiới chức Ukraine cho biết thành phố Kherson và khu vực lân cận nhiều ngày qua mất điện, cáo buộc lực lượng Nga phá hủy hạ tầng năng lượng tại đây. Thị trưởng Kherson của Ukraine Roman Holovnia ngày 12/11 thừa nhận thành phố đối mặt tình hình nhân đạo nghiêm trọng khi thiếu nước, thuốc men, bánh mì và mất điện.', 13, '2022-11-27 16:10:09', '2022-11-27 16:10:09', 3),
	(54, 'Anh nói Nga dùng tên lửa thời Liên Xô đánh lừa Ukraine', 'anhnoi.jpg', 'Anh cho rằng Nga đang tháo đầu đạn hạt nhân khỏi tên lửa Kh-55 từ thời Liên Xô, biến chúng thành mồi bẫy đánh lừa phòng không Ukraine.', '"Nga nhiều khả năng đang tháo dỡ đầu đạn hạt nhân khỏi các tên lửa hành trình đời cũ và phóng những quả đạn này vào Ukraine. Hình ảnh tại Ukraine cho thấy mảnh vỡ nghi của tên lửa hành trình phóng từ máy bay Kh-55, được biên chế từ thập niên 1980 chuyên để mang đầu đạn hạt nhân", Bộ Quốc phòng Anh cho biết trong báo cáo tình báo hôm nay.<br>\r\n\r\nQuân đội Anh cho rằng tên lửa Kh-55 tháo đầu nổ vẫn có thể gây thiệt hại thông qua động năng và lượng nhiên liệu chưa tiêu thụ, nhưng khó lòng đạt hiệu quả như các quả đạn hoàn chỉnh.<br>\r\n"Nga dường như dùng chúng để làm mồi bẫy, nhằm đánh lạc hướng và gây khó khăn cho lực lượng phòng không Ukraine. Dù ý định của họ là gì đi nữa, quá trình hoán cải cũng thể hiện tình trạng cạn kiệt kho tên lửa tầm xa trong biên chế quân đội Nga", báo cáo của quân đội Anh có đoạn.\r\n\r\nBộ Quốc phòng Nga và quân đội Ukraine chưa bình luận về thông tin.\r\n\r\nKh-55 là tên lửa hành trình phóng từ máy bay được Liên Xô phát triển từ thập niên 1970 và đưa vào biên chế năm 1983. Dòng Kh-55 trang bị hệ thống dẫn đường quán tính và khớp ảnh địa hình, có khả năng bay bám địa hình ở độ cao nhỏ và cơ động liên tục để tránh lưới phòng không đối phương.<br>\r\n\r\nMỗi quả đạn Kh-55 có tầm bắn 2.500-3.000 km tùy phiên bản, mang đầu đạn hạt nhân mạnh tương đương 200.000 tấn thuốc nổ TNT, cung cấp vũ khí đầy uy lực cho phi đội oanh tạc cơ chiến lược Tu-95MS và Tu-160, cho phép chúng tấn công mục tiêu từ ngoài tầm đánh chặn của mọi tổ hợp phòng không đối phương.<br>\r\n\r\nTrong gần 9 tháng chiến sự, quan chức quốc phòng và giới chuyên gia quân sự phương Tây nhiều lần nhận định kho vũ khí Nga, trong đó có tên lửa dẫn đường, sắp cạn kiệt do tần suất sử dụng cao. Tuy nhiên, lực lượng Nga những tháng qua gần như ngày nào cũng tuyên bố sử dụng tên lửa và vũ khí chính xác cao tập kích mục tiêu tại Ukraine.<br>\r\n\r\nChủ tịch Hội đồng Tham mưu trưởng Liên quân Mỹ Mark Milley cho biết Nga đã tập kích toàn bộ lãnh thổ Ukraine bằng gần 100 tên lửa hành trình và nhiều vũ khí khác trong ngày 15/11, đánh dấu đợt không kích lớn nhất nhằm vào Ukraine kể từ đầu xung đột.', 13, '2022-11-27 16:12:06', '2022-11-27 16:12:06', 1),
	(55, 'Châu Âu chạy đua bảo vệ huyết mạch năng lượng dưới đáy biển', 'chauau.jpg', 'Châu Âu đang dồn nỗ lực bảo vệ hệ thống đường ống khí đốt xuyên biển, bởi mọi sự cố xảy ra với chúng đều sẽ là thảm họa.', 'Tàu ITS Numana của hải quân Italy gần đây đang tuần tra trên một đường ống dẫn khí đốt từ Bắc Phi sang châu Âu thì thiết bị sonar phát hiện vật thể kim loại khả nghi.<br>\r\n\r\n"Đó là một nguy cơ rõ ràng", hạm trưởng Gianluigi Barberisi nói. Vì vậy, con tàu đã hạ một robot xuống lòng biển, lặn sâu khoảng 200 mét để quan sát kỹ hơn. Vật thể khả nghi hóa ra chỉ là một cái bàn rỉ sét chìm dưới biển.<br>\r\n\r\nTrước đây, hải quân Italy không dành nhiều thời gian tuần tra các đường ống dưới đáy biển. Nhưng mọi thứ đã thay đổi sau ngày 26/9, khi các vụ nổ, dường như là hành vi phá hoại, khiến đường ống khí đốt Nord Stream ở biển Baltic bị xé toạc. Kể từ đó, việc bảo vệ các đường ống, mạng lưới điện hay trạm tiếp nhận khí đốt tới châu Âu đã trở thành ưu tiên an ninh hàng đầu trên khắp lục địa.<br>\r\nSau khi phát động chiến dịch quân sự tại Ukraine, Moskva đã giảm dần xuất khẩu khí đốt sang châu Âu, cho thấy nguy cơ của châu lục khi phụ thuộc vào nguồn dầu mỏ, khí đốt Nga. Các vụ nổ tại đường ống Nord Stream, dẫn khí đốt từ Nga đến Đức, cho thấy một điểm yếu khác đối với châu Âu: Cơ sở hạ tầng dễ bị tổn thương trước các cuộc tấn công.\r\n\r\nMột số quan chức châu Âu nói Nga gây ra vụ nổ, nhưng Moskva phủ nhận mọi cáo buộc, cho rằng phương Tây đứng sau sự việc.\r\n\r\nBảo vệ cơ sở hạ tầng năng lượng của châu Âu là một nhiệm vụ đầy thách thức. Có hơn 9.600 km đường ống dẫn khí đốt xuyên qua vùng biển Na Uy và Địa Trung Hải cùng hơn 1.000 cơ sở dầu khí ngoài khơi ở vùng biển châu Âu.\r\n\r\nCác nhà phân tích năng lượng và an ninh cho biết bất kỳ cuộc tấn công nào vào chúng, giống như vụ nổ Nord Stream, sẽ mất hàng tháng, thậm chí hàng năm, để khắc phục vì việc tiếp cận rất khó khăn và các đường ống dễ bị hư hại bởi nước biển.<br>\r\n\r\nHải quân Italy đang liên tục rà soát các đường ống dẫn khí đốt cho quốc gia. "Chúng tôi đã tăng cường tuần tra trên và dưới mặt nước, huy động cả tàu ngầm có người lái, tàu ngầm điều khiển từ xa và lực lượng không quân", đô đốc Giuseppe Cavo Dragone, tham mưu trưởng quốc phòng Italy, cho hay. "Đó không chỉ là việc phát hiện xem có điều gì xảy ra hay không, mà trên thực tế là hoạt động tuần tra ngăn mọi nguy cơ xảy ra".<br>\r\n\r\nHầu hết các nước châu Âu đều đang thực hiện những nỗ lực tương tự. Đôi khi, họ phải sử dụng lực lượng cảnh sát và quân đội vốn không được đào tạo cho những nhiệm vụ như vậy. Tàu chiến, tàu không người lái dưới nước (UUV) và máy bay quân sự đang tích cực tuần tra, kiểm tra các đường ống và giàn khoan ngoài khơi.', 13, '2022-11-27 16:21:56', '2022-11-27 16:21:56', 1),
	(56, 'Ông Trump hứng chỉ trích sau bữa tối với rapper Mỹ', 'donaltrump.jpg', 'Cựu tổng thống Trump hứng chỉ trích vì dùng bữa tối với rapper Kanye West và người theo chủ nghĩa da trắng thượng đẳng Nick Fuentes ở Mar-a-Lago.', 'Phó thư ký báo chí Nhà Trắng Andrews Bates đã lên án cuộc gặp của ông Trump với Fuentes, người được Bộ Tư pháp Mỹ mô tả là phần tử theo chủ nghĩa da trắng thượng đẳng.\r\n\r\n"Kỳ thị, thù ghét và quan điểm bài Do Thái hoàn toàn không có chỗ trên đất Mỹ, kể cả ở Mar-a-Lago. Việc phủ nhận thảm họa diệt chủng Holocaust là hành động nguy hiểm và phải bị lên án mạnh mẽ", Bates nói ngày 26/11.<br>\r\nTổng thống Mỹ Joe Biden, người đang nghỉ cuối tuần ở Nantucket, đã né tránh câu hỏi từ phóng viên về bữa tối gây tranh cãi của người tiền nhiệm. "Bạn không muốn nghe những gì tôi nghĩ đâu", ông nói.\r\n\r\nTrước đó, cựu tổng thống Trump thừa nhận đã ăn tối với rapper Kanye West tại dinh thự Mar-a-Lago, Florida, hồi đầu tuần. Ông cho biết nam rapper đi cùng bạn bè, một trong số đó là Nick Fuente, nhà bình luận chính trị bài Do Thái và phân biệt chủng tộc. Tuy nhiên, cựu tổng thống Mỹ cho hay trước bữa tối, ông "chưa bao giờ gặp" Fuentes.<br>\r\n\r\nRapper West, người tuyên bố sẽ tranh cử tổng thống vào năm 2024, cũng đã đưa ra hàng loạt bình luận bài Do Thái trong những tuần gần đây, dẫn đến việc anh bị đình chỉ hoạt động trên các trang mạng xã hội. Công ty quản lý đã sa thải West và anh cũng bị một số công ty như Adidas hay Gap cắt hợp đồng hợp tác.<br>\r\n', 13, '2022-11-27 16:26:01', '2022-11-27 16:26:13', 2),
	(57, 'Cá sấu sinh sôi trên sông siêu ô nhiễm', 'casau.jpg', 'Một loài cá sấu dễ tổn thương đang sinh sôi mạnh trên sông Tarcoles, dù đây là một trong những dòng sông ô nhiễm nhất Trung Mỹ.', 'Sông Tarcoles hàng ngày phải nhận lượng lớn rác và nước thải từ các hộ gia đình, nhà máy ở thủ đô San Jose của Costa Rica cách đó 97 km, khiến những khu rừng ngập mặn ven sông ngập lốp xe, rác nhựa. Tuy nhiên, khoảng 2.000 con cá sấu Trung Mỹ, danh pháp khoa học Crocodylus acutus, vẫn có thể thích nghi và sinh sôi trong môi trường này.\r\n\r\n"Đây là một khu vực siêu ô nhiễm, nhưng lại không ảnh hưởng đến số lượng cá sấu", Ivan Sandoval, nhà sinh vật học tại Đại học Quốc gia Costa Rica, nói.<br>\r\n\r\n"Tarcoles là sông bị ô nhiễm nặng nhất Costa Rica, và là một trong những sông ô nhiễm nhất Trung Mỹ, có thể tìm thấy kim loại nặng, nitrit, nitrat và lượng lớn chất thải từ con người tại đây", chuyên gia về cá sấu nói thêm.<br>\r\nTheo Liên minh Quốc tế về Bảo tồn Tự nhiên (IUCN), cá sấu Trung Mỹ hiện chỉ còn khoảng 5.000 cá thể ở 18 quốc gia, sau nhiều thập kỷ bị săn bắt và mất môi trường sống. IUCN xếp cá sấu Trung Mỹ vào nhóm "dễ bị tổn thương", nhưng xác nhận số lượng cá thể đã tăng trong những năm gần đây. Tổ chức cho biết quần thể cá sấu Trung Mỹ ở Costa Rica "khỏe mạnh và bùng nổ".\r\n\r\nLoài bò sát lớn này dường như không bị ảnh hưởng bởi khoảng 150 loại vi khuẩn được phát hiện trên sông Tarcoles. Sandoval mô tả cá sấu là "những hóa thạch sống" có khả năng sinh tồn trong điều kiện rất khắc nghiệt.<br>\r\n\r\n"Chúng không có sự thay đổi nào trong hàng triệu năm. Chúng được tạo ra một cách hoàn hảo", ông nói.<br>\r\n\r\nSố lượng cá sấu tại Costa Rica phục hồi từ năm 1980. Cá sấu trên sông Tarcoles đã trở thành nét độc đáo thu hút du khách nước ngoài. Họ thường tham gia các chuyến đi bằng thuyền để được nhìn cận cảnh cá sấu, một số còn cho chúng ăn, dù hành động này bị cấm. Giới chức Costa Rica lo ngại chúng đang dần quen với sự gần gũi con người.', 13, '2022-11-27 16:28:51', '2022-11-27 16:28:51', 1),
	(58, 'Trung Quốc có thể đã sản xuất 200 tiêm kích J-20', 'tq.jpg', 'Trung Quốc đang tăng tốc độ sản xuất tiêm kích tàng hình J-20 và được cho là đã xuất xưởng ít nhất 200 chiếc.', 'Một nguồn tin quân sự Trung Quốc ngày 27/11 cho biết nước này tăng tốc sản xuất tiêm kích J-20 nhằm vân bằng việc Mỹ tăng cường triển khai tiêm kích tàng hình F-22 và F-35 tới khu vực Đông Bắc Á.<br>\r\nTruyền thông Trung Quốc hồi tháng 1 đưa tin quân đội nước này lên kế hoạch triển khai khoảng 200 tiêm kích J-20 để đáp ứng nhu cầu huấn luyện chuyên sâu "nhằm đối phó việc Mỹ tăng cường triển khai F-35 tới khu vực".<br>\r\n\r\nQuân đội Trung Quốc biên chế tiêm kích J-20 năm 2017, song chưa công bố số lượng cụ thể. Phát ngôn viên không quân Trung Quốc, đại tá Thẩm Kim Khoa tháng 9 cho biết tiêm kích J-20 đã có mặt trong biên chế các đơn vị thuộc cả 5 bộ tư lệnh chiến khu của quân đội Trung Quốc.', 13, '2022-11-27 16:31:28', '2022-11-27 16:31:28', 2),
	(59, 'Cô dâu gốc Việt trong hoàng tộc Monaco sắp đón con đầu lòng', 'codau.jpg', 'Marie Hoa Chevallier, vợ Louis Ducruet, thành viên hoàng tộc Monaco, thông báo đang mang thai con đầu lòng.', 'Trong bài viết trên Instagram ngày 24/11, hai vợ chồng đăng ảnh họ ngồi bên chó cưng Pancake đang mặc áo in dòng chữ "sắp thành anh cả", kèm chú thích "Một cuộc phiêu lưu mới sắp bắt đầu". Họ nhận được rất nhiều lời chúc mừng từ người thân và người hâm mộ ở phần bình luận.\r\n\r\nLouis Ducruet là con trai của Công chúa Stephanie của Monaco và là cháu nội của Thân vương Monaco Rainier III cùng Vương phi Grace Kelly. Marie Hoa Chevallier sinh tại Monaco, có cha là người Pháp, mẹ là người Việt Nam.\r\n\r\nDucruet xếp thứ 15 trong danh sách kế vị ngai vàng Monaco, nhưng không có tước hiệu hoàng gia. Đứa trẻ sắp ra đời sẽ xếp vị trí thứ 16 trong danh sách.<br>\r\nTình yêu của Chevallier và Louis Ducruet từng được ví như chuyện cổ tích thời hiện đại. Là con út trong gia đình có ba con, trên có hai anh trai, Chevallier từ nhỏ là "viên ngọc" được cả nhà nâng niu, chăm sóc. Cha mất năm Chevallier học trung học, khiến cô gái trở nên tự chủ và độc lập hơn.\r\n\r\nChevallier sau đó thi đỗ đại học với kết quả xuất sắc. Vì gia cảnh khó khăn, cô vừa học, vừa đi làm thêm nhiều việc, như phát tờ rơi, gia sư, bồi bàn, thu ngân siêu thị, rạp phim.<br>\r\n\r\nChevallier gặp Ducruet năm 2011, khi cả hai đều là sinh viên tại trường Kinh doanh SKEMA của Pháp, nơi cô có bằng cử nhân ngành quản trị kinh doanh. Hai vợ chồng đã mô tả đó là khoảnh khắc "yêu từ cái nhìn đầu tiên". Hai người tìm thấy tiếng nói chung do cùng có sở thích về bóng đá, du lịch.<br>\r\n\r\nTháng 2/2018, Louis Ducruet cầu hôn Chevallier tại Hội An. Họ tiến hành hôn lễ vào tháng 7/2019. Hiện cô dâu gốc Việt đang là quản lý cho khu phức hợp và giải trí Monte-Carlo ở Monaco, trong khi chồng đang làm việc tại câu lạc bộ bóng đá AS Monaco.', 13, '2022-11-27 16:34:04', '2022-11-27 16:34:04', 3),
	(60, '10 tài năng khoa học trẻ nhận giải Quả cầu vàng 2022', 'tainang.jpg', '10 gương mặt xuất sắc và 20 nữ sinh viên tiêu biểu được chọn để trao giải Quả cầu vàng với thành tích nổi bật trong lĩnh vực khoa học công nghệ.', 'Tối 27/11, Trung ương Đoàn TNCS Hồ Chí Minh, Bộ Khoa học và Công nghệ trao giải thưởng Khoa học công nghệ thanh niên Quả cầu vàng và phần thưởng Nữ sinh khoa học công nghệ năm 2022. Đây là giải thưởng hàng năm nhằm phát hiện, tôn vinh các tài năng trẻ không quá 35 tuổi có thành tícxuất sắc về khoa học công nghệ.<br>\r\nHội đồng giải thưởng đã chọn 10 gương mặt tiêu biểu trong 37 ứng viên để vinh danh từ 5 lĩnh vực: Công nghệ thông tin, chuyển đổi số và tự động hóa (3 giải), công nghệ y - dược (2 giải), công nghệ sinh học (1 giải), công nghệ môi trường (2 giải) và công nghệ vật liệu mới (2 giải).<br>\r\nTheo ban tổ chức, Giải thưởng năm nay xuất hiện nhiều gương mặt nhà khoa học trẻ đang sinh sống học tập và làm việc tại các cơ sở đào tạo uy tín trên thế giới. Trong đó, nhiều cá nhân là chủ nhiệm đề tài khoa học công nghệ cấp quốc gia, quốc tế, có đề tài được ứng dụng thực tế đem lại hiệu quả cao. Nhiều công trình khoa học, công bố chất lượng cao thuộc mục Q1, có bằng độc quyền sáng chế, giải pháp hữu ích, đạt huy chương trong nước và quốc tế.<br>\r\nMỗi cá nhân đạt Giải thưởng được nhận Huy hiệu "Tuổi trẻ sáng tạo" của Trung ương Đoàn, Cúp Quả cầu vàng, Giấy chứng nhận đạt Giải thưởng kèm theo phần thưởng tiền mặt 20 triệu đồng.<br>\r\n\r\nBan tổ chức cũng lựa chọn 20 cá nhân xuất sắc nhận Giải thưởng Nữ sinh Khoa học công nghệ Việt Nam. Từ năm 2022, Trung ương Đoàn đã mở rộng 15 ngành đào tạo xét giải thuộc các lĩnh vực khoa học công nghệ chiến lược ưu tiên quốc gia, phát triển ứng dụng công nghệ số, trí tuệ nhân tạo (AI) nhằm chủ động tham gia cuộc cách mạng công nghiệp lần thứ 4 và đẩy mạnh chuyển đổi số quốc gia. Các nữ sinh đồng thời được tặng bằng khen của Trung ương Đoàn, biểu trưng và tiền thưởng 5 triệu đồng. Đến năm 2022 đã có 719 nữ sinh được trao giải thưởng.', 18, '2022-11-27 16:42:00', '2022-11-27 16:42:00', 1),
	(62, 'Mây thấu kính bao đỉnh núi Bà Đen như đĩa bay', 'nuibaden.jpg', 'Những đám mây lạ xuất hiện ở núi Bà Đen được chuyên gia lý giải là hiện tượng mây thấu kính.', 'Ngày 24/11, những hình ảnh ghi lại khoảnh khắc về hiện tượng trên đỉnh núi Bà Đen (xã Thạnh Tân, thành phố Tây Ninh, tỉnh Tây Ninh) thu hút sự chú ý của nhiều người. Hình ảnh những đám mây lớn tạo thành quầng bao quanh đỉnh núi nổi bật giữa nền trời xanh đã tạo ra khung cảnh thiên nhiên kỳ thú.<br>\r\n\r\nTheo thông tin ghi nhận từ anh Đỗ Vinh Quan, người dân địa phương, hình ảnh được anh chụp khoảng 6h trên đường đi làm. "Tôi rất bất ngờ khi thấy khung cảnh mây bao đỉnh núi Bà Đen có hình thù độc đáo và đẹp mắt thế này", anh Quan nói. Anh cho biết, hiện tượng đã xuất hiện từ khoảng 17h hôm trước, nhưng không rõ nét và có hình thù đặc biệt.<br>\r\nAnh kể thêm, hiện tượng mây trùm núi xuất hiện tần suất khá nhiều ở vùng, thông thường những ngày nhiều mây sẽ có. "Khoảng 1-2 năm trước, tôi từng chụp cảnh núi trông "giống đội nón" song để thành quầng to thế này lại là lần đầu tiên", anh nói với VnExpress.', 18, '2022-11-27 16:43:30', '2022-11-27 16:43:30', 4),
	(64, 'Cứu nhiều bệnh nhân nhờ kỹ thuật phát hiện sớm ung thư đại trực tràng', 'cuunguoi.jpg', 'Nhóm bác sĩ tại Bệnh viện K Tân Triều nghiên cứu và phát triển thành công kỹ thuật sàng lọc, chẩn đoán, điều trị ung thư đại trực tràng giúp nhiều bệnh nhân phát hiện sớm, chữa khỏi bệnh.', 'Năm 2014, ông Nguyễn Văn Dũng (67 tuổi, ở Hà Nội) thấy biểu hiện khó khăn khi đi đại tiện, ra máu nhiều. Các bác sĩ chẩn đoán ông bị ung thư trực tràng và được chỉ định mổ.\r\n\r\nNhư kỹ thuật thông thường, ông sẽ phải cắt cụt trực tràng và thêm 1 túi hậu môn nhân tạo vĩnh viễn. Tuy nhiên, ông Dũng được chỉ định tia xạ trước mổ kết hợp uống thuốc. Sau 23 ngày được kiểm tra và mổ bảo tồn cơ tròn, chỉ cắt đoạn trực tràng giữ lại ống hậu môn thay vì cắt bỏ toàn bộ.<br>\r\n\r\nĐợt kiểm tra tổng quát sau 5 năm phẫu thuật, sức khỏe ông Dũng ổn định, bác sĩ nói tình hình bệnh tốt. "Đợt điều trị mổ, tôi sút 20 cân và ăn cháo 6 tháng sau đó. Giờ tôi 77 cân và vẫn tự kiểm tra được các vết khâu nối hàng ngày", ông kể với VnExpress sau 8 năm ứng dụng kỹ thuật mới vào điều trị bệnh. Ông bảo rất may mắn vì sức khỏe trở lại bình thường.<br>\r\n\r\nÔng Dũng chỉ là một trong số nhiều bệnh nhân được ứng dụng tiến bộ khoa học trong điều trị bệnh ung thư đại trực tràng. GS.TS Lê Văn Quảng, Giám đốc Bệnh viện K Tân Triều, cho biết từ năm 1997 các bác sĩ tại bệnh viện đã bắt đầu nghiên cứu về giá trị của lâm sàng và nội soi trong chẩn đoán, điều trị ung thư đại trực tràng, đồng thời chọn lọc các kỹ thuật mới.<br>\r\n\r\n\r\n"Khi tiếp cận các công nghệ tiên tiến hàng đầu thế giới, chúng tôi vừa học, vừa tìm cách để có được kỹ thuật phù hợp với điều kiện Việt Nam, vẫn hiệu quả thay vì làm theo cách phương Tây với chi phí đắt đỏ", ông nói.<br>\r\n<h4>Kỹ thuật phát hiện sớm và bảo tồn cơ tròn</h4>\r\n\r\nĐiểm sáng tạo của nhóm nghiên cứu là đồng bộ kỹ thuật và tìm cách bảo tồn cơ tròn nhằm đảm bảo kết quả ít tai biến nhưng vẫn phù hợp với người Việt. GS Quảng cho biết, việc điều trị ung thư trực tràng cần nhiều thời gian, có nhiều trường hợp phải mất tới hơn một năm để có thể đi đại tiện bình thường. "Nếu chủ động được công nghệ, nhất là với bệnh ung thư, bệnh nhân được hưởng lợi từ các kỹ thuật tiên tiến giúp kéo dài cuộc sống, giảm tỷ lệ tử vong và giảm chi phí điều trị", ông nói.\r\n\r\nĐiểm đặc biệt, nhờ kỹ thuật xét nghiệm hoá miễn dịch trong phân đã giúp phát hiện sớm đối với trường hợp bị ung thư đại trực tràng. Tỷ lệ phát hiện sớm tăng từ 20% lên khoảng 41%. "Với bệnh nhân ung thư giai đoạn sớm chỉ cần cắt hớt niêm mạc trực tràng, trong khi giai đoạn muộn sẽ cần phối hợp đa phương thức như hóa xạ trị, phẫu thuật khiến chi phi tăng nhiều lần", GS Quảng nói. Ngoài kỹ thuật này, nhóm nghiên cứu và ứng dụng thành công nội soi với ánh sáng dải tần hẹp có phóng đại (Narrow Band Imaging - NBI). Kỹ thuật nội soi NBI cũng cho phép phát hiện ung thư ở giai đoạn sớm, làm tiền đề ứng dụng một kỹ thuật cắt hớt niêm mạc (EMR).', 18, '2022-11-27 16:45:39', '2022-11-27 16:45:39', 3),
	(65, 'Bể chứa hydro lạnh -253 độ C cho máy bay', 'hidro.jpg', 'Airbus đang phát triển mẫu bể chứa hydro siêu lạnh dành cho máy bay không phát thải, dự kiến thử nghiệm vào năm 2026 - 2028.', 'Airbus đang thiết kế các bể chứa hydro lạnh mới gọi là "trái tim lạnh" nhằm phục vụ cho mẫu máy bay chạy bằng hydro lỏng ZEROe trong tương lai, Interesting Engineering hôm 26/11 đưa tin.\r\n\r\n"Hydro là chìa khóa cho sứ mệnh đưa máy bay không phát thải ra thị trường vào năm 2035 của chúng tôi, nhưng cần được bảo quản ở nhiệt độ đặc biệt lạnh, -253 độ C. Sử dụng công nghệ này đồng nghĩa với việc phát triển những bể chứa hydro lạnh cải tiến", Airbus cho biết.\r\n<br>\r\nCó hai công nghệ chính cho phép máy bay bay trực tiếp bằng hydro: Cung cấp năng lượng cho một động cơ đốt hydro thông qua động cơ turbine khí đã sửa đổi, hoặc sử dụng pin nhiên liệu hydro để tạo ra điện. Một lựa chọn khác là kết hợp cả hai công nghệ.\r\n<br>\r\nPhương pháp nào cũng đòi hỏi giữ lạnh hydro. Hydro cần được bảo quản ở mức nhiệt -253 độ C và duy trì nhiệt độ này ổn định trong suốt chuyến bay. Đây là lý do khiến bể chứa của máy bay hydro là một bộ phận then chốt. Tuy nhiên, chúng rất khó chế tạo vì hoàn toàn khác với bể chứa của máy bay truyền thống.<br>\r\n\r\nĐể phát triển bể chứa, Airbus thành lập các Trung tâm Phát triển Không phát thải (ZEDC) ở Nantes (Pháp) và Bremen (Đức) khoảng 15 tháng trước. Các bể chứa được sản xuất tại Nantes, trong khi các hộp lạnh - có tác dụng khí hóa hydro lỏng - được sản xuất tại Bremen.', 18, '2022-11-27 16:47:54', '2022-11-27 16:47:54', 3),
	(66, '106 đề tài được giải Sáng tạo thanh thiếu niên, nhi đồng toàn quốc 2022', '106detai.jpg', 'Hội đồng giải thưởng lựa chọn và trao 106 giải nhằm khuyến khích tài năng khoa học trẻ tuổi, hướng tới đào tạo và nuôi dưỡng các nhà sáng chế tương lai.', 'Cuộc thi Sáng tạo dành cho thanh thiếu niên, nhi đồng toàn quốc lần thứ 18 đã tìm ra chủ nhân của các giải thưởng, trao giải sáng 27/11 tại Nhà hát lớn Hà Nội. Giải thưởng dành cho thanh thiếu niên từ 6 đến 18 tuổi trong cả nước, tiêu chí đề tài được trao dựa trên tính mới, sáng tạo và khả năng áp dụng rộng rãi.\r\n<br>\r\nTừ 832 đề tài tham dự, Hội đồng giải thưởng lựa chọn 106 giải, trong đó trao 1 giải đặc biệt, 5 giải nhất, 10 giải nhì, 30 giải ba và 60 giải khuyến khích. Ngoài phần thưởng tiền mặt, các tác giả đoạt giải được tặng bằng khen của Quỹ Hỗ trợ sáng tạo kỹ thuật Việt Nam (Vifotec).<br>\r\nGiải đặc biệt thuộc về Mô hình thiết bị ứng dụng sóng siêu âm để cô đặc mật ong và nước mắm ở nhiệt độ thấp của nhóm học sinh Vũ Việt Nguyên (THCS Dịch Vọng), Đặng Chí Bằng (THCS Lê Quý Đôn), Vũ Khánh Uyên (THCS Nghĩa Tân, Cầu Giấy), Hoàng Khánh An (THCS Ngô Sĩ Liên, Hoàn Kiếm) và Lã Nguyệt An (trường chuyên Hà Nội Amsterdam).\r\n<br>\r\nMô hình thiết bị được chế tạo và lắp ráp gọn, đơn giản dễ vận hành, phù hợp với nhiều mô hình sản xuất, năng suất tách nước cao, có thể gia nhiệt để tăng hiệu quả tách ẩm. Việc chỉ sử dụng đầu siêu âm và gia nhiệt gián tiếp ở nhiệt độ thấp giúp đảm bảo chất lượng, không làm thay đổi màu sắc, không biến đổi hoặc mất đi các chất có trong sản phẩm. Mô hình cũng có thể áp dụng ở quy mô công nghiệp nhằm gia tăng giá trị của mật ong, nước mắm - mặt hàng nông thủy sản đặc trưng của Việt Nam.\r\n<br>\r\n5 đề tài được trao giải nhất được nhận bằng khen của Bộ Giáo dục và Đào tạo cùng huy hiệu tuổi trẻ sáng tạo. Trong đó có thể kể đến mô hình bè lọc nước bằng công nghệ sinh học của nhóm tác giả Lê Huyền Trang, Nguyễn Thùy Linh, Hà Nguyễn Gia Bảo, Nguyễn Hải Anh (THPT Hoằng Hóa 4) và Trịnh Nguyên Thành (THPT Lương Đắc Bằng), tỉnh Thanh Hóa.\r\n<br>\r\nĐây là mô hình bè nổi hình lục giác trồng cây thủy sinh làm từ chai nhựa tái chế góp phần lọc nước ô nhiễm tại các con sông. Nhóm đã tận dụng những chai nhựa tái chế để tạo thành chiếc bè hình lục giác nổi trên sông. Những chiếc bè hình lục giác này được trồng các loại cây thuỷ sinh (thuỷ trúc, rau muống, cỏ nến, lục bình...). Thông qua các bộ rễ, cây sẽ lọc nước liên tục, góp phần cải thiện nguồn nước. Bên cạnh đó, nhóm còn tích hợp hệ thống pin năng lượng mặt trời để bổ sung một số tính năng như hệ thống máy bơm để lọc nước qua các tầng lọc cát, sỏi, than hoạt tính, máy bơm sục khí hay hệ thống chân vịt từ xa, loa âm thanh.\r\n<br>\r\nHay đề tài chế tạo đồng phục học sinh tích hợp phao cứu sinh tự động nhằm giảm thiểu tai nạn đuối nước của nhóm tác giả Nguyễn Đức Minh, Hoàng Thu Trang, Vương Hoàng Toàn, Nguyễn Ngọc Khánh Huyền, Đỗ Thị Phương (THPT Lương Đắc Bằng, Thanh Hóa).', 18, '2022-11-27 16:51:45', '2022-11-27 16:51:45', 2);

